insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1070218105,'关于暑期小学期抢选选课通知','关于报名参加第五届“全国大学生创新年会”的通知 教务处通知〔2012〕 54号 为了充分展示广大学生实施“国家大学生创新性试验计划”和“国家大学生创新创业训练计划”的研究成果，切实加强各高校之间的交流和合作，有效提升大学生的学术交流水平，教育部将与科技部联合在中国农业大学举办第五届“全国大学生创新年会”。 “全国大学生创新年会”是学生自我展示的舞台，通过这个平台可与全国各大高校学生交流和结交朋友，并与相关领域知名专家、学者零距离接触。我校学子通过参加历届 “全国大学生创新年会”，充分展示了我校大学生的风采以及创新创业活动的成果，并在历届创新年会上屡获佳绩。参与学生纷纷表示收获颇多并且深有感触，包括开阔了眼界，结交了朋友，提高了自身论文撰写、语言表达、与人交流沟通等能力。 欢迎参与“国家大学生创新性试验计划”和“国家大学生创新创业训练计划”（包括所有结题和未结题的项目）的学生踊跃报名参加，来争取这个难得的提高学术交流水平、展现创新性思维与能力的机会。 本届全国大学生创新年会将评选120项入围项目和90篇入围学术论文。入围参加创新年会的项目组可参加十佳项目或十佳论文的评选。同一个项目只能推荐1篇学术论文。 参与成果展示的项目应体现“兴趣驱动、自主实践、重在过程”的原则，注重以学生为主体的创新活动和科学探索，通过学生的自主实践充分调动学生的学习主动性、积极性，在项目实施过程中使学生得到创新性科学研究的锻炼，在创新思维和创新实践方面有较大的收获。 参与优秀学术论文评选的论文应具有一定的技术创新意义、重要创新见解和发现、实际应用价值，论文要求论点鲜明，论据充分，数据可靠，图表清晰，引证准确，符合论文的格式要求。 本次我校将推荐三个展示项目、三篇学术论文参加第五届“全国大学生创新年会”的选拔。所有推荐项目将来自于上海交通大学2012年“本科生创新成果展”，请报名参加第五届“全国大学生创新年会”的项目务必参加校级本科生创新成果展，学校将聘请相关专家对成果展项目和论文进行评审，择优推选参评第五届“全国大学生创新年会”。学校将为参会学生提供相关费用。 请各院（系）工作组根据《上海交通大学关于举办2012本科生创新成果展的通知》（教务处通知〔2012〕51号）积极动员和推荐学生参加2012年本科生创新成果展，同时欢迎各项目组踊跃参与。 附件：上海交通大学关于举办2012本科生创新成果展的通知 联系人：田夏 联系电话：34206429','2012-06-21 08:46','2012-06-21 08:46','','','教学信息服务网','2012-06-21 08:46:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1779000812,'2012年校“优秀教师奖”及“烛光奖”评选工作通知','关于2011－2012学年第二学期节假日安排的通知 各院（系）、部、处、直属单位： 　　根据国务院办公厅关于2012年节假日安排的相关规定，现将我校2011－2012学年第二学期节假日调休安排通知如下： 　　一、 清明节：4月2日至4日放假调休，共3天。3月31日（星期六）上4月2日（星期一）的课、4月1日（星期日）上4月3日（星期二）的课。 　　二、 劳动节：4月29日至5月1日放假调休，共3天。4月28日（星期六）上4月30日（星期一）的课。 　　三、 端午节：6月22日至24日放假公休，共3天。课程安排不变。 　　重庆路校区放假安排由医学院另行通知。专业学位研究生、继续教育学院学生放假期间的课程安排，分别由研究生院、继续教育学院另行通知。第二学科、外校选修我校的跨校辅修课程安排按以上通知执行。 　　放假期间，各单位要加强值班，做好安全保卫工作；后勤部门要做好师生员工的后勤服务工作；校区间交通班车按双休日班车时间运行。请各单位将值班表，分别报校长办公室和保卫处。 　　特此通知。 　　上海交通大学 相关附件：  ','2012-06-20 17:28','2012-06-20 17:28','','','教学信息服务网','2012-06-20 17:28:00.0');
