insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1058627034,'安泰经济与管理学院2011级自主转专业面试名单','上海交通大学 关于组织申报上海高校创新创业教育实验基地的通知 教务处通知〔2012〕 050 号 各学院（系）： 根据上海市教育委员会《关于申报第一批上海高校创新创业教育实验基地的通知》（沪教委高〔2012〕47号）精神，为将创新创业教育融入到我校人才培养全过程，提升学生的社会责任感、创新精神、创新意识和创新能力，不断提高人才培养质量，学校决定组织开展上海高校创新创业教育实验基地的申报和推荐工作。现将有关事项通知如下： 一、申报材料 各院（系）应认真领会通知精神，以学院（系）为单位组织申报，并填写《上海高校创新创业教育实验基地申报书》（以下简称《申报书》，见附件2）。 二、申报方式及时间 请各院（系）务必于6月25日之前将院（系）负责人签字并盖章的纸质《申报书》一式五份报送教务处实践教学办公室，同时将电子版发至zhangsm@sjtu.edu.cn，逾期不再受理。 三、遴选推荐 学校将组织专家对申报材料进行审核，择优遴选推荐名单，报主管校领导批准后，于2012年6月29日前报上海市教育委员会。 联 系 人：张淑敏 联系电话：34206429 电子信箱：zhangsm@sjtu.edu.cn 联系地址：新行政楼B325室                                         教务处                                         2012年6月12日 相关附件：   附件1、 关于申报第一批上海高校创新创业教育实验基地的通知.doc 附件2、 上海高校创新创业教育实验基地申报书.doc','2012-06-11 21:29','2012-06-11 21:29','','','教学信息服务网','2012-06-11 21:29:00.0');
