insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1450416765,'安泰经济与管理学院2011级自主转专业笔试名单','2012 “最受学生欢迎的教师”奖网上投票评选启动 各位同学：  大家好！ 上海交通大学2012“最受学生欢迎的教师”奖网上投票评选正式启动。今年的候选人从学生网上课堂教学质量评估得分位于前列的教师中产生。他们爱岗敬业，严谨治学；教书育人，师德高尚；坚守教学第一线，教学效果好，深受学生和同行好评。 2012“最受学生欢迎的教师”奖将由学生投票产生。得票前十名的教师将获得本年度“最受学生欢迎的教师”奖；得票第十一至二十名的教师将获得“最受学生欢迎的教师”提名奖。 欢迎同学们积极参与“最受学生欢迎的教师”奖的评选活动，为我们爱戴的好老师、为我们尊敬的好老师、为我们心目中的好老师投上宝贵的一票！ 投票网址：http://202.120.35.20/ppa/reportawardindex.aspx    投票时间：2012.6.1――2012.6.30   请用校内jaccount用户名和密码登录进行投票。 联 系 人： 刘老师 联系方式： 34206426-805                                   上海交通大学教务处                                                      2012年6月1日 相关附件：  ','2012-06-01 09:05','2012-06-01 09:05','','','教学信息服务网','2012-06-01 09:05:00.0');
