insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-236966267,'关于提交2012年小学期院（系）实施方案的通知','2012年致远学院第一批招生申请开通 致远学院是上海交通大学全新设立的拔尖创新人才培养基地和实验特区，由校长张杰院士亲自兼任学院院长，并得到教育部“基础学科拔尖学生培养试验计划”的有力支持。 学院设置数学、物理学、生命科学和计算机科学四个方向，每个方向招生总数不超过35人，4个方向的总人数不超过120人。 2012年致远学院第一批招生工作现已开始，要求如下： 1. 招生对象 上海交通大学保送生和预录取生。 2. 报名时间和方式 报名时间：5月15日上午8：30至6月14日上午8：30，逾期不再受理。 报名方式：通过致远网站在线提交申请。 3. 学生选拔标准 品学兼优，具有很好的数理基础； 志向远大，具有科学研究的激情； 求知欲强，具有锲而不舍的精神。 4. 选拔方式 通过简历筛选进入面试环节的学生。 5. 面试时间 6月底或7月初，具体时间网站另行通知。 6. 咨询电话 021-54745845 、021-54747161 致远学院张开双臂拥抱热爱科学的青年学子！ 更多信息详见学院招生专栏 http://zhiyuan.sjtu.edu.cn/enrollment/default.aspx 致远学院 相关附件：  ','2012-05-10 09:13','2012-05-10 09:13','','','教学信息服务网','2012-05-10 09:13:00.0');
