insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-366484724,'“环保杰出贡献奖”评选活动','上海交通大学 关于开展2012年暑期本科生科研见习活动的通知 教务处通知〔2012〕035号 各院（系）、科研院所、实验室（中心）：    “暑期本科生科研见习岗位”是我校面向本科低年级学生的重要实践活动，它引导学生适时适量参加教师科研项目，了解科研工作，体验科研活动，培养学生的科研兴趣，树立问题意识和批判性思维，提高分析问题和解决问题的能力，已成为我校本科教育的特色之一。学校要求校内各科研单位充分发挥科研优势，促进教学与科研相结合，充分发挥教师教书育人的作用，发动教师积极申报面向本科生的暑期科研见习岗位。具体要求如下： 1、    目标 设置科研见习岗位的目的，主要是使本科生有机会亲临科研现场，直接感受科研氛围，了解教师和科研人员的工作内容，感受他们的科学精神、认真负责的工作态度及良好的精神风貌，从而破除科研神秘感，增加自我认知，进一步明确学习目标，增强学习动力，优化学习方法，提升自主学习能力。 2、    任务 科研见习岗的上岗学生在暑假中的4至6周时间内，像正式研究人员一样参与工作，参加集体活动（如项目组例会等），并按照指导教师的要求开展一些力所能及的研究工作，或协助指导教师完成一些辅助性工作。指导教师须为学生制定详细的见习计划，向学生全面介绍本单位的研究工作，使学生建立起对科研工作的感性认识。同时，对学生感兴趣的问题给予热心解答，鼓励学生发现问题，主动学习。指导教师要定时考查学生的工作状况和见习收获，并给予有针对性的指导。上岗学生也应该主动求教，主动汇报，主动学习。 3、    申报要求与程序 采用教师自愿申报，网上公布信息，学生网上报名，师生双向选择确定上岗人员的方式进行。教师在申报科研见习岗位时，应对岗位的工作要求、纪律要求、考核形式、获得学分的条件以及面向的学生对象等给出具体规定。 4、    学生学业成果 完成科研见习岗位工作并提交总结报告的学生，一般可获得1个学分（成绩计为P）。对于在见习过程中通过相对独立的工作取得了有一定显示度的成果的学生，学分数可酌情增加，但一般不超过2个学分（申请2个学分的学生须提交相关佐证材料予以支持）。学生获得的该类学分可作为培养方案中的个性化学分或任意选修课学分记入学业档案。 5、    其他事项 学生选择科研见习岗位的时间，不可与本人小学期的教学安排相冲突。指导教师须对学生进行必要的安全教育。严禁让学生参与有一定危险性的科研活动。 6、          具体工作','2012-04-28 08:47','2012-04-28 08:47','','','教学信息服务网','2012-04-28 08:47:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(339233834,'U21 早稻田及墨西哥暑期项目(会议)面试通知','关于上海交通大学2012年度新增“卓越工程师教育培养计划”试点专业评审结果的公示 教务处通知<2012>034号 根据《教育部关于实施卓越工程师教育培养计划的若干意见》教高［2011］1号精神，为扩大我校工程教育教学改革范围，促使更多特色鲜明、优势突出的工科专业加入到“卓越计划”行列，学校于4月份组织了上海交通大学2012年度新增“卓越计划”试点专业的申报、评审工作。 学校组织召开答辩评审会，评审专家根据各专业负责人的答辩情况和递交的相关材料，进行综合评定，给各专业投票打分。学校将各申报专业按投票总得分由高到低进行排序。 现将排序结果进行公示，凡对此结果有异议者请联系教务处教学改革与质量办公室，公示有效期为自公示之日起一周。  联系地址：闵行区新行政楼B323室 联系电话：34206426--803 联系人：仝月荣 上海交通大学教务处 教学改革与质量办公室 2012年4月26日 相关附件：   附件1、 上海交通大学2012年度新增“卓越计划”试点专业评审结果.xls','2012-04-27 10:35','2012-04-27 10:35','','','教学信息服务网','2012-04-27 10:35:00.0');
