insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-794392101,'空调教室开放通知','创新与创业第四讲：创业经验分享 主题：创业经验分享 嘉宾：黄飞燕 女士 爱康国宾集团首席营销官 时间：2012年4月18日（周三）下午13:30-15:00 地点：东上院500号 嘉宾介绍： 黄飞燕女士 爱康国宾集团首席营销官 加入爱康国宾之前，黄飞燕女士在e龙网、美国强生公司、IBM（Informix）公司以及美国IDG公司拥有超过10年的市场与销售经验。作为中国互动营销及消费者服务行业卓越的女性之一，黄飞燕女士对于消费者的需求具有相当深刻的理解，这一切为爱康国宾会员服务的发展策略起到前瞻性的作用。 教育背景： 上海交通大学 美国Mount Holyoke女子学院 美国波士顿大学工商管理学院 主要创业经历： 艺龙旅行网 市场与销售副总裁 艺龙旅行网 (NASDAQ: LONG)是中国领先的在线旅行服务提供商之一，通过网站（eLong.com）、24小时预订热线（4006-161616）以及手机艺龙网（m.eLong.com）、艺龙iPhone和Andriod无线客户端等平台，为消费者提供酒店、机票及旅行团购产品等预订服务。艺龙旅行网通过提供强大的地图搜索、酒店360度全景、国内外热点目的地指南和用户真实点评等在线服务，使用户可以在获取广泛信息的基础上做出最佳的旅行决定。截至2011年6月，艺龙旅行网可提供中国大陆700多个城市21000多家酒店、海外100多个国家和地区的大约14万多家酒店预订服务；同时通过与国内外各航空公司合作，向用户提供国内、国际绝大多数航班机票的实时查询和预订服务。 爱康国宾集团 首席营销官         爱康国宾是中国领先的提供体检、疾病检测和私人医生服务的健康管理机构，由爱康网和国宾健检联合组建。依托旗下健康医疗服务中心、IT技术平台和强大的客户服务体系，爱康国宾为个人及团体提供从体检、检测、医疗、家庭医生、慢病管理、健康保险等全方位个性化服务，帮助其全面摆脱亚健康、预防慢性病、解决就医难，为根本提高现代中国人的整体健康水平和生命质量作贡献。同时，爱康国宾为保险公司和医疗机构提供第三方的健康管理服务以及客户关系管理的解决方案。 爱康国宾360°健康全管理是张黎刚先生“从遗传、生活习惯、饮食、生活环境、职业行为等方面出发，对身体状况进行预测跟踪、对疾病早期预警，并进行全方位地健康干预”的前瞻性理念，参照国外健康管理行业标准，并结合先进完善的医疗保健服务与信息技术手段，为追求健康生活的员','2012-04-16 10:28','2012-04-16 10:28','','','教学信息服务网','2012-04-16 10:28:00.0');
