insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(744467667,'企业望远镜第十一期│总裁来访 Welcome President & COO','企业望远镜第十一期│总裁来访 Welcome President& Chief Operating OfficerHORIBA集团全球总裁&COO（首席运营官） Masayuki Adachi博士到访交大2018年7月4日下午3:00 – 4:00上海交通大学密西根学院 龙宾楼 302 A-B文末加入群聊总裁Masayuki Adachi博士将给大家带来主题为Trends in Emission Regulations and Measurement Technologies for Current and Future Powertrains 的演讲以及回答相关技术以及职业发展的问题。Masayuki Adachi博士是现任Horiba集团全球总裁兼首席运营官（COO），近30年来致力于研发汽车测试系统，并主导汽车尾气排放的相关研究，在Horiba集团同时也负责整个集团R&D中心。此前于加州大学欧文分校开展科研工作，并在京都Doshisha大学取得博士学位。2014年入选SAE Fellow。HORIBA集团成立于1953年，其测量分析仪器处于世界领先地位，正被广泛应用于：汽车、生物、能源、环境、医学、半导体及食品等领域。其销售网络遍布世界各地，HORIBA集团致力于向用户提供具有世界先进水平的分析仪器及系列产品，以不断提升人类的生活质量。其中，尤其是HORIBA Scientific（HORIBA 科学仪器事业部）隶属于HORIBA 集团，是全球最大的分析与检测仪器制造商之一。此外，应邀的嘉宾还会有：许敏 教授（Professor Min Xu）机械与动力工程学院 汽车研究院院长SAE fellow上海交通大学特聘教授Robert Bosch 博世公司讲席教授孔令逊 教授 (Professor David L.S Hung)密西根学院副院长SAE fellowShanghai 1000 Scholar Plan AwardNew Century Excellent Talents in University (NCET) Award吴晓东 副教授 (Associate Professor Xiaodong Wu)美国电气与电子工程师协会IEEE/IES会员IACSIT MemberIEEE Transactions on CSS ，Elsevier等国际期刊组织审稿成员','2018-07-04 15:00','2018-07-04 16:00','上海交通大学密西根学院 龙宾楼 302 A-B','https://tongqu.me/upload/acts/20180701/small_7130a94859183d768802d508f3798588.png','同去网','2018-07-04 15:00:00.0');
