insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1397459153,'2018年社会实践分论课——PPT POSE——让你的讲演展示有“腔调”','课程名称：PPT POSE——让你的讲演展示有“腔调”授课教师：潘冬远授课时间：7月8日14:00——15:45授课教室：逸夫楼200号课程简介：制作PPT是一项必备技能，是进行讲演展示的前期基础环节。与其他PPT制作课程（或者经验交流）关注PPT软件操作技巧和图形动画技术不同，本课程更加注重PPT制作与讲演内容的结合，以“观点凝练-结构搭建-形式设计（包含操作技巧）-更高要求”为讲课思路，带领学生探究PPT制作的逻辑起点和根本目的，促使学生了解PPT制作的大体思路和基本技巧，以期指导学生更好进行讲演展示的PPT制作。同时，针对学生在社会实践“总结展示”环节的实际需要，进行社会实践开展过程和PPT制作的案例交流。每位选课学生，须至少修读一门社会实践分论课，请各团长与团队成员充分沟通，尽量保证两类课程均有团队成员参与学习。课程结束后，请填写课程问卷，并交至课程助教处。','2018-07-08 14:00','2018-07-08 15:45','逸夫楼200号','https://tongqu.me/upload/acts/20180628/small_c54ee746f2ab03d40af882c552318059.png','同去网','2018-07-08 14:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2012201765,'2018届本科毕业演讲比赛','长亭外，古道边，芳草碧连天。暖煦六月，毕业的钟声终究还是响起在耳畔留下的是不舍与眷恋，带走的是自信和恬然以青春之名—— 把酒祝东风，且共从容！ATTENTION！“以青春之名，践交大责任”上海交通大学2018届本科毕业生演讲比赛时间：6月29日 16：00——17：30地点：上海交通大学闵行校区图书信息楼九楼盛闻悦我永远喜欢二餐川味点心阮雅烨幸获柒年照拂，愿未来凭己之力让母校更加闪亮！查鑫澄愿每一个交大人都成为时代的开拓者郝笃行感谢交大和致远，帮助我在困境中找到勇气池松恒鸿鹄万里去，温酒待君归，愿诸君此后万事胜意一切安好，来日相见你我依旧少年。廖书恒感恩母校，愿与所有交大人共同追寻梦想！彭灿邦七年交医时光，充实、难忘、深刻，感恩您的栽培！焦文宾青春最美的相遇，就是遇见了你，交大！杨闰哲Seeing is Believing，去亲历，去改变王子昭感谢交大在我们心中种下梦想，让我们一往无前！','2018-06-29 16:00','2018-06-29 17:30','图书信息楼九楼','https://tongqu.me/upload/acts/20180628/small_d0e2f4660913ca4eb18025aa651a5bc2.jpg','同去网','2018-06-29 16:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1260776524,'2018年社会实践分论课——同理心沟通','课程名称：同理心沟通授课教师：汪国琴授课时间：7月6日16:00——17:30授课教室：东中院1-106教师简介：汪国琴，女，上海交通大学心理咨询中心副主任、副教授，中国心理咨询师协会专业委员会委员、国家职业心理咨询师考评员,上海高校心理咨询协会理事。在交大开设《情感心理学》课、“小凡倾听——亲密关系发现之旅”系列团辅，受到参与者的高度好评。主持“小凡老师情感工作室”。曾担任7000m载人潜水器“蛟龙号”潜航员的心理教练；曾赴美国加州大学伯克利分校心理咨询中心进行访学交流；曾获得全国心理健康教育先进个人、上海市青年教师心理健康教育课程大赛一等奖等荣誉。具有较丰富的个体咨询和团体辅导经验。喜欢用平常话、身边事、真性情与大家分享心理学在日常生活中的运用。课程简介：当你踏上社会实践之旅，小伙伴们的朝夕相处，必然面临观点上的摩擦与碰撞，做事风格上的迥异与磨合，你可曾想过：如何更好地倾听彼此，欣赏彼此，让社会实践之旅成为“情感账户”的存款之旅？如何在知国情的同时懂人情、充分发挥团体合作的效应？本次分论课将由校心理咨询中心汪国琴老师给大家介绍“情感账户”的概念、同理心沟通技巧、往情感账户上存款时的五个基本“币种”和注意事项，并进行现场演练，协助大家体会和践行“先处理心情、再处理事情”、“给对方所需而非给自己所要”、“己所欲也勿强施于人”的具体做法。协助大家运用心灵的荧光笔标明彼此的善意和长处，让社会实践之旅成为彼此的优势发现之旅。每位选课学生，须至少修读一门社会实践分论课，请各团长与团队成员充分沟通，尽量保证两类课程均有团队成员参与学习。课程结束后，请填写课程问卷，并交至课程助教处。','2018-07-06 16:00','2018-07-06 17:30','东中院1-106','https://tongqu.me/upload/acts/20180628/small_89879d76fabe987123be36c589f8941f.png','同去网','2018-07-06 16:00:00.0');
