insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1831224614,'2018年社会实践分论课——安全防范知识培训','课程名称：安全防范知识培训授课教师：马秀军、杨立志授课时间：7月8日18:00——19:30授课教室：东上院100教师简介：马秀军，男，汉族，上海市公安局闵行公安分局交警五大队宣教专员，三级警长。马警官从警十余年，在维护交通秩序稳定的同时经历无数事故现场，见证事故惨烈，感慨生命脆弱，心系群众生命安全的他以一颗炙热的心灵向大家讲述交通安全的重要性，以期人民群众出行平安、阖家团圆！杨立志，男，汉族，交通大学保卫处治安科科长，保卫处54749110报警电话宣讲团成员，2017年交通大学校长奖获得者。杨老师公安侦查专业毕业，毕业后长期奋斗在校园治安管理及保安队伍管理一线，教学案例丰富，教学方式亲和，其深知治安管理工作“预防为主 打防结合”原则的指导意义，惟愿用最朴素真实的行动守护校园师生的生命财产安全！课程简介：安全防范知识培训课程与师生生命财产安全密切先关，课程内容含交通安全知识、消防安全知识及治安防范安全知识三块内容。交通安全知识含道路交通安全的重要原则、行人在道路上通行的注意事项、做文明乘车人及文明骑行人的几点要求等；消防安全知识含灭火器材的使用、消防火情的应对技巧、逃生要领及消防法规相关内容；治安防范知识主要讲述校园常见案件类型、诈骗案件及盗窃案件防范常识、旅途安全知识等内容。本课程教学时长90分钟，讲授内容以丰富的案例解说安全防范的相关知识点，以身边的安全故事显现潜伏的风险隐患。案例袭击眼球，故事深入人心，真实且严肃，细微但重要，对提升学员安全防范意识意义重大！每位选课学生，须至少修读一门社会实践分论课，请各团长与团队成员充分沟通，尽量保证两类课程均有团队成员参与学习。课程结束后，请填写课程问卷，并交至课程助教处。','2018-07-08 18:00','2018-07-08 19:30','东上院100','https://tongqu.me/upload/acts/20180628/small_1d2bc917c096a8da4c33b6d961a3301e.png','同去网','2018-07-08 18:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(27829704,'2018年社会实践分论课——非虚构写作——写出直抵人心的报道','课程名称：非虚构写作——写出直抵人心的报道授课教师：兰天鸣授课时间：7月5日18:30——20:00授课教室：东上院315教师简介：兰天鸣，新华社上海分社政治文化采访部记者，毕业于中国传媒大学电视学院，曾就职于中国青年报《冰点周刊》，有过西部驻站经历，一直从事调查和特稿新闻写作。个人作品《“二十四条”阴影下》《走出13号室》《中国农村剩男调查》《世界第一高楼命悬半空》《天水——一个西部城市的水危机》等。课程简介：本课通过对非虚构理论、写作技法、案例、作品诞生背后的故事的讲述剖析，旨在带领同学们了解非虚构写作，初探非虚构写作魅力，了解新闻也可以不追 “热点”“焦点”，可以超越一天的生命力。希望同学们通过课程，构建出一种新闻“冷视角”看待外部世界，培养出特稿写作感觉、对事实的敬畏心和矜持态度，以及更深切的人文关怀。每位选课学生，须至少修读一门社会实践分论课，请各团长与团队成员充分沟通，尽量保证两类课程均有团队成员参与学习。课程结束后，请填写课程问卷，并交至课程助教处。','2018-07-05 18:30','2018-07-05 20:00','东上院315','https://tongqu.me/upload/acts/20180628/small_39520fb8f9185d1ef982ea0f9b1c796e.png','同去网','2018-07-05 18:30:00.0');
