insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1559302819,'上海交通大学通识教育名家讲堂第76讲：德国30年来的哲学','上海交通大学通识教育名家讲堂第76讲德国30年来的哲学主讲嘉宾：沃尔夫冈·顾彬(Wolfgang Kubin)主办单位：上海交通大学文科与通识教育委员会  教务处  人文学院讲座时间：2018年 6 月 29 日（周五），14：00---16：00讲座地点：闵行校区人文楼100号主讲嘉宾简介：沃尔夫冈·顾彬(Wolfgang Kubin)，汉学家、翻译家、作家，波恩大学汉学系教授，德国翻译家协会及德国作家协会成员。以中国古典文学、中国现当代文学和中国思想史为主要研究领域。主编德文杂志Minima Sinica（袖珍汉学）、Orientierung （东方）。主要作品和译著有《中国诗歌史》、《二十世纪中国文学史》、《鲁迅选集》（六卷本）等。获中国政府友谊奖(2016)、2017 国家外教年度人物奖（中国国家奖）等奖项。讲座内容简介：德国在世界哲学发展史上占有重要地位，曾经涌现出众多享誉全球的哲学家。德国哲学对中国的哲学研究影响颇大，中德哲学交流也日趋活跃。两个德国统一后哲学界发生了很多变化。本讲座梳理30年来德国哲学的发展情况，并以一些当代哲学家为例，剖析当代德国哲学的典型特征，阐释德国哲学何以是一种追问为什么的哲学，分析中国与德国对语言理解上的差别，并指出德国哲学的新倾向。','2018-06-29 14:00','2018-06-29 16:00','闵行校区人文楼100号','https://tongqu.me/upload/acts/20180625/small_6fa7961692495fc2de984e58e7b1d092.png','同去网','2018-06-29 14:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1636179574,'上海交通大学通识教育名家讲堂第75讲：中国新石器时期宴饮传统与社会复杂化的发展','上海交通大学通识教育名家讲堂第75讲中国新石器时期宴饮传统与社会复杂化的发展主讲嘉宾：刘莉主办单位：上海交通大学文科与通识教育委员会  教务处  人文学院讲座时间：2018年 6 月 27 日（周三），14：00---16：00讲座地点：闵行校区人文楼100号主讲嘉宾简介：刘莉，哈佛大学人类学博士（1994），现任斯坦福大学东亚语言与文化系Sir Robert Ho Tung讲席教授，澳大利亚人文学院士。刘莉教授学术成果丰硕，获得极高的国际学术声誉，主要研究西方考古学理论、东亚早期文明、古代中国艺术及信仰等。代表性专著有：《早期中国国家的形成》，《中国新石器时代：迈向早期国家之路》，《中国考古学：从旧石器晚期至青铜时代早期》等。报告内容简介：宴饮是世界各地区人类共有的一种文化现象，在文明发展过程中具有重要的作用。在中国古代礼制传统中，宴饮是重要的组成部分。通过分析中国新石器时代遗址中的饮酒器物和遗存的微型残留物，具体认识酿酒的起源和发展，洞悉史前不同地区的宴饮活动，以及与之相应的礼仪行为和文化价值观念的形成。在此基础上，了解宴饮之传统何以在某种程度上推动了史前中国由蒙昧状态进入酋邦社会，由此深入理解史前中国由蒙昧而向文明演化的进程，并揭示蕴含其中的奥秘。','2018-06-27 14:00','2018-06-27 16:00','闵行校区人文楼100号','https://tongqu.me/upload/acts/20180625/small_8caf3f4acb8199c5ab8f1bf7002a7e0d.jpg','同去网','2018-06-27 14:00:00.0');
