insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1893176792,'2018年度杨元庆基金颁奖仪式','【活动通知】为了加速上海交通大学计算机一流学科建设和创新人才培养，85届计算机系校友和杨元庆校友捐资于2015年5月成立了85届计算机系教育发展基金暨杨元庆教育基金，支持上海交通大学计算机系本科学生、硕士生、博士生和青年教师发展。欢迎各位电院同学，特别是计算机系同学于本周日下午1:45分在电群3-200出席2018年度的基金颁奖活动（包含学长论坛），学习获奖同学的先进经验，激励大家在下一年度积极申报基金奖项。该活动主要面向全体电院学生，特别是 计算机系本科及研究生同学 ，将有独角兽公司CEO学长和加拿大院士和同学进行现场交流:嘉宾名单：杨元庆 - 联想集团董事长兼 CEO 陆益民 - 中国联通集团 CEO、副董事长 凌晓峰 - 教授、加拿大工程院院士 钱蓬 - ODE 咨询公司中国区总经理本科生记一次1161项满分讲座素拓研究生记一次讲座素拓','2018-06-24 13:50','2018-06-24 14:50','电院3-200','https://tongqu.me/upload/acts/20180621/small_e75af6f40c25cbd3004f663fbb80d0af.jpg','同去网','2018-06-24 13:50:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(331444263,'躺在床上也能日入千元的创业机会，是不是碰到了假师兄？！','我们是谁记得校庆期间被刷屏了的交大朋友圈么，还记得那个带着校徽圆圈的头像么，那就是有来有趣（y1y.me,中间是数字1）的创意。有来有趣是一家众包打造微信好玩创意应用的平台，任何人不需任何技术，就可以在几分钟内制作出类似交大头像的遍朋友圈的创意。公司是零号湾最早的一批入住团队。有来有趣团队介绍创始人毕业于交大计算机系，在学校只是一名普通的学生，依靠微信的风口，从一名普通的程序员，到年收入数百万。我们核心成员毕业于交大、南大等名校，并曾在腾讯、宝洁等知名企业任职。为什么启动这个项目我们相信"越分享越有价值"。随着微信“小程序”、“小游戏”日趋火爆，微信互联网的下一个风口已经在路上。我们相信最有创意的人，还是在校的学生。我们愿意把我们所有的经验无偿分享出来，给在校学生提供一个创业而不是实习的机会，让没有技术基础的同学都可以在移动互联网时代找到一个属于自己的舞台。本项目在前期的内部推广测试中，已成功地让数名没有互联网运营经验的外部合伙人月入15-30万（后台数据可查证）。相信富有创意的交大同学，更能在我们的平台上取得更好的成绩。项目形式【线下培训】我们会先从小程序运营的基础培训开始，帮助大家快速做出属于自己的一批小程序。【床上实操】接入变现渠道，随时随地根据数据不断优化运营套路。【定期交流】每周一次的交流会，一起分享自己的运营战绩。要求只要对于任何对微信创业, 对新媒体，对小程序感兴趣的人都可以加入我们的“小程序”项目。运营过微信公众号的优先运营过网站的优先，有自媒体经验的人优先，懂技术，懂产品的优先，有线上活动策划经验的优先当然这些都不是最重要的，最重要的是你的热情和创意。名额：择优录取，招满为止，上限10人。有兴趣可手机联系：13706242451，或把简历砸到syb@shengye.co(注明来自同去','2018-08-01 14:47','2018-12-31 14:47','零号湾','https://tongqu.me/upload/acts/20180621/small_585b947823d67d1ef8bf2d2b7d7b691a.jpg','同去网','2018-08-01 14:47:00.0');
