insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1859803065,'树根互联人工智能实验室实习生招聘','欢迎加盟树根互联，用你的聪明才智，在智能制造新浪潮中占有一席之地！公司官网： http://www.rootcloud.com/简历投递： suikaixin@163.com，请注明实习期开始和结束时间公司地址： 北京市海淀区西北旺东路 10 号院东区 5 号互联网创新中心树根互联人工智能实验室（AI LAB） 由 IEEE Fellow（国际电气和电子工程师协会院士） 刘震博士带领。刘震博士是树根互联的联合创始人兼首席技术官，曾任微软亚洲工程院院长、罗技首席技术官、诺基亚发展中国家研究院院长， 在云计算、 大数据、 人工智能、 通信网络及分布式系统等领域取得了重要的研究成果，发表论文 200 余篇。 实验室核心技术团队成员来自于微软亚洲研究院、 清华大学和北京大学等，在机器学习和大数据分析方面有深厚的技术积累和极强的技术原创力。无论你是喜欢和数据打交道，设计 AI 算法解决工业互联网实际问题，还是喜欢接触各类大数据开源平台，构建高性能的数据分析引擎， 树根互联人工智能实验室都期待你的加盟。 在实习期间， 你将接触到真实的海量的工业大数据， 得到技术专家一对一的指导和培养， 在各项有挑战的项目中迅速提升你的技术能力和实战经验。薪酬福利    1. 日薪 300 元    2. 转正机会和富有竞争力的薪酬待遇    3. 浓厚的技术氛围，参与核心工作    4. 和谐的团体氛围，定期团体活动    5. 轻松的工作环境，健身器材免费使用    6. 每日餐补和加班通勤补助    7. 佳节更有贴心福利职位描述    1. 使用数据挖掘或统计学习领域的前沿技术构建设备画像    2. 对设备行为特征大数据进行分析和建模    3. 负责风险评估、异常检测和故障预测等核心技术的研究和开发    4. 设计并实现机器学习算法从海量工业数据中挖掘并解决最有价值的问题','2018-08-01 13:24','2018-09-01 13:24','北京市海淀区西北旺东路 10 号院东区 5 号互联网创新中心','https://tongqu.me/upload/acts/20180621/small_c9a7dd2889d865b9304fdcd9f8c97986.jpg','同去网','2018-08-01 13:24:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(100256030,'西门子医疗器械有限公司【供应链部门实习生】','实习地点：上海岗位职责Support SI(Source&Inventory)部门老板及同事处理部分物流订单及信息整合与更新；学习使用SAP系统相关操作（有很nice的同事教）；定期参与部门会议，协助完成阶段性的医疗器械物料数据的更新。岗位要求要求英语听说读写能力良好，通过CET-6，有海外经历及德语背景优先。熟练使用MS office等办公软件；工作积极，适应性强，有责任心，沟通能力良好，协调组织能力强；每周三天时间，可稳定实习6个月者优先，遇事可以请假；能够尽快入职者优先。专业不限。商务、物流、生物医学、德语等相关专业者优先。薪资福利本科生15/h，研究生20/h，饭补15/day。上下班有班车接送，覆盖上海绝大多数地区。不定期会有部门经理及同事进行专业培训，以促进实习生对医疗行业及供应链的深入认识。超级nice的老板和同事，工作氛围舒适轻松。部门会议均用英语，可以锻炼口语能力，培养英语思维，是提前适应外企环境的好机会。简历投递简历&邮件名称：【姓名+学校+年级+专业+一周可以实习天数+持续时间+到岗时间】请同时发送简历至邮箱：yixiao.guo.ext@siemens-healthineers.com；  365773783@qq.com','2018-06-30 12:49','2018-10-31 12:49','浦东新区周祝公路278号','https://tongqu.me/upload/acts/20180621/small_b001ebc7ee4ed52119e649204b205a6b.jpg','同去网','2018-06-30 12:49:00.0');
