insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(40922841,'06月29日晚-07月01日 穿越华东香格里拉—仙居公盂 梯田 篝火 音乐','活动须知【目的地介绍】 　　仙居公盂村,最早是台州的几个玩摄影的发现的,那边的地形地貌竟然和江南地区完全不同,山都是直立的,象在桂林阳朔,群山环绕下的小盆地,是个世外外桃圆般的小村子,公盂,喜欢坐在村头的草坪上喝着咖啡看着小说,听风吹过的声音和久违的炊烟,或去赏杜鹃,和老农挖笋. 公盂位于仙居县田市镇境内，距县城38公里的西南处，与十三都景区边缘，核心面积31平方公里，造型地貌密度大、品位高，岩崖峰岗变化万千，以巍峨著称。地形构造呈多级梯状，中心海拔1150米，整个公盂岩巍峨鼎立，气势峥嵘，石林崖壁，各有风采。有人说公盂就像是一幅画，一幅需要住下来，静静品味，静静享受的画。小雨过后，雾气从峡谷开始升腾，公盂成了一个缥缈的仙境，奇峰幻化成了无数的蓬莱仙岛，公盂村就成了这仙境中的天宫，置身其中，心早已飘飞到山顶，翻越到仙境。这是水墨山水画里的公盂。活动详情【行程安排】：第一天06月29日星期五晚上19:00人民广场博物馆南大门集合出发,半夜前坑村下高速扎营或农家睡觉...第二天早上起来,镇上早饭,前坑村是公路终点，沿石块铺成的主干小道前行，到公盂约8KM山路，3小时可到。公盂村里约有30户人家，比较热情，有土鸡，山上有自来水（山顶的水流下来的）同时已通电。到公盂可以在公盂村休息，也可以去去公盂背，从村里出发到登顶约30分钟，但路很陡，峰顶海拔约1200米，..晚上农家菜，宵夜烧烤，发呆，看星星，....想干嘛干嘛 我们住村里 篝火晚会哦 ！！！晚上村里热闹的很哦第三天7点起床,吃完早饭，.继续拔营前进了，从西北面的山路经过近3个小时的下山路前往本次活动徒步的终点林坑.一路风景秀丽，山路崎岖。（约有10KM左右山路）。..中午吃农家菜，吃完休息....可以去河滩摄影，休整完 下午一点收队轻松回家。大约19：:30左右到达上海','2018-06-29 19:25','2018-07-01 19:25','仙居公盂','https://tongqu.me/upload/acts/20180619/small_79c8cb6c329e2662075cf85e494b9301.jpg','同去网','2018-06-29 19:25:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1672881953,'港澳台办学伴招募','【夏季学期学伴招募】7月1日预计有275名港澳台高校的学生来校参加夏季学期的课程学习。现拟招募部分学伴，协助处理相关安排事宜。招募人数：10-15人主要负责：1、接机。7月1日周日。宿舍楼两栋各需要2名同学；浦东机场、虹桥机场预计6个班次接机，需要6-8名同学。2、之后，夏季学期在7.7-8，7.14-15，7.21-22会安排3次出游活动，分别是乌镇杭州，南京，绍兴，可以安排同学们作为学伴参加。3、夏季学期的活动课余活动；预计会举办一场晚会，可由学伴资助策划。4、送机安排。欢迎同学们报名参与夏季学期接待！注：参与接机的同学可给予报酬，按助管酬金150元/天发放。','2018-07-01 15:31','2018-07-30 15:31','上海交通大学闵行校区','','同去网','2018-07-01 15:31:00.0');
