insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-761009703,'僵尸网络溯源——2018年暑期社会实践青年教师科研专项','所属院系电子信息与电气工程学院项目简介僵尸网络是当前网络攻击的重要源头，大规模的攻击事件背后都有僵尸网络的支撑。谁是幕后的黑手，怎样追踪僵尸网络以及溯源到具体的C&C服务器、具体的人，是本项目的目标。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长希望参与同学是电院对网络信息安全有充分兴趣，并期望通过自己动手实践解决网络信息安全问题。项目能够得到指导老师单独指导，并帮助解决相关的问题。学生可以通过项目得到科研项目的全面训练，学会解科研问题的普遍方法，提高动手实践能力。申报学生可得到申报老师的直接指导。教师简介邹福泰具有较为丰富的本科生科研实践指教经验（PRP，大创，暑期科研实践，毕业设计等），对待学生认真负责，指导本科生多次获得全国级别的信安安全竞赛一等奖。研究领域为恶意代码/僵尸网络检测、区块链安全、大数据安全分析等。已发表超过30篇的为SCI/EI索引论文，授权国家发明专利18项，论著3本。','2018-07-01 09:17','2018-08-31 09:17','闵行校区软件大楼移动安全实验室','','同去网','2018-07-01 09:17:00.0');
