insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1364324350,'2018年春季学期港澳高校港澳籍、内地籍与台籍交换生信息统计','2018年春季学期的港澳交换生同学们，学校将会在近期发放“澳与内地高等学校师生交流计划”（简称“万人计划”）资助，来自香港、澳门高校的港澳籍、内地籍与台籍的2018年春季交换生同学皆可以申请，请大家在规定时间内提交相关信息。申请资助的同学必须办理中国银行卡，并将自己的交大学生证、港澳通行证（港澳籍）/身份证（内地籍）/台胞证（台籍）、中国银行卡的图片上传至系统（三张证件拍摄一张图片）。我们将会进行审核并及时发放资助。所有的内容请用简体中文填写，所有的选项必填。','2018-06-30 08:00','2018-07-01 08:00','闵行校区','https://tongqu.me/upload/acts/20180614/small_6d8d6e21c8401069f262970a34bcfd35.jpg','同去网','2018-06-30 08:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-873978083,'基于大学生获得感的思想政治理论课教学设计优化——2018年暑期社会实践青年教师科研专项','所属院系：马克思主义学院项目主题、内容及目标：一、项目主题在“立德树人、学在交大”教育思想大讨论背景下，了解交大学生个性特点、成长发展需要与需求，探索课程思政理念下“有虚有实、有棱有角、有情有义、有滋有味”的思想政治理论课教学设计。二、研究内容1.上海交通大学学生修习“思想政治理论课”获得感调研以访谈为主要方式调研交大学生修习《思想道德修养与法律基础》、《毛泽东思想和中国特色社会主义理论体系概论》获得感，以大一和大四学生为访谈主体对象，调查修习“思想政治理论课”对不同年级学生的影响，通过“回头看”形式探究“思想政治理论课”获得感的长效性问题。2.思想政治理论课新版教材与学生成长成才发展需要契合度调研把握交大人才培养理念和办学理念特色，以访谈为主要方式深入了解交大学生的个性特点、成长发展的需求和期待。在研习新版教材的基础上，探究契合交大学生情况、与各类课程同行同行的“思想政治理论课”教学体系。3.《思想道德修养与法律基础》与《毛泽东思想和中国特色社会主义理论体系概论》教学设计优化在前述研究基础上形成涵盖教学目标、教学内容、教师角色、教学方式、课堂管理、课程评价为主体的教学设计优化方案。三、研究目标根据教育部思想政治理论课建设标准要求和交大学生个性特点，形成满足交大学生成长发展需求和期待的《思想道德修养与法律基础》和《毛泽东思想和中国特色社会主义理论体系概论》课程教学设计方案。根据教育部思想政治理论课建设标准要求和交大学生个性特点，形成满足交大学生成长发展需求和期待的《思想道德修养与法律基础》和《毛泽东思想和中国特色社会主义理论体系概论》课程教学设计方案。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：本项目面向上过《思想道德修养与法律基础》或《毛泽东思想和中国特色社会主义理论体系概论》课程的学生，年级不限、专业不限，会使用Citespace或SPSS软件者优先。本项目将选拔5-8名来自不同专业的学生开始项目调研，调研对象为交大学生，深入了解修习“思想政治理论课”的获得感。通过参与项目研究，学生可锻炼交流沟通能力，提升理论思维能力和写作能力，增进对自身成长发展的认识，优化个人发展规划，同时为改进思政课教学做贡献。教师简介','2018-06-26 00:00','2018-09-26 23:59','上海交通大学','','同去网','2018-06-26 00:00:00.0');
