insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1739899000,'垂直起降无人飞行器研发与设计——2018年暑期社会实践青年教师科研专项','项目所属院系：航空航天学院项目项目主题、内容及目标：进行各类垂直起降无人飞行器试飞实验，进行设计改进、制作、试飞与数据收集工作。对垂直起降无人飞行器试飞过程中的起降性能、巡航稳定性能、垂直起降切换区间飞行品质等主要参数进行数据收集与评估，根据收集到的数据进行分析与飞行器设计改进工作。教师简介：吴俊琦多年从事无人机领域技术研究，带领团队获得2项无人机类竞赛国际一等奖、6项全国一等奖。项目简介：无人机作为未来科技发展综合体，将在新零售、新制造、新数据等各领域蓬勃发展。无论是物流无人机、作业无人机、wifi无人机，都需要垂直起降、高速飞行、智能思维等新能力。经费保障1万元','2018-07-01 00:00','2018-09-01 23:59','交大闵行校区','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1636150341,'美丽乡村建设——农户调研及农居环境改造设计——2018年暑期社会实践青年教师科研专项','项目所属院系：设计学院项目项目主题、内容及目标：项目是美丽乡村建设中的“农户调研及农居环境改造设计”这一部分工作内容，通过实地调研、访谈、体验，了解农户实际需求，进行有效、合理的农居环境改造设计。教师简介：苏晓静，博士，讲师，设计学院设计系环境设计专业主任，研究领域包括城市景观规划与设计、小城镇综合环境规划与设计、传统文化与现代设计。项目简介：浙江省嘉兴市姚庄镇政府委托项目经费保障：横向课题项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：本科二年级及以上，环境设计、视觉传达设计、建筑设计、园林专业等专业学生及其他专业对艺术、设计有一定基础和兴趣的学生，通过2个月左右的真实项目跟踪学习与工作，掌握一定的调研方法，获得第一手资料，提高设计实践能力。','2018-06-28 00:00','2018-08-28 23:59','嘉兴','','同去网','2018-06-28 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-287584399,'北斗导航接收机探索——2018年暑期社会实践青年教师科研专项','项目所属院系：航空航天学院项目项目主题、内容及目标：我国北斗导航系统刚刚进入第三阶段建设。本项目旨在帮助学生了解我国北斗导航产业发展现状、行业内领先企业的发展现状；通过校企联合（交大-司南联合实验室），掌握北斗导航接收机开发的相关基础知识。教师简介：张欣研究领域：卫星导航、组合导航、自主导航项目简介：通过航空航天学院本科生、研究生的卫星导航原理课程教学、PRP、本科生导师制培养学生的过程，发现本项目内容可能有助于培养学科兴趣与实际能力。经费保障相关支出有科研经费支持','2018-07-30 00:00','2018-09-09 23:59','航空航天学院GNC实验室、上海司南导航有限公司','','同去网','2018-07-30 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1125662911,'数据资产化交易所——2018年暑期社会实践青年教师科研专项','项目所属院系： 电院项目主题、内容及目标：基于BTS石墨烯的底层公有链打造的数据资产化交易所教师简介：邹福泰，具有较为丰富的本科生科研实践指教经验（PRP，大创，暑期科研实践，毕业设计等），对待学生认真负责，指导本科生多次获得全国级别的信息安全竞赛一等奖。研究领域为恶意代码/僵尸网络检测、区块链安全、大数据安全分析等。在本项目实践中指导区块链原理和算法设计。项目简介：ExuanChain（易宣链）是易宣链团队打造的数据交换公有区块链，也是共享智能硬件生态下基于区块链技术打造的基于大数据用户标签的数据交易所。易宣链是易宣链数据交易平台的底层链，不仅支撑易宣链数据交易所高频的数据交易，同时还支持开发者进行应用开发，基于易宣链的应用开发，除了可以利用区块链的技术特性，还能够获得各行业多维度数据的支持。在易宣链项目共享营销价值商业生态中，通过区块链技术中不可篡改、共识机制、加密算法、隐私保护等特性，商家的交易流水被记录在区块链公有链中，成为去中心化交易所的数据源，并面向中小额贷、银行等金融机构提供权威、清晰的用户群体画像及商家消费数据。由校外创业团队以及申报教师本人负责指导。经费保障： 5000/月项目对学生要求：熟悉c/c++编程语言，熟悉linux系统编程，了解非对称密码学','2018-06-23 00:00','2018-09-30 23:59','剑川路951号零号湾创新创业集聚中心','','同去网','2018-06-23 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1149978903,'基于不确定性量化的气动光学效应研究——2018年暑期社会实践青年教师科研专项','项目所属院系：航空航天学院项目项目主题、内容及目标：项目主题：基于不确定性量化的气动光学效应研究内容：通过CFD数值模拟以及实验手段，研究来流不确定性对气动光效应的影响。具体研究内容如下：（1）气动光学问题调研（2）不确定性方法编程（3）CFD数值模拟流场以及气动光学评价（4）干涉光路实验搭建（辅助）目标：研究不确定性对气动光学的影响。教师简介：张斌高超声速空气动力学、先进计算流体力学项目简介：项目基于航天八院八部合作的创新实验室。研究背景为气动光学效应。当飞行器在高空高速飞行时，飞行器上光学窗口上方的空气密度会产生复杂的变化，气流的折射率起伏很大，使得远场峰值强度减少以及光束扩散和出射波前畸变，从而导致光学系统的性能下降，这就是气动光学效应。一般气动光学效应包括图像的抖动、偏移、模糊等。本项目以此为背景，研究流场的不确定性对气动光学的影响程度。经费保障1000元（市内交通费）','2018-06-23 00:00','2018-09-10 23:59','上海航天技术研究院','','同去网','2018-06-23 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-944977626,'视频中物体检测和分割通用框架研究——2018年暑期社会实践青年教师科研专项','项目所属院系： 电信学院计算机系项目主题、内容及目标：在Mask RCNN等最新框架的基础上，充分分析视频数据的特点，结合视频处理和序列处理中的动作估计，时许建模等技巧，设计新的网络结构并在常用imagenet等数据集上训练与测试，研究如何更高效准确的实现视频中的物体检测和物体分割。教师简介：马汝辉，男，博士，副研究员，硕士生导师，IEEE 会员，是省部共建国家重点实验室培育基地、上海市可扩展计算与系统重点实验室、上海云计算联合实验室成员。主要研究方向为人工智能、计算机视觉、虚拟化技术等，获得江苏省科技进步三等奖1项，主持8项项目，包括1项国家自然科学基金，1项上海市专项重点项目，1项上海市“科技创新行动”高新领域项目等，并参与2项国家重点研发计划项目，近年来在计算机领域的顶级国际会议和顶级国际期刊发表SCI/EI索引论文27篇，包括TPDS,TDSC,TCC,TETC,JCST,JSA,ICSM,ICPP等，授权发明专利11件（包括3件美国专利），国际PCT专利申请3件，国内专利申请19件。作为IEEE Transactions on Parallel and Distributed Systems (TPDS), IEEE Transactions on Cloud Computing (TCC), Future Generation Computer Systems (FGCS), Computer and Electrical Engineering (CAEE), International Journal of Software Engineering and Knowledge Engineering(IJSEKE), Journal of Communications and Networks(JCN)等期刊审稿人，以及国际会议ICAPDS 2016 的TPC Member.项目简介：本项目来源于科技部科技专项项目经费保障： 根据实验需求，购置相关材料项目对学生要求：掌握C++,Python,了解机器学习、图像处理的相关知识','2018-07-01 00:00','2018-09-30 23:59','省部共建国家重点实验室培育基地、上海市可扩展计算与系统重点实验室、上海云计算联合实验室（电院3号楼）','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1056883281,'特定免疫细胞多表型图像特征智能分析初探——2018年暑期社会实践青年教师科研专项','项目所属院系：机动学院项目项目主题、内容及目标：特定免疫细胞多表型图像特征智能分析初探，内容为采用机器学习与图像处理手段对特定细胞的特征进行自动分析，结合医学专家知识，实现自动识别。教师简介：黄亦翔数据科学、信号处理与故障诊断、特征提取与降维分析项目简介：本项目背景为医工交叉研究基金项目，由机动学院与仁济医院合作进行。传统技术难以在组织切片内实现对同一个体免疫细胞三种及以上免疫表型特征及其微环境分子的稳定染色和有效识别。本项目旨在利用信号处理与特征提取、图像处理与模式识别技术对胃肠道肿瘤中浸润性免疫细胞单一表型进行自动识别和定量计算。','2018-07-01 00:00','2018-09-10 23:59','上海交大闵行校区与重庆南路校区','','同去网','2018-07-01 00:00:00.0');
