insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(964491457,'人体关键点检测及姿态估计——2018年暑期社会实践青年教师科研专项','项目所属院系： 电信学院计算机系项目主题、内容及目标：对计算机视觉的研究流程有初步的了解，熟悉深度学习的一些基本框架，尝试训练出自己的模型并针对出现的问题改进性能阶段一：学习机器学习和计算机视觉的基本概念阶段二：阅读有关keypoints检测的论文阶段三：尝试运行论文中的开源代码阶段四：尝试在已有的结果上进行改进提高性能教师简介：马汝辉，男，博士，副研究员，硕士生导师，IEEE 会员，是省部共建国家重点实验室培育基地、上海市可扩展计算与系统重点实验室、上海云计算联合实验室成员。主要研究方向为人工智能、计算机视觉、虚拟化技术等，获得江苏省科技进步三等奖1项，主持8项项目，包括1项国家自然科学基金，1项上海市专项重点项目，1项上海市“科技创新行动”高新领域项目等，并参与2项国家重点研发计划项目，近年来在计算机领域的顶级国际会议和顶级国际期刊发表SCI/EI索引论文27篇，包括TPDS,TDSC,TCC,TETC,JCST,JSA,ICSM,ICPP等，授权发明专利11件（包括3件美国专利），国际PCT专利申请3件，国内专利申请19件。作为IEEE Transactions on Parallel and Distributed Systems (TPDS), IEEE Transactions on Cloud Computing (TCC), Future Generation Computer Systems (FGCS), Computer and Electrical Engineering (CAEE), International Journal of Software Engineering and Knowledge Engineering(IJSEKE), Journal of Communications and Networks(JCN)等期刊审稿人，以及国际会议ICAPDS 2016 的TPC Member.项目简介：本项目来源于科技部科技专项项目经费保障： 根据实验需求，购置相关材料项目对学生要求：掌握C++,Python,了解机器学习、图像处理的相关知识','2018-07-01 00:00','2018-09-30 23:59','省部共建国家重点实验室培育基地、上海市可扩展计算与系统重点实验室、上海云计算联合实验室（电院3号楼）','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(514289913,'工科人才创新创业教育需求调研——2018年暑期社会实践青年教师科研专项','项目所属院系：机械与动力工程学院项目项目主题、内容及目标：采用对比研究及调研访谈等方法，了解工科人才创新创业中存在的问题及其对学校创新创业教育的需求，主要研究内容包括：1）国内外对比研究；2）调研访谈；3）总结分析教师简介：张执南，机械与动力工程学院副教授，博士生导师，主要从事创新设计及摩擦学设计方面的教学与研究工作，主持国家自然科学基金课题及子课题3项，企业合作课题10余项。长期担任学生科创及实践项目指导教师.项目简介：项目来源于教育部人文社会科学研究专项任务项目（工程科技人才培养研究），旨在通过项目研究发现工科人才创新创业教育现状，存在关键问题，为创新创业教育提供参考依据。经费保障充足','2018-07-01 00:00','2018-08-31 23:59','上海','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1651090608,'航空叠层结构CFRP/Ti高效精密钻孔研究——2018年暑期社会实践青年教师科研专项','项目所属院系：机械与动力工程学院项目主题、内容及目标：开展航空叠层结构CFRP/Ti精密制孔实验及切削工艺方案研究。研究内容：应用低频振动辅助切削（Low Frequency Vibration Assisted Drilling，LFVAD）技术和先进刀具结构与涂层对碳纤维复合材料/钛合金（CFRP/Ti）叠层结构开展钻削制孔研究；探究钻削参数（切削速度和进给率）对CFRP/Ti钻削力、切削温度、表面缺陷及孔几何精度的影响规律；揭示刀具在低频振动钻削模式下的磨损机理和失效机制；对切削参数进行多目标优化，获得适用于航空叠层结构高效精密制孔的工艺参数。研究目标：揭示航空叠层结构CFRP/Ti的切削机理、表面缺陷形成机制，并建立适用于叠层结构CFRP/Ti高效钻削的工艺方案，全面提高叠层材料的制孔质量和加工效率。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：欢迎有机械、电子或材料专业背景的大二、大三学生或对机械加工、智能制造感兴趣的学生参加。通过参加本项目研究，学生可得到以下预期收获及成长：1.可加深自己对当前航空领域先进材料及先进制造技术的了解；2.可加强自己对科研活动及学术研究的了解，并全面培养自己分析问题与解决问题的能力；3.可充分培养自身的实验动手能力、科技英语与论文写作能力，并加强机械制造与切削工艺方面的理论基础与实际应用能力。教师简介：徐锦泱，上海交通大学机械与动力工程学院特别副研究员、博士生导师，上海市浦江学者，现为美国ASME和SME会员，主要从事航空复合材料精密加工、先进制造技术（如高速切削、硬切削、微量润滑清洁加工等）及切削有限元仿真等研究。目前主持国家自然科学基金项目、国家科技重大专项子课题、上海市浦江人才计划、上海航天科技创新基金重点项目等共5项。近年来在复合材料加工及先进制造领域发表期刊与会议论文40余篇，并担任多个国际学术期刊和国际学术会议的特约审稿人，荣获法国皮埃尔·贝塞尔优秀博士学位论文奖、上海市研究生优秀成果（学位论文）奖等。项目简介：','2018-07-01 00:00','2018-07-30 23:59','机动学院先进制造楼B楼切削磨削与刀具研究实验室','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1099573927,'检测网络所需特征研究——2018年暑期社会实践青年教师科研专项','项目所属院系： 电信学院计算机系项目主题、内容及目标：视频检测的训练集制作成本较高，现有的检测网络几乎都在由不相关的图片数据组成的数据集上训练，这些图片数据往往由清晰的图片组成，而视频贞中的数据往往伴随着抖动，虚焦等情况。当这些网络应用到视频检测中时，由于训练集的缺失，往往无法识别上述情况中的物体，造成识别准确率下降。而如果能利用无监督的学习方式学习网络所需特征，则既能学习视频中的特殊情况，也能避免较高的数据集制作成本。本项目基于CVPR 2017论文learning features by watching object move，将这种无监督的学习方式引入到视频检测中来进行预训练。在已给定物体mask的基础上自动给予bounding box作为训练样本对区域提案网络进行训练，之后在imagenet vid上微调，最后与传统方式训练的网络进行比对，分析这种学习方式的可行性。教师简介：','2018-07-01 00:00','2018-09-30 23:59','省部共建国家重点实验室培育基地、上海市可扩展计算与系统重点实验室、上海云计算联合实验室（电院3号楼）','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1776509359,'新型生物医学胶材料在外周神经再生的应用研究——2018年暑期社会实践青年教师科研专项','项目所属院系：药学院项目主题、内容及目标：学习生物医用胶材料制备方法、表征、细胞（粘附、分化、增殖及毒性等体外生物学表征等）；协助学姐学兄进行体内生物学研究。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：对生物医学感兴趣的学生、有一定研究基础并志向出国或读研的同学尤其欢迎。教师简介：袁伟恩，生物医学材料在药物输送、组织修复的应用及基础研究、转化研究项目简介：周围神经缺损的修复是一大临床难题。神经纳米导管胶具有桥接周围神经缺损、促进神经再生的作用，目前临床使用的产品只能为神经再生提供机械通道，无法提供神经纤维再生所需要的微血管环境，因而再生的速度与质量均不满意，更无法修复长段神经缺损。本课题组前期研究发现：纳米导管具有良好的生物相容性，能诱导骨髓间充质干细胞（BMSC）分化；体内研究也初步证实：纳米导管修复大鼠坐骨神经缺损，再生神经的血管化程度明显高于对照组。因此提出假设：纳米导管具有募集血管内皮细胞的作用，并通过诱导BMSC分化，进一步加速微血管形成，为修复长段神经缺损提供了可能。来源于国家自然基金项目经费保障：横向和纵向课题经费','2018-07-01 00:00','2018-08-31 23:59','药学院','','同去网','2018-07-01 00:00:00.0');
