insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1380929143,'基于相关滤波器的追踪的端到端表示学习——2018年暑期社会实践青年教师科研专项','项目所属院系：电信学院计算机系项目主题、内容及目标：目标追踪是计算机视觉（人工智能重要分支）中是一项有重要意义同时又极具有挑战性的任务，其在视频监控、无人驾驶、智能交通、智慧城市，人机交互等领域有着广泛的应用价值。相关滤波器是一种能够快速在线训练的算法，而卷积神经网络具有强大的特征提取能力，但是却需要大量数据的离线训练。本课题着力解决如何将相关滤波器的在线学习能力与离线训练的卷积神经网络的分类能力结合的难题。针对上述目标追踪的挑战，本课题的主要研究内容包括（但不局限于）：1. 分析现有的将相关滤波器与卷积神经网络的方法的性能和优劣2. 思考对现有方法的改进教师简介：马汝辉，男，博士，副研究员，硕士生导师，IEEE 会员，是省部共建国家重点实验室培育基地、上海市可扩展计算与系统重点实验室、上海云计算联合实验室成员。主要研究方向为人工智能、计算机视觉、虚拟化技术等，获得江苏省科技进步三等奖1项，主持8项项目，包括1项国家自然科学基金，1项上海市专项重点项目，1项上海市“科技创新行动”高新领域项目等，并参与2项国家重点研发计划项目，近年来在计算机领域的顶级国际会议和顶级国际期刊发表SCI/EI索引论文27篇，包括TPDS,TDSC,TCC,TETC,JCST,JSA,ICSM,ICPP等，授权发明专利11件（包括3件美国专利），国际PCT专利申请3件，国内专利申请19件。作为IEEE Transactions on Parallel and Distributed Systems (TPDS), IEEE Transactions on Cloud Computing (TCC), Future Generation Computer Systems (FGCS), Computer and Electrical Engineering (CAEE), International Journal of Software Engineering and Knowledge Engineering(IJSEKE), Journal of Communications and Networks(JCN)等期刊审稿人，以及国际会议ICAPDS 2016 的TPC Member.项目简介： 本项目来源于科技部科技专项项目经费保障： 根据实验需求，购置相关材料','2018-07-01 00:00','2018-09-30 23:59','省部共建国家重点实验室培育基地、上海市可扩展计算与系统重点实验室、上海云计算联合实验室（电院3号楼）','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(340447238,'基于视觉的自动驾驶汽车研发——2018年暑期社会实践青年教师科研专项','项目所属院系：机械与动力工程学院项目主题、内容及目标：项目主题：基于视觉的自动驾驶汽车研发内容目标：学习开发视觉处理算法、学习开发车辆控制算法项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：年级要求：高年级本科生专业要求：机械工程、计算机、通讯工程、自动化等工科专业能力要求：C++熟练、有一定的图像处理或车辆控制算法基础预期收获：学习并实践工业界前沿技术教师简介：王亚飞，博士毕业于日本东京大学电气工程专业，2016年3月起在上海交通大学机械与动力工程学院任讲师，博士生导师，研究方向为智能网联汽车传感器信息融合技术、车辆控制等。他本科毕业于吉林大学汽车学院，硕士毕业于上海交通大学机械与动力工程学院，2008年至2009年在德尔福（中国）科技研发中心从事汽车电子的研发工作，2013年4月至2016年2月在日本东京大学新领域创成研究科从事智能电动汽车控制研究。目前主持包括国家自然科学基金、国家重点研发计划课题和上海市产学研合作在内的各类智能网联汽车项目十余项，另参与多项和美国、德国的国际合作项目。项目简介：项目来源于上海纵目科技有限公司，总部位于上海浦东张江高科技园区，另有北京、厦门分公司。公司主要业务是车辆主动安全和智能辅助驾驶系统。企业创始人及主要技术团队成员都具有在汽车电子行业15年以上的相关技术研发经历，曾担任欧美知名汽车电子半导体企业的技术研发团队负责人，在芯片底层技术，计算机视觉、导航、蓝牙、图形处理和多媒体等多个方面，拥有从算法到系统设计的丰富经验。目前公司70%以上成员具有研究生以上学历，已经拥有多项软件著作权和发明专利。本项目主要工作内容包括：针对特定图像算法的优化、自动驾驶汽车横向以及侧向控制算法的仿真调试。纵目科技的全自动泊车系统经费保障：企业方提供实习补助','2018-07-01 00:00','2018-08-31 23:59','上海纵目科技有限公司/机动学院汽车楼','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1172851831,'新型ER-a/AGR2双特异免疫治疗抗体药效学研究——2018年暑期社会实践青年教师科研专项','项目所属院系：药学院项目主题、内容及目标：主题：新型ER-a/AGR2双特异免疫治疗抗体药效学研究内容：对照单独抗体组合，在整体动物水平研究验证ER-a/AGR2双特异抗体能否富集于表达AGR2的乳腺肿瘤组织内并具有抗ER-a的免疫激活功能和更高的药理活性，以及双特异抗体能否减少ER-a抗体由于靶点免疫细胞分散带来的副作用。目标：明确ER-a对双特异抗体的肿瘤靶向肿瘤作用、双特异抗体的体内免疫抑瘤作用。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：要求学生有一定的分子生物学基础，能够阅读相关文献，进行实验。学生能够掌握双特异免疫治疗抗体的相关理论与实验技术，为以后的科研和工作打下基础。教师简介：武正华,主要研究方向是肿瘤生物药物和肿瘤分子生物学，从事抗肿瘤机制靶标发现和新型生物技术药物研发，在相关领域以第一作者和通讯作者发表SCI研究论文6篇，发表杂志包括 Oncogene等；负责研制出的抗AGR2单克隆抗体18A4现已申请PCT国际专利，并已经在美国、欧盟、日本和澳大利亚获得授权；还获得了相关的国内授权专利6项。项目简介：实体肿瘤是由肿瘤细胞与多种非肿瘤细胞结合形成的独特微环境组织。而分泌型AGR2是肿瘤微环境的形成信号，并在肿瘤组织中高表达。我院艾克舒单抗可以富集在肿瘤组织间隙液中，阻断肿瘤AGR2信号，促进肿瘤细胞凋亡与坏死。免疫检查点蛋白存在于免疫细胞中抑制自身免疫反应，也造成肿瘤的免疫逃逸。用抗体阻断免疫检查点蛋白是目前治疗肿瘤的有效手段，但肿瘤特异性不强，造成多种自身免疫副作用。本课题通过构建、优化、表达、验证与机制研究，验证同时靶向AGR2和免疫检查点蛋白的双特异抗体是否可以通过结合AGR2获得肿瘤靶向性，富集于AGR2阳性的实体肿瘤组织内同时保持对免疫检查点蛋白和AGR2的阻断活性，减少免疫检查点抗体的非靶向副作用并提高双特异抗体的有效性。项目依托药学院细胞工程及抗体药物教育部工程研究中心，组织五个独立课题组，在靶点、构建、优化、规模表达、动物模型和分子机制领域协同研究，以揭示双特异抗体通过靶向肿瘤微环境AGR2对其抑制免疫检查点蛋白对肿瘤抑制与自身免疫副作用的影响和机制，为靶向肿瘤微环境的新型双特异抗体药物的研发提供技术支撑、概念验证和机制研究的基础。经费保障本课题为国家自然科学基金项目','2018-07-15 00:00','2018-08-30 23:59','药学院','','同去网','2018-07-15 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(465194024,'Human Detection In Soccer Game——2018年暑期社会实践青年教师科研专项','项目所属院系：电信学院计算机系项目主题、内容及目标：In this project, you’ll build a detection framework to detect human bodies in a soccer game.• To understand a soccer game, knowing the position of each player is undoubtedly important. Your framework should generate a bounding box for each player in the given image.• The performance of your method consists of two parts: (1). speed, and (2). precision• You may adapt traditional detection framework to soccer game domain.Check SSD [https://arxiv.org/pdf/1512.02325.pdf]and Faster RCNN [https://arxiv.org/pdf/1506.01497.pdf].教师简介：','2018-07-01 00:00','2018-09-30 23:59','省部共建国家重点实验室培育基地、上海市可扩展计算与系统重点实验室、上海云计算联合实验室（电院3号楼）','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(550164375,'“税法教育进校园”实践与调研——2018年暑期社会实践青年教师科研专项','项目所属院系：凯原法学院项目主题、内容及目标：“税法教育进校园”实践与调研；采取参观上海市税务系统征税大厅、税收大数据中心等，以及校园税法宣传、校园调查问卷、税法讲座座谈等方式进行；让在校大学生体验税法教育，落实学校与上海市税务局达成的共建税收法治研究基地合作协议。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：二、三年级，除法学、经济学、管理学、教育学、社会学、新闻学等专业外，其他专业本科生也可报名，但应具有团队意识、调研精神、公益心和纪律性，对“税法教育进校园”主题活动有兴趣。教师简介：王桦宇，北京大学法学博士，上海交通大学法学博士后，上海交通大学财税法研究中心副主任。北京大学第17届“学术十杰”获得者，主持国家社科基金等课题多项，在《中国社会科学》等刊物上发表论文数十篇。主要研究方向：财税法律政策、税法教育、公共治理。项目简介：2015年12月，中办国办印发《深化国税、地税征管体制改革方案》，在31条明确提出“加强税法普及教育。将税法作为国家普法教育的重要内容。把税法教育纳入国民教育体系，加强对青少年的税法宣传教育。开展经常性的税收宣传工作，增强全社会的税法意识。”上海市税务局作为全国税务系统的排头兵，大力推进税法普及教育活动。2017年10月，学校与市税务局达成合作协议，其中“税法教育进校园”宣传与实践是重要合作事项。经费保障：市内差旅、问卷制作、座谈讲座会务等','2018-07-01 00:00','2018-08-31 23:59','上海','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(547568774,'测量火焰的温度——2018年暑期社会实践青年教师科研专项','项目所属院系：机械与动力工程学院项目主题、内容及目标：实现高温高压气体的标准光谱测量标定与层流火焰和湍流火焰的温度场测量。研究内容包括1. 使用高温标定炉，测量半导体激光扫描得到的高温、高压气体分子光谱，并进行分析，并得到温度标定的光谱系数。测量FTIR光谱仪得到的高温高压分子全谱并进行拟合。2. 针对层流火焰进行半导体激光吸收光谱的温度测量，得到准确温度。同时进行双色PLIF温度场测量，用吸收光谱标定双色PLIF测量信号。3. 使用双色PLIF 方法对湍流火焰进行温度测量。研究目标：研究火焰结构与运动的测量的测量方法，从流体力学与热力学角度对湍流燃烧进行定量研究，并研究燃烧与流动的相互作用。教师简介：本课题组致力于燃烧诊断方法的发展，积累了大量经验。实验室建设完备，有各种实验设施和硬件配套，发表了大量光谱测量和燃烧诊断方面的文章。实验平台完整，建设了世界最先进的高频燃烧诊断平台。具有多套高频激光，高速相机相增强器，半导体激光，标准火焰炉和航发模形燃烧室，以及高分辨质谱，自制质谱，催化装置等先进设备，可以支持本项目研究所需。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：本项目将采用多种试验方法，采用几种不同的实验仪器，采用非接触式光谱测量方法，最终测量湍流火焰的温度。本项目通过基础研究，综合培养光电，机械设计，数据处理编程等各方面的能力。对学生要求积极进取，勤于思考和动手。大二及以上，成绩良好，有阅读英语文献的能力。欢迎物理学院和电院的同学参加，本项目的研究很适合理科和光电的学科背景。项目简介：本项目依托本实验室中世界最先进的燃烧测量设备，面向航空发动机模形燃烧室中火焰温度的测量难题开展研究。综合使用多种光谱测量方法，进行温度测量的标定和方法发展。特色为使用高重复频率激光和中红外量子级联激光进行高频温度场测量。经费保障：本课题有国家自然科学基金，科技部项目支持，活动经费充足','2018-06-23 00:00','2018-08-31 23:59','机械与动力工程学院','','同去网','2018-06-23 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1883315287,'印刷体文字的OCR算法研究——2018年暑期社会实践青年教师科研专项','项目所属院系：电院/网络空间安全项目项目主题、内容及目标：研究主题：研究印刷体文字的OCR（光学字符识别）算法研究内容：1）学习光学字符识别的原理及知识；2）学习并分析google的开源OCR程序Tesseract。3）以汉语为例，实现汉语印刷体文字的自动识别算法。研究目标：1）实现汉语印刷体文字的自动识别。2）针对不同的算法给出详细的实验及分析。教师简介：刘功申，博士，网络空间安全学院副教授。主要研究方向是信息内容安全、自然语言理解、机器学习和大数据等。主持和参与自然基金项目5项；参与863项目2项，上海科委和经信委等地方重点项目6项；主持和参与信息安全领域工程建设项目10多项（涉密项目）；在国内外核心期刊及重要会议上发表学术论文100多篇（其中，SCI或EI收录20多篇），独立出版教材3部，合作编写2部，申请软件版权2项、国家专利10多项（授权5项）。带队参加全国大学生信息安全大赛，获得全国一等奖4项。项目简介：随着人们物质生活水平的极大丰富，人们越来越喜欢到其他地区旅游。在旅游过程中，遇到不认识的文字（主要指其他语种的文字）不是偶然事件。如果通过智能手机快速拍照，自动识别，并自动翻译成母语文字，将能满足很多人的需求。本项目研究上述需求的其中一个重要环节，即，如何拍照识别字符。该功能是后续功能的基础，具有重要研究意义。项目来源：国家自然科学基金项目。经费保障：实习酬金1000元补助主要用于学生的交通费项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：对同学的要求：1）具有C语言编程能力。2）在暑假期间保证全身心投入到项目。同学的预期收获：1）学习解决问题的能力；2）共同积累，发表学术论文','2018-07-01 00:00','2018-08-31 23:59','零号湾','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-223472593,'上海市蔬菜大数据与国内外典型案例调研——2018年暑期社会实践青年教师科研专项','项目所属院系：农业与生物学院项目项目主题、内容及目标：项目主题：针对蔬菜生产精准化与智能化的需求，对上海市的蔬菜产业进行大数据获取、分析，并对生产现状和国内外典型案例进行调研。项目内容：（1）根据蔬菜产业链关键环节，选取主要蔬菜种类，如青菜、杭白菜、生菜等，进行调研问卷设计，开展实地走访和数据收集，包括3-4家代表性园艺场（如星辉、弘阳、百蒂凯、多利、城市超市）周年生产数据、批发市场动态价格和数量数据、3-4家大型超市动态价格和数据数据，为上海市设施蔬菜生产经济效益评估模型和供需均衡模型提供大数据支持；（2）根据调研需求，选取国内2-3个设施生产典型案例，进行实地考察调研，并对标美国、日本、荷兰等国家的先进技术，分析其先进生产体系、技术与设施装备。项目目标：形成上海市蔬菜产业链和国内外设施生产高端技术体系调研报告。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：面向即将进入大三或大四阶段的学生；勇于接受新事物和挑战，具有一定的调研设计能力和良好的团队合作能力。通过本项目的锻炼，参与学生能较好地了解蔬菜产业以及上海市设施蔬菜生产现状，同时提升自身的团队合作能力；通过大数据收集和分析，得到一定的数据分析和建模训练，锻炼与人沟通能力的同时，具有一定的社会历练；通过典型案例调研，锻炼文献和资料检索及阅读能力，以及调研报告撰写能力。教师简介：章竞瑾，女，博士，副研究员，现就职于上海交通大学农业与生物学院植物科学系。研究领域为设施生产信息化与环境调控。2010年本科毕业于浙江大学，2014年获美国华盛顿州立大学农业工程博士学位，之后在该校园艺系进行博士后研究工作，2015年入职于上海交通大学农业与生物学院。入选2016年度上海市“晨光计划”人才项目，曾获美国农业与生物工程师学会2014年度最佳研究奖和2017年度最佳论文奖。先后参与国家“863”和国家自然基金重点项目。目前承担国家自然科学基金青年项目、上海市“晨光计划”、上海市科技创新行动计划项目、上海市科技兴农项目、上海市绿叶蔬菜产业技术体系等多项省部级科研项目。承担园艺专业本科、研究生的教学工作，主讲蔬菜学、植物嫁接理论与技术、全英文课程设施园艺理论与技术等课程。项目简介：','2018-07-01 00:00','2018-08-31 23:59','上海市及阜外','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1752510329,'农户生物农药的使用现状调查——2018年暑期社会实践青年教师科研专项','项目所属院系：生命科学技术学院项目主题、内容及目标：对某地区普通农户的生物农药购买、使用现状，以及农民对生物农药的认知程度进行调研分析，并以此为依据总结某地区生物农药的推广使用现状和农民对生物农药的认知程度，以供政府制定生态文明建设和可持续发展相关政策参考。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：农学和药学相关专业，对本项目感兴趣，有环境保护意识的学生。学生通过参加本项目的调研，可以在最基层的实践中锻炼自己的能力，提升自己的环境保护意识。教师简介：蒋海霞，微生物源生物农药的研发项目简介：化学农药残留对人类健康产生了严重危害，人们开始考虑使用生物农药。生物农药的社会效益、生态效益远远大于经济贡献，并已上升到农业生产安全的高度，但目前农村生物农药销售所占比例很低。生物农药的推广需要调查分析农户对生物农药的认知程度和购买意愿。经费保障交通费','2018-08-01 00:00','2018-09-10 23:59','项目成员所在的地区','','同去网','2018-08-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-11048708,'少数民族语言自动翻译研究——2018年暑期社会实践青年教师科研专项','项目所属院系：电院/网络空间安全项目主题、内容及目标：研究主题：研究我国少数民族语言与汉语之间的自动翻译。研究内容：1）学习已有的自动翻译算法（基于统计的翻译算法、基于深度学习的Seq2Seq翻译算法、无监督的翻译算法、基于GAN的翻译算法、多种翻译模型的集成方法等）；2）收集并处理少数民族语言的数据。3）针对上述算法，在收集的数据或者课题组已经有的数据上做实验，并给出评价和分析。研究目标：初步实现一种或几种少数民族语言到汉语的自动翻译。针对不同的算法给出详细的实验及分析。教师简介：刘功申，博士，网络空间安全学院副教授。主要研究方向是信息内容安全、自然语言理解、机器学习和大数据等。主持和参与自然基金项目5项；参与863项目2项，上海科委和经信委等地方重点项目6项；主持和参与信息安全领域工程建设项目10多项（涉密项目）；在国内外核心期刊及重要会议上发表学术论文100多篇（其中，SCI或EI收录20多篇），独立出版教材3部，合作编写2部，申请软件版权2项、国家专利10多项（授权5项）。带队参加全国大学生信息安全大赛，获得全国一等奖4项。项目简介：随着我国“一带一路”倡议的深化，亟需通过自动化手段帮助各族人名之间进行文化交流，帮助跨区域的自助旅游；亟需通过网络公开信息自动感知“一带一路”沿线地区的各种类型的商业信息等。当前的最大困难是沿线地区的语言多属于小语种（例如，壮族语、蒙古语、哈萨克语，藏语，维吾尔语等），完全靠人工掌握这些语言是不可能的。伴随着机器翻译技术的不断发展，借助计算机来实现翻译已经成为一件便捷而高效的事情。基于此，本项目拟开展我国少数民族语言和汉语之间的翻译研究。项目来源：国家自然科学基金项目。经费保障：实习酬金1000元补助主要用于学生的交通费项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：对同学的要求：能够书写任何一种少数民族语言的同学，或者具有深度学习算法使用经验的同学。同学的预期收获：1）学习到利用人工智能技术解决问题的能力；2）共同积累，发表学术论文。','2018-07-01 00:00','2018-08-31 23:52','零号湾','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1921452757,'上海市中小种植农户采用智能化农业机械的影响因素及影响水平——2018年暑期社会实践青年教师科研专项','项目所属院系：农业与生物学院项目项目主题、内容及目标：主题：上海市中小种植农户采用智能化农业机械的影响因素及水平研究目标：1.总结智能化 vs. 传统农业机械的异同点。2.理解并刻画农户采用新技术的决策机制。3.推测农户采用新技术的可能性。4.提炼相关政策建议内容：1.对比总结用户所关心的“智能化 vs. 传统型”农业机械的技术属性，例如节省劳动时间、具备信息记载功能、具备精准作业功能等。2.田野调查上海市涉农大区（例如青浦、金山、松江、闵行）的中小种植农户，调研其“生产、消费、劳动/休闲”的综合决策机制。3.根据内容一“新旧技术的异同点”与内容二“运用模型刻画决策机制”，协助模拟新技术情景下，用户采用新技术后决策变量的变化（农业生产、消费、劳动/休闲）。4.依据上述研究内容（重点是内容三），协助提炼适用于激励微观农户采纳新技术的政策建议。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：年级：大二及以上。专业：对应用经济学感兴趣；对农业机械、智能化技术感兴趣。能力要求：访谈与沟通能力、数据记录与数据清理能力较突出。 预期收获：了解智能化农业机械的发展前沿、初步理解微观经济学基本知识、形成建模思维。预期成长：1.了解上海地区都市农业的基本形态，加深对农业农村现状的认识。2.对智能化农业机械应用前景及阻碍因素形成一定的经验认识，对技术采纳从无到有/从有到广的过程或可能性具备科学分析推测能力。3.通过访谈种植农户，加深对社会的认知和同理心。4.通过调研活动的全程执行与团队合作，提升行动能力、人际互动能力。教师简介：','2018-07-16 00:00','2018-08-10 23:59','闵行、金山、松江、青浦粮食作物生产基地','','同去网','2018-07-16 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(864348103,'创新马铃薯功能冬季饮品 设计与开发的企业实践项目——2018年暑期社会实践青年教师科研专项','项目所属院系：农业与生物学院项目主题、内容及目标：围绕马铃薯、杭白菊、花茶、减糖甜味剂等功能性食材，结合市场冬季饮品的需要，进行产品创意设 计和开发，包括产品市场调研、国内外大型餐饮客户接触业务、产品创意研发、创意广告开发等内容。项目简介：项目开展基于国家马铃薯主食化战略背景，在上海农委推动下，推广马铃薯在中国地区的深加工和消费宣传，进行饮食健康消费的引导。上海交通大学马铃薯主食化创新工作室在与上海德馨浓缩饮料有限公司（浙江德馨浓缩饮料有限公司母公司）正在联合进行马铃薯创新产品的研发。上海/浙江德馨浓缩饮料有限公司是中国餐饮十大饮品供应商之一，主要生产和推广餐饮饮品和配料的食品加工企业，目前是上海市马铃薯主食化推广十大企业之一。企业位于浙江省嘉兴市南湖区，距离学校车程1.5小时。公司可以提供食宿，并提供产品研发、广告部门、市场部门等实习岗位。具体实习时间安排在暑假，具体工作时间可以与企业再沟通安排实习时间。申请校团委支持2000元，用于补贴学生来往高铁费和或出租车费等。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：学生希望二年级及以上的同学，具有食品科学、广告设计、工商管理等专业背景的同学优先考虑。学生通过实行可以了解国内外食品企业市场运作和需求、提高食品产品研发生产实践能力，培养市场推广和宣传等方面的能力。教师简介：研究领域：功能食品与食品化学研究方向：1、食源性功能因子构效关系与作用机制理论研究；2、蛋白质生物活性组分的开发与利用；3、功能性农产品深加工关键技术开发与应用推广。经费保障：申请2000元','2018-07-01 00:00','2018-08-30 23:59','浙江/上海德馨浓缩饮料有限公司','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-689069216,'基于区块链技术的旅游短视频云存储管理系统——2018年暑期社会实践青年教师科研专项','项目所属院系：电院项目项目主题、内容及目标：区块链技术应用，短视频，云存储，版权保护防篡改教师简介：邹福泰，具有较为丰富的本科生科研实践指教经验（PRP，大创，暑期科研实践，毕业设计等），对待学生认真负责，指导本科生多次获得全国级别的信息安全竞赛一等奖。研究领域为恶意代码/僵尸网络检测、区块链安全、大数据安全分析等。在本项目实践中指导区块链原理和算法设计。项目简介：和旅游行业OTA公司合作，对用户UGC短视频内容进行有序整理，并应用区块链及数字视频指纹识别技术实现防伪造和版权云端管理，对评比入选者可获得Token通宝实现游客，旅游景区和OTA平台间三方互信共赢协力。由校外创业博士以及申报教师本人负责指导。经费保障：实习酬金150-300/天项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：希望参与同学有公私钥密码学及图像视频处理基础知识，以利于理解区块链技术原理。。结合旅游行业场景需求的实习过程中，将参与搭建EOS区块链网络及实际短视频应用业务设计，对于区块链技术应用形成实战经验。','2018-06-16 00:00','2018-09-30 23:59','上海/杭州','','同去网','2018-06-16 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1577577393,'健康传播框架下上海青少年吸烟动机影响因素研究——2018年暑期社会实践青年教师科研专项','项目所属院系：媒体与传播项目主题、内容及目标：主题：健康传播框架下上海青少年吸烟动机影响因素研究近年来我国青少年吸烟率显示出增长趋势，并呈现低龄化趋势，初中生吸烟的越来越多，并且受到影视剧、偶像与同伴的影响。青少年吸烟人数的增长问题，成为亟待改善和解决的社会问题，是关系到整个社会甚至影响全世界的公共健康问题。本研究将以计划行为模型为主要理论框架，研究国内的青少年控烟态度和行为，以及影响这些态度和行为的相关因素。目标之一是用结构方程模型测试西方的行为改变理论在上海青少年控烟行动作用中的适用性，分析影响青少年控烟态度和行为的重要核心概念以及概念之间的相互关系，并在此基础上提出适合中国社会和文化背景的控烟研究模型框架。目标之二是分析和解释影响青少年对控烟的态度和行为的因素，并在此基础上提出上海实施控烟宣传与教育计划的侧重点和规划。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：年级：2-4年级均可专业：人文学科、社会科学、管理学、教育学等相关、有兴趣的其他学科也可能力要求：能够进行访谈，问卷发放等研究实践收获与成长： 学生对学术研究有所了解，了解问卷发放和访谈的流程学习到社会科学研究的方法教师简介：王茜，美国普渡大学博士，专业方向为健康传播，控烟研究。新媒体研究项目简介：项目为上海市哲学社会科学研究课题经费保障：项目经费','2018-06-23 00:00','2018-09-10 23:59','上海','','同去网','2018-06-23 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-977629210,'上海市农产品质量安全群众满意度调查研究——2018年暑期社会实践青年教师科研专项','项目所属院系：农业与生物学院项目项目主题、内容及目标：主题：上海市农产品质量安全群众满意度调查内容：开展问卷调研及分析目标：对本市9个涉农区超市、农贸市场发放问卷，各区有效问卷200份项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：学生年级无要求；需要责任心强、对社会调查及分析感兴趣的同学参与。 培养学生学习项目问卷调查方法及沟通技巧，加强了解农产品质量安全教师简介：曹正伟，上海交通大学农业与生物学院讲师，上海市浦江人才。研究领域为都市现代农业评价、农产品质量安全、粮食价格等。项目简介：当前，上海市正在根据第一批国家农产品质量安全县创建经验，对另外七个涉农区同步开展群众满意度调查。《国家农产品质量安全县考核办法》提出的基本考核中，群众满意度占总分值比重为20%。本课题拟及时掌握群众对农产品质量安全的满意程度，从而调查保障农产品消费安全的直接举措，还将为检验属地农产品质量安全监管工作的提供间接决策依据。经费保障：问卷填写10元/份问卷录入5元/份','2018-07-01 00:00','2018-09-30 23:59','上海市9个涉农区','','同去网','2018-07-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-138727697,'我国各地创业活动动因、环境——2018年暑期社会实践青年教师科研专项和政策调研','项目所属院系：安泰经济与管理学院项目主题：我国各地创业活动动因、环境和政策调研项目内容及目标：通过深度调查问卷访谈政府官员、企业家和潜在创业者的形式，理解我国各地创业活动的个人动因、环境以及相关政府政策，并量化评估这些因素的绝对和相对重要性程度。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：1.期望每个省能保证2-3名学生，各省会城市能保证至少1名学生，以保证调研结果的真实性和可靠性。2.年级不限（含大一学生），专业不限。参与的学生须对本课题感兴趣，认真、细心、靠谱，时间上能保证至少2周的全职时间。3.通过本项目，学生可以理解我国各地：a）创业环境、政策以及创业者的不同创业动机；b）经济发展的引擎和差异；c）不同行业未来发展机遇所在。此外，通过和政府官员、企业家和潜在创业者的深度访谈，学生可以透过不同市场参与主体的视角来立体实践理解我国的政商社会。教师简介：舒海兵博士的学术研究主要集中在实证公司金融、公司治理领域，研究成果已发表在Journal of Financial Economics、Journal of Management Studies等学术期刊。更多个人信息，请参见安泰经管学院个人主页。项目简介：活跃的创新活动是每一个经济体持续、健康向前发展的原生动力之一。而创业，作为一种更激进、更具创造性破坏的创新活动，以新的生产关系方式重组各生产要素，从而达到资源的更优配置，进而更大程度上促进区域乃至更大范围内的经济发展。正是基于上述考虑，李克强总理在2014年夏季达沃斯论坛上发出“大众创业、万众创新”的号召，随后又以国务院红头文件要求各地政府、国务院各部委和各直属机构改革完善相关体制机制，推动大众创业和万众创新活动。创业，由于其强破坏性创造的属性，一般是少数人选择的艰难之路。除自身动因外，创业的外部影响因素也有很多，如政府政策、劳动力市场健全程度等营商环境。本调研目的是希望通过深度调查问卷访谈政府官员、企业家和潜在创业者的形式理解我国各地创业活动的个人动因、环境以及相关政府政策，并量化评估这些因素的重要性程度，进而为政府后续相关政策的调整做辅助依据。经费保障：学生自己承担','2018-07-10 00:00','2018-08-20 23:59','全国各省市（以二三线城市为主）','','同去网','2018-07-10 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(950203915,'高效基因转染材料在生物医学的应用研究——2018年暑期社会实践青年教师科研专项','项目所属院系：药学院项目项目主题、内容及目标：学习高效基因转染材料制备方法、表征、细胞（粘附、分化、增殖、转染效率及毒性等体外生物学表征等）；协助学姐学兄进行体内生物学研究。项目对学生年级、专业及能力要求，学生能得到的预期收获及成长：对生物医学感兴趣的学生、有一定研究基础并志向出国或读研的同学尤其欢迎。教师简介：袁伟恩，生物医学材料在药物输送、组织修复的应用及基础研究、转化医学研究项目简介：类风湿性关节炎（Rheumatoid arthritis, RA），是一种原因未明且多发的系统性炎症反应和自身免疫性疾病，发病率高，致残性强，危害大。基因治疗可以针对RA发病机制，具有高选择性和特异性等特点。病毒载体因其自身具有的免疫原性和潜在的致癌性而难以用于临床；非病毒载体具有低毒、低免疫反应、不发生外源基因随机整合等优点，但目前缺乏高效的非病毒基因载体。本课题以低毒性的小分子聚乙烯亚胺PEI 1.8 KDa作为基本构建单元，芳香烃二甲醛作为连接剂，构建了一系列新型聚乙烯亚胺衍生物。在前期合成的载体材已证实有效的基础上，拟开展输送IL-1Ra治疗RA的研究。拟通过滑膜细胞转染及毒性研究、RA小鼠生长状况、足爪肿胀度、关节炎评分、血清IL-1Ra因子检测、H&E病理形态学等研究。来源于浦东新区基金项目经费保障：横向和纵向课题经费','2018-07-01 00:00','2018-08-31 23:59','药学院','','同去网','2018-07-01 00:00:00.0');
