insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(340628249,'免费公益培训-计算机操作员国家职业资格考证','计算机操作员国家职业资格证书免费公益培训协议一. 计算机操作员考证是什么？计算机操作员指使用计算机从事文字、图形、图像等信息处理工作及计算机系统操作、维护与管理的人员，广义上也包括日常需要熟练使用Office办公软件的相关人员。计算机操作员国家职业资格证书共设初级、中级、高级三个等级，通过相关考试即可获得由上海市人力资源与社会保障部颁发的国家职业资格等级证书，此证书终身有效，对今后学习和工作都大有裨益。二. 通过培训课程可收获什么？1. 计算机相关操作（Word/Excel/PPT）的高级技能以PPT课程为例：你的PPT还停留在这个阶段吗？而别人的PPT是这样的通过培训，你也能从PPT小白进阶成PPT达人，从而制作出简洁大气有质感的PPT！2. 上海市人力资源与社会保障部颁发的国家职业资格等级证书计算机管理员国家职业资格证书具有权威性和广泛适用性，是计算机从业者以及日常办公需熟练使用office软件的人员应具备的基本技能。三. 培训内容此次培训课程主要分为以下内容：电子文档（Word）处理技巧电子报表（Excel）处理技巧演示文稿（PPT）制作技巧PPT基本应用PPT高级设计计算机操作项目设计四. 报名事项参加对象：面向交大在校学生，不限专业不限年级（含大一、大二、大三、大四、研究生）报名时间：2018年6月6号—6月17号费用问题：此次培训课程免费。报名需缴纳300元押金(以保证培训项目顺利进行)，参加考试当天，无论是否通过皆如数退还押金。                 联系老师：如有问题，请联系赵老师18202122329五. 相关问题','2018-06-22 15:27','2018-07-01 15:28','上海交通大学闵行校区','https://tongqu.me/upload/acts/20180606/small_c55ecd139cf8e1297539112c19b855bb.jpg','同去网','2018-06-22 15:27:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1743128788,'“生科讲坛”第九期——医学人生','报告人简介王莲芸，上海交通大学生命科学技术学院教授，仁济医院过敏科主任医师。现任中华预防医学会过敏性疾病防治委员会顾问，曾任中华医学会变态反应分会常务委员，中华医学会上海分会变态反应专科委员会委员。主要研究过敏性疾病的发病机理和防治措施，发表科研论文40余篇（其中被SCI收录30余篇）；主编教材《现代医学导论》和《大学生健康导论》，副主编专著《中西医临床耳鼻咽喉科学》和《鼻炎与哮喘中西医最新诊疗学》，参编《前沿生命的启迪》、《临床变态反应学》和《专家解答过敏性疾病》。曾获我校“最受学生欢迎老师奖”、“师德标兵”和“教书育人”提名奖、“上海市育才奖”、“优秀教师宝钢奖”等荣誉。','2018-06-09 13:30','2018-06-09 15:00','生命药学楼3-105','https://tongqu.me/upload/acts/20180606/small_4a94984f186a581cf4835476dbc654e2.jpg','同去网','2018-06-09 13:30:00.0');
