insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(190159936,'古装荒诞喜剧《破阵子》','【票 价】学生票 80元+5元票务服务费    普通票 180元【咨询电话】4008781318【购票规则】于当天开场前1小时内在售票处兑换实体票入场。演出票一经售出，不能退款。购票入口https://clubz.cloudsation.com/event/2285.html演出详情由董天翼编剧、刘丹导演的至乐汇荒诞喜剧代表作《破阵子》自升级复排以来，成为娱乐快餐文化中的一道清流，被誉为最具现代性的古装题材戏剧堪称近年来戏剧界最具诚意之作。《破阵子》的幽默背后蕴藏了更深层次的底色与思辨，“抑扬顿挫、不卑不亢、辛辣有度”，更有着探讨归属感的“家国大爱”，是商业市场话剧艺术难得的美学突围，被视为现代元素完美汇入古典主义的惊艳之作。与此同时，它独特的黑色幽默特质，也将带给观众极致而另类的喜剧体验——前一刻笑到哭，后一刻便可能“哭笑不得”。该剧首演至今三年来收获业内外好评如潮，今年5月新版在北京上演，周冬雨、papi酱等名人也出现在观演人群中。有观众评价该剧为“纠结致死的分不清喜悲的荒诞剧”，《破阵子》自有这“悲喜交织”的戏剧魅力，最特别之处便在于故事“鉴古于今”之笔——背景虽架设于北宋末年宋辽边境的白虎荡，却巧妙展现亦古亦今、亦喜亦悲的故事人生，也以此让观众体验这高级的、不一般的喜剧幽默。《破阵子》讲述了北宋末年，宋辽边境有一处名唤“白虎荡”的穷山恶水的人迹罕至之地，寡居着一群失意的人们。他们是无法操刀的屠户、欲求不满的人妇、考不上功名的书生，出嫁便守寡的媳妇，利用精神胜利法为自己封侯的老地主。辽宋纷争家国天下的宏大叙事背景下，以宋辽边境人们二元对立的“抢妻”为叙述线索，展开了一系列爱恨情仇的矛盾纠葛。剧中故事推进过程中，处处展现了古今交织的幽默，“移民”“签证”等当下炙手可热的话题，在北宋年间，穿越现身，荒诞而不维和、幽默又引人联想，也总能赢得观众的笑声。','2018-06-30 19:30','2018-07-01 16:00','862时尚艺术中心(滨江大道1777号)','https://tongqu.me/upload/acts/20180601/small_c15968cf9fcaa981a6de05e65a8dd0bd.jpg','同去网','2018-06-30 19:30:00.0');
