insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(83726287,'【满分素拓】电院学业交流分享会','电院学业交流分享会学在交大17级电院的同学们～考试周就要来了，你开始慌了么？想为你的考试周制定一份完美的学习计划？想知道高数大物概统C++如何科学备考？ 听说在电院，学业交流分享会和考试周更配哦～本学期学业交流分享会第二弹听优秀学长学姐分享备考经验，传授高数大物概统C++提分秘诀！还有惊喜期末学习资料及1161项满分素拓一次哦！ 你还在等什么？我们周三不见不散！ #本次分享会只面向电院17级本科生#活动内容本次活动旨在落实“学在交大”的理念，构建一个电院同学与国奖同学交流的平台，解答同学们在学习中遇到的各种问题。通过国奖同学的分享，帮助同学规避学习中误区，找到各学科最有效的学习方法和考试技巧，帮助同学们共同进步。本期嘉宾郭昕16级工试的一枚老学姐，现在在仪器系。曾获校级优秀团员、三好学生，全国大学生数学建模竞赛市二等奖，美国大学生数学建模竞赛H奖，电院新生杯决赛二等奖，有声衡器奖学金，校级B等奖学金等荣誉，c++满绩。程钰茜2016级微电子科学与工程专业2017年金龙鱼奖学金2017年校级B等奖学金2018年美国大学生数学建模竞赛M奖时间地点时间：第十四周周三（5月30日）14:00 地点：电群3-100 【请参加分享会的同学提前15分钟进场】','2018-05-30 14:00','2018-05-30 15:30','电群3-100','https://tongqu.me/upload/acts/20180528/small_3ab845b7f13f84e216baca4dffe162b9.jpg','同去网','2018-05-30 14:00:00.0');
