insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1068316421,'6月10日周日相约宁波的香格里拉，徜徉在最美风车公路','活动须知介绍：菩提岭古道，又名风车古道，坐落于素有宁波的“香格里拉”之称的童夏家村，岭道曲折蜿蜒，山林浓密悠长。迎着缕缕清风向上攀登，不时会偶遇郁郁葱葱的古木和色彩斑斓的鲜花。阳光透过枝叶星星点点洒落下来，眼前悠长的古道仿佛幻化成了童话里的隧道，满足游客探幽探险的好奇心 梦幻般的日出、云海、日落……迎着山风，徐徐转动的风车……都可以在这里看到 ！这里的美，让人心醉！活动详情','2018-06-10 07:00','2018-06-10 19:42','上海集合出发','https://tongqu.me/upload/acts/20180526/small_53cff04ef0eaf5c25c3398000099cc71.jpg','同去网','2018-06-10 07:00:00.0');
