insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2143764717,'【青春如化】化学化工学院暑期就业实践团报名','为了加深交大学生对化学化工类国家骨干企业的了解，做好职业规划；同时鼓励学生投身于国家重点行业建功立业，实现自身价值，最大限度发挥在母校所积累的能量，我院将于今年暑期组织学生（2019届毕业生优先）到新疆、宁夏、福建、内蒙古、四川、陕西等就业实习实践基地，深度走访国工程物理研究院、神华宁东能源基地，并和这些企业的人事部门及校友面对面交流。希望同学们能够抓住机会，积极报名参加！项目的开展形式主要为：座谈会、企业参观、专题讲座、部门职能分享会、同学与企业研究方向对接讨论会、生活区参观、初步面试等。每条支线团员独立招募，活动时间为暑假小学期中的3-5天（部分线路会根据实际情况调整），最终路线会根据报名情况适当调整，同学们可根据个人意愿选择参加。活动内容和路线均会根据情况调整，请同学届时根据情况进行选择，目前为预报名阶段。注意事项：以上路线均不对大一同学开放，请同学报名时注意。路线一：福建-宁德时代新能源路线参访宁德时代新能源科技公司（http://www.catlbattery.com/）宁德时代新能源科技股份有限公司（CATL）成立于2011年，公司总部位于福建宁德，在德国慕尼黑、北京、上海、江苏、青海分别设有分支机构。通过多年运营，公司已建成动力电池和储能系统研发制造基地，拥有材料、电芯、电池系统、电池回收的全产业链核心技术，致力于通过先进的电池技术为全球绿色能源应用、能源存储提供解决方案。2017年，动力锂电池全球出货量第一。活动时间：时间待定（暑期小学期）活动内容：根据宁德时代新能源公司的安排进行。报名要求：电化学及相关领域的本科生、研究生、博士生。研究生及博士生优先，2019届毕业生优先。服从团队管理和参访企业要求，按要求安全出行及参访。统一前往、统一返回（除暑期实习之外）备注：参访路线和企业可能会根据情况额外增加或调整信息务必填写准备，报名成功不代表参团成功，学院会组织企业进行简单的筛选座谈。参团成功以电话或短信通知为准。路线二：宁夏—神华宁东能源基地参访及暑期支教活动','2018-07-01 00:00','2018-07-29 23:59','上海、宁夏、新疆、四川、内蒙、福建等','https://tongqu.me/upload/acts/20180524/small_a17616e1c632b040cf03930086156da3.png','同去网','2018-07-01 00:00:00.0');
