insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(227177752,'青锐创投投资分析师实习生招募','招聘职位：投资分析师实习生岗位职责：1、协助投资经理进行项目收集；2、协助投资经理进行行业研究；3、协助投资经理进行投后管理；4、参与公司集体项目讨论。希望你是：1、大四（保研）、研一、研二，一周能实习4天以上；2、对创新创业和风险投资感兴趣，对商业模式有基本的认知；3、工作细致认真，责任心强；4、985在校生优先，立即到岗优先，上海在校学生优先；我们能给你：1、朝十晚六错峰上班，追求高效从不加班；2、零食管够，氛围管好；3、锻炼行业研究和公司分析的能力；3、良好的工作习惯和思考习惯，深入体验关于风险投资的基本流程；4、待遇：RMB 150/Day工作地点：上海市黄浦区淮海中路283号（1号线黄陂南路）入职时间：尽快入职，越久越好简历投递：发送投资分析师-学校-姓名-专业-年级-电话到 hr@edgevc.com.cn官方网站：http://www.edgevc.com.cn公司介绍：青锐创投成立于2015年1月，是从事早期风险投资的专业机构之一。公司总部位于上海，当前管理着两支人民币，一支美元共三支早期创投基金，总共折合人民币6亿，专注于互联网、大消费和高科技领域内优秀创业企业的早期投资。青锐创投的团队在相关领域的创业、投资和融资方面拥有丰富经验，对国内市场有深刻的了解，能够在所投资公司迅速成长的过程中提供必要的帮助。团队平均年龄27岁，管理扁平化，工作环境自由轻松。','2018-07-01 16:28','2018-08-25 16:28','上海黄浦区','https://tongqu.me/upload/acts/20180524/small_f9d65b4150d985049393a353d27db554.jpg','同去网','2018-07-01 16:28:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-107058046,'上海交通大学通识教育“名家讲堂”第73讲：哲学中的沉思传统与实践转向','上海交通大学通识教育“名家讲堂”第73讲哲学中的沉思传统与实践转向主讲嘉宾：郁振华主办单位：上海交通大学文科与通识教育委员会  教务处人文学院讲座时间：2018年5月30日（周三） 15:00—17:00讲座地点：闵行校区学憩苑（东下院108）主讲嘉宾简介：郁振华，挪威卑尔根大学博士，华东师范大学哲学系教授，教育部“长江学者”特聘教授。曾担任华东师范大学哲学系主任、美国波兰尼学会理事（2006-2015）。第七届国务院学科评议组成员、国际形而上学学会（ISM）秘书长、中国知识论学会副会长、上海中西哲学与文化比较研究会副会长、中法科学与社会联合研究院知识与行动研究室(KAL)中方主任。主要学术兴趣：认识论、形上学、比较哲学。代表作：《形上智慧如何可能？》，《人类知识的默会维度》。报告内容简介：知识和行动或理论和实践的关系问题，是一个古老而常新的哲学难题。在西方哲学史上，存在着一个根深蒂固的理论至上的沉思传统，即理论之于实践的优先性。在当代哲学与科学（特别是认知科学）发展的背景下，知行哲学又孕育出了新的可能性：理论之于实践的经典优先性，被翻转为实践之于理论的优先性，这一重大转折可用“实践转向”来加以刻画。本讲座将结合亚里士多德、海德格尔、杜威、哈贝马斯等哲学家的思想来分析西方哲学中的这个重要转向。','2018-05-30 15:00','2018-05-30 17:00','：闵行校区学憩苑（东下院108）','https://tongqu.me/upload/acts/20180525/small_36c59c4a7a044cf733b813732d6f5226.png','同去网','2018-05-30 15:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1222152987,'上海交通大学通识教育“名家讲堂”第74讲:经典的旅行—以索福克勒斯的《俄狄浦斯王》为例','上海交通大学通识教育“名家讲堂”第74讲经典的旅行—以索福克勒斯的《俄狄浦斯王》为例主讲嘉宾：耿幼壮 教授主办单位：上海交通大学文科与通识教育委员会  教务处人文艺术研究院讲座时间：2018年5月31日（周四）14：00—16：00讲座地点：闵行校区东中院1-108主讲嘉宾简介：耿幼壮，中国人民大学文学院教授，《世界汉学》主编、《外国文学研究》（人大复印报刊资料）主编。主要研究领域为西方文论与比较文学、艺术史与艺术哲学、跨学科与文化研究。主要学术著作有：《敞开的视界——跨文化与跨学科视野下的文学研究》（北京大学出版社，2016）、Between East and West/Word and Image（Baylor University Press, 2015）、《倾听：后形而上学时代的感知范式》（北京大学出版社，2013）、《圣痕：基督教与西方艺术》（台湾中原大学出版社，2009）等。报告内容简介：追溯2500年前索福克勒斯悲剧《俄狄浦斯王》的产生背景、历史命运及其批评、阐释和接受过程，梳理亚里士多德、伏尔泰、荷尔德林、黑格尔、尼采、弗洛伊德、拉康、海德格尔、德里达等思想家的相关论述，阐析古典主义、浪漫主义、心理分析、现象学、结构主义等不同文学批评流派对其所运用的批评方法,揭示这一经典作品在西方文学和文化发展中的深远影响和复杂意义。','2018-05-31 14:00','2018-05-31 16:00','闵行校区东中院1-108','https://tongqu.me/upload/acts/20180524/small_1518ba1d8cbd07176e200fe623b997d2.png','同去网','2018-05-31 14:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(141677659,'上海交通大学波兰科学年交流活动','波兰科学的『心脏』2018年对于波兰人而言是一个特殊的年份，不仅仅是是波兰重获独立100周年，而且2018年也被宣布为波兰科学之年。波兰大使馆文化处将与 中国顶级大学-上海交通大学的师生们，携手庆祝波兰科学之年。多年以来，波兰和波兰人继承哥白尼、居里夫人或扬•柴可拉斯基等波兰科学家的工作，对世界科学发展做出了巨大的贡献。来自全球各地波兰的科学家，正在为世界科学无数的领域的发展付出努力。为祭奠波兰最近几年的科学家，以及他们为科学发展做出的贡献，波兰大使馆文化处、上海交通大学学生创新中心与晶核教育联合启动「波兰科学的『心脏』」项目。此项目的灵感来自于『VR心脏』应用程序，该程序由波兰The Farm 51工作室、波兰心脏外科发展基金会和奥波莱大学医学院联合研发，并于今年2月份交给奥波莱大学医学院。在医学中使用VR和AR技术可以说是当代波兰的良好典范——当代波兰是一个具有现代化和有创意性的国家，也是科学传统的延续者。为庆祝波兰科学之年，我们将通过『科学是自由』多媒体展览，和师生们一起了解波兰近年来的科学成果。此外，我们还会了解到波兰心脏外科的历史——『VR心脏』，这可以作为其历史的最新篇章。在波兰心脏外科发展中发挥了最关键的作用是兹比格涅夫•莱利加[Zbigniew Religa]教授，也是波兰扎布热市心脏外科发展基金会的创造人。半个世纪以前，兹比格涅夫•莱利加教授首次进行心脏移植手术。他的故事，将由获得许多国内外奖项的电影『神』向大家娓娓道来。','2018-05-31 13:30','2018-05-31 16:30','上海交通大学学生创新中心思源楼200','https://tongqu.me/upload/acts/20180528/small_75434499c82b2a9b7b207f5aa298f558.jpg','同去网','2018-05-31 13:30:00.0');
