insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(17207479,'专业社会实践活动—彭博资讯参观','“专业实践类课程模块”是安泰经济与管理学院本科生专业实践类必修课程，修业时间为第2-4学期。该课程旨在结合各个专业（经济、金融、工商管理、会计）及其专业方向/模块的知识内容和实际操作过程，通过现场参观、现场观看企业录像，现场参观和组织企业嘉宾对话结合、请企业人员来学校进行有关专业职能方面讲解等实地学习方式，对所在专业及其专业方向相关的部分理论应用、实际操作过程、业务流程、解决专业问题的工具手段等进行感性认知，促使学生初步认识企业工作环境、直观了解所学专业职能、初步接触一线岗位的基本工作内容，为后期培养专业学习兴趣、加深对专业知识和工具的实际应用打下基础，也为学生明确自身未来的职业规划和职业发展提供一定的参考。为提高本课程学生的获得感，授课教师将仔细思考对象企业的参观内容、对参观过程与学生知识的匹配性进行分析、获得对象企业实践中专业知识的相关材料、企业参观后学生参观心得评价表的设计等等。专业社会实践活动—彭博资讯参观时间：2018年6月13日（周三），下午11:50-16:30出发地点：闵行校区菁菁堂广场11:50集合，乘坐大巴集体前往上海市浦东新区世纪大道8号上海国金中心汇丰银行大楼12层参观名额：40人（仅限安泰2016级本科生）','2018-06-13 11:50','2018-06-13 16:30','上海国金中心汇丰银行大楼','https://tongqu.me/upload/acts/20180523/small_c154032299eb5ec459d369f0c318447d.jpg','同去网','2018-06-13 11:50:00.0');
