insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-870291753,'本科学术系列讲座（十六）','本科学术系列讲座是我院本科生的必修课程“专业学术报告”的组成部分之一，它是将经济管理领域的理论知识和工具与社会实践特别是经济管理领域的热点问题相结合的学术导向课程。通过本课程的学习，促使学生熟悉经济管理领域的相关问题或关注要点，了解问题和热点背后的知识基础和作用机制，提升学生观察问题、思考问题、提出应对问题的基本学术素养，为未来从事科研和实际经济管理领域相关的工作打下扎实的基础。讲座嘉宾将结合当前的经济管理热点或学术前沿，邀请学院教师安排系列讲座，每次2小时；讲座将分经济和管理两个专业组织的专场学术讲座。学生参加不少于16次报告或讲座，并每次撰写报告听后感，每篇听后感不低于1000字。邀请嘉宾：姚佶博士讲座题目：机器学习助力保险业智慧化变革讲座时间：2018年5月30日（周三），下午13:30-15:30（含提问时间）讲座地点：闵行校区，东中院2-105备注：2016级、2017级安泰经管学院本科生可作为必修课程《专业学术报告》1次。嘉宾简介：姚佶博士，上海交大安泰中英精算项目学术主任毕业于上海交通大学电子信息学院和英国伯明翰大学。英国精算师，特许企业风险分析师，主要从事非寿险精算工作，特别在精算定价及相关统计模型方面积累了十多年的经验。姚佶博士曾在英国肯特大学兼职教精算课程，是英国精算协会非寿险教育和职业再教育委员会成员，参与了英国精算考试大纲和教材的审阅和修订工作。他经常在英美精算年会上发表研究报告和演讲，最近还联合编著了预测模型系列丛书。','2018-05-30 13:30','2018-05-30 15:30','闵行校区，东中院2-105','https://tongqu.me/upload/acts/20180523/small_f795ad5630696673643e0357ae33353d.jpg','同去网','2018-05-30 13:30:00.0');
