insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(58676308,'第二期仪表人材——材料科学与工程的基础实验室','材料科学一直都是科学研究领域的先驱者，材料学院也一直致力于培养优秀的科技创新型人才。想了解材料学院的大牛们每天都在研究些什么吗？想亲身体验参与材料学院各个实验室的功能，并实际操作吗？想近距离接触SEM、XRD、焊接机器人、EFM等高端实验仪器，学以致用吗？为培养本科生的科研兴趣，增强科研素养，鼓励本科生积极参与相关科研活动，材料科学与工程学院学生科技协会联合学院各课题组和实验室拟开展“仪表人材——材料科学与工程的基础实验室”活动，组织本科生进实验室参观走访活动。本次活动立足于创新的思维引领发展，将分为两个小组，采用轮换制分别走访材料学院五大科研平台的重点实验室，由院士、教授等领衔的讲解队伍引领大家深入了解各个领域及研究内容。本次活动实验平台简介：一、特种材料研究所由教育部“长江学者”王浩伟教授任中心主任，中科院院士周尧和教授担任学术顾问。研究所主要开展新型特种材料的制备和成形科学技术研究。近年在铝、镁基复合材料的界面与性能、成形过程、腐蚀与防护、疲劳与断裂，铝再生技术、重金属离子吸附技术等领域进行深入研究并取得突破性进展。多年来承担国家重点项目、国家自然科学基金、国际合作项目等十多项，获得教育部技术发明一等奖一项。在国内外学术刊物上发表学术研究论文100余篇，获授权中国国家发明专利43项。特种材料研究所目前拥有复合材料调压铸造设备、先进复合材料原位合成设备、复合材料挤压铸造设备、复合材料压力浸渗设备、复合材料挤压设备、复合材料连铸设备、静态力学试验机、动态力学试验机、超高压水射流切割机床、光电直读光谱分析仪、高频阻尼分析仪、图像分析仪、电化学工作站等大型设备和材料分析检测手段。二、轻合金研究所上海交通大学轻合金精密成型国家工程研究中心(Light Alloy Net Forming National Engineering Research Center-LAF-NERC)成立于2000年3月，是由国家发改委批准组建、上海交通大学承建的国家级工程研究中心，现任中心主任为丁文江教授。经 过十年多的发展，中心目前已经成为我国重要的镁合金材料研发基地，同时也是世界范围内最大最先进的镁合金研究团队之一。中心主要研究领域包括高性能镁合金 材料、镁合金液态成型技术、镁合金固态成型技术、镁合金净化技术、镁合金表面处理技术、镁基生物材料、镁基能源材料等七个主要研究领域','2018-05-24 12:00','2018-05-25 14:00','材料科学与工程','https://tongqu.me/upload/acts/20180522/small_fb735f392eda4feb767021b1ee5d85ed.jpg','同去网','2018-05-24 12:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1674718862,'【大师讲坛第104期】21st Century Medicine Will Transform Healthcare','报告主题：21st Century Medicine Will Transform Healthcare报告嘉宾：Leroy HoodMember of the National Academy of Sciences美国科学院院士Member of the National Academy of engineering美国工程院院士Member of American Academy of Arts and Sciences美国艺术与科学院院士报告时间：2018年6月4日（周一）  14:00-16:00报告地点：系统生物医学研究院一楼报告厅嘉宾介绍：Leroy Hood，美国国家科学院院士、美国国家工程院院士、美国艺术与科学院院士，美国医学科学院院士，美国系统生物学研究所所长及创始人。他于1964年获得约翰霍普金斯大学医学院医学博士学位，1968年在加州理工学院获得生物化学博士学位，之后在国家卫生研究院担任了3年高级研究员。在加州理工学院，期间，他和他的同事开发了DNA基因测序仪、DNA合成器、蛋白质合成器和蛋白质测序仪，该技术的发明成为生物技术领域重要的里程碑,为人类认识基因组铺平了道路。 Leroy Hood教授曾在15家生物技术公司成立过程中发挥至关重要的作用，包括Amgen公司、Applied Biosystems公司、 Integrated Diagnostics and Arivale公司等。Leroy Hood教授获得17个国际知名大学荣誉学位，已发表了750多篇学术论文，并获得36项专利。他还主编或合著了众多教科书，涵盖生物化学、免疫学、分子生物学和遗传学等领域。他编写了广受欢迎的关于人类基因组计划的著作《The Code of Codes》，并刚完成了《系统生物学》的编著。Leroy Hood教授曾获得多项国际大奖，例如，1987年因其在免疫多样性领域研究成就获拉斯克奖，2006年因在系统生物医学领域前瞻性工作获亨氏奖，2011年因发明了DNA自动测序仪获美国国家工程院 Fritz J. & Delores H. Russ奖。2013年,他获得奥巴马总统授予的国家科学奖章，并在2014年他被评为当今世界最有影响力的50位科学家之一。邂逅大师：','2018-06-04 14:00','2018-06-04 16:00','系统生物医学研究院一楼报告厅','https://tongqu.me/upload/acts/20180522/small_4842f59583a2034675422a788d3a0602.jpg','同去网','2018-06-04 14:00:00.0');
