insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2029477621,'Robotic AI Research Program','上海交通大学“上海市北斗导航与位置服务重点实验室”Robotic AI Research Program研究背景机器学习，深度学习等人工智能(AI)技术是我们这个时代发展最重要的技术引擎，图像和视频的自动化解译问题是AI的试金石。能够简单理解图像视频的AI已日趋成熟，但局限于对图像本身信息的挖掘，拥有视觉的机器人就算搭载目前最前沿的AI技术也无法对图像背后蕴藏的位置，场景，环境进行全面的理解推导。在这个AI领域最顶尖挑战的号召下，我们的研究目标设定为构建可通过图像和视频自主理解，推导，重建机器人所在三维场景信息的强人工智能。研究内容本实验室在多年导航、计算机视觉、目标检测识别的科研基础上，建立一个场景视频数据库，以深度挖掘目标信息等的潜能，探索新的算法和达到更好的效果。项目优势本项目旨在建立起场景自然图像视频数据库，并在建立数据库的过程中产生有益的创新性想法。项目紧密结合自然场景理解、机器学习等前沿科学，科学系统地收集整理相关数据，建立起具有学术影响力的数据库，推动相关算法的进一步发展和完善。学生将在项目中培养起研究精神并提升水平能力，积累相关经验，例如计算机视觉、机器学习等实验室氛围良好 ，可提供指导帮助。学生要求上海交通大学全日制本科生，不限学院和专业。认真努力，具有学术热情，拥有较高责任心和耐心；熟悉编程的同学优先。实验室简介上海交通大学“北斗导航与位置服务”上海市重点实验室是部室共建重点实验室，坐落于上海交通大学闵行校区微电子楼3楼。实验室现有设备包括深度学习服务器集群（包含12块Nvidia TitanXP GPU等）、深度学习服务器等在内的计算资源。实验室致力于解决北斗导航与位置服务中的基础理论与核心技术问题，探索导航定位新概念、新方法和新技术，促进我国导航定位于位置服务技术的研究、发展与应用，推动导航与位置服务新兴产业的发展。实验室现包括：新概念定位导航与信息体制、融合导航技术、位置信息处理与空间行为分析等几个主要研究方向。实验室先后培养博士、硕士生140余人，承担了各类国家和省部级科研项目110余项，发表包含Transaction等顶级期刊会议在内的200余篇，授权专利近百项，研制出样机和原型系统多台（套），培养了一批优秀人才。报名联系方式liboying.fhyt@sjtu.edu.cn 或 291412331@qq.com','2018-08-01 15:12','2018-10-01 15:12','上海市北斗导航与位置服务重点实验室','https://tongqu.me/upload/acts/20180522/small_cc82357015793eaea85aff759ce3574b.jpg','同去网','2018-08-01 15:12:00.0');
