insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1715927736,'古咏——西方古典音乐赏析','国际大赛获奖者，男中音歌唱家Ilya Vinnikov和钢琴家Victoria Eliseeva 将为您呈现一场文艺复兴后期至巴洛克时期的音乐盛宴。届时，您将欣赏到由意大利、德国和英国音乐巨匠道兰德、蒙特威尔第、普塞尔、巴赫、亨德尔和史卡拉第等作曲家创作的嘉雅舞曲、意大利牧歌及歌剧等众多形式的音乐曲目。地点：上海交通大学闵行校区 光彪楼 一楼音乐厅时间：2018年5月24日（星期四）19:00免票入场International competition laureates, OperON Duo, baritone Ilya Vinnikov and pianist Victoria Eliseeva present a program comprising jewels from the late Renaissance and Baroque music. The concert will take place at Guangbiao hall (1st floor) of the Minhang campus of the Shanghai Jiao Tong University on Thursday, the 24th of May at 19:00. You will get acquainted with galliards, madrigals, operas of composers from Italy, Germany and England: Dowland, Monteverdi, Purcell, Bach, Handel, Scarlatti and others. The entrance is free.音乐会免费入场。为保证音乐会秩序，请提前10分钟进场入座。请勿携带饮料、零食进入，音乐会过程中请关闭手机铃声。如有可能，尽量不要中途离场，谢谢您的配合和对表演者的尊重！','2018-05-24 19:00','2018-05-24 20:30','闵行校区光彪楼一楼多功能厅','https://tongqu.me/upload/acts/20180521/small_fb2470afc99ae4bf91efb396876c40f1.jpg','同去网','2018-05-24 19:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(493650674,'心理健康月|心理沙盘——“我们的世界”同学关系工作坊','一盘细沙，一瓶清水，一架子各式各样的物件造型，治疗师的关注与投入，来访者的自由表现与创造，这就构成了沙盘游戏的最基本的要素！在这简易的设置中，内心的世界得以呈现，心灵的充实与发展，治愈与转化也获得了可能。这就是同学最期待的心理沙盘工作坊！本次心理健康月，我们为同学带来“我们的世界“同学关系工作坊。想要了解自己的人际交往模式，想要改善与同学的相处之道，快报名工作坊吧！名额有限，先到先得！具体信息如下：【时间】2018年5月22日14:00-15:00（第一场），15:30-16:30（第二场）【地点】电信群楼1-201A“倾青有约”心理健康工作室（电群东侧走廊，靠近电群草坪1号楼2楼）【人数】每场6人（每位同学只能选择一个场次）【带领人】邹雷，讲师，国家二级心理咨询师。曾侨居国外多年，03年回国并从事学生工作。擅长领域为家庭问题、情感与性、人际交往和个人发展问题。【报名方式】直接在同去上报名，报名成功后将收到短信、邮件通知。由于名额有限，报名成功又无法参加的同学，请在5月22日10点前联系宋老师（34205080，songyq@sjtu.edu.cn），我们将把名额顺延给后面报名的同学。谢谢！','2018-05-22 14:00','2018-05-22 16:30','电信群楼1-201A','https://tongqu.me/upload/acts/20180521/small_4d2c25ce446a0eeaa67596e75c678e66.jpg','同去网','2018-05-22 14:00:00.0');
