insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1855070220,'活动｜热血一夏，首届 AI 极客训练营开始报名啦！','通过下面的报名方式报名，同去网报名无效。为什么要办这个训练营人工智能正在越来越深刻地改变着人们的生活，也同样在变革着整个人类社会的运行方式。越来越多有梦想的年轻朋友希望更加深入地理解 AI，学习相关的知识，成为这场科技革命的参与者，站在潮头。然而，对于很多学生，尤其是低年级的同学而言，却苦于缺少真正接触和解决实际问题的机会，更缺少行业大牛的指导。基于上述种种，由图森未来、Amazon Web Services（AWS） 以及将门创投联合举办的首届 AI 极客训练营（AI Geekthon Camp）将于6月3日正式开营，旨在让广大年轻朋友充分掌握 AI 技术，通过对现实问题的洞察与思考，将对未来的美好想象变为现实！训练营介绍本届训练营将于6月3日正式启动，8月11日进行最终答辩。期间将在北京和上海组织开营、中期考核及结营答辩，入围的营员还将有机会参加 AWS Summit，并与导师进行面对面的交流与培训。主办方将报销营员往返的交通（高铁二等座，机票国内航班经济舱）并统一安排住宿。时间安排报名：5月17日-5月27日审核：5月28日-6月1日开营：6月3日中期考核：6月29日结营：8月11日活动形式本届训练营将设有两个平行项目>>>>项目一●     主题：兴趣算法大赛●     形式：○     深度学习或计算机视觉或自动驾驶领域相关项目，具体选题不限；○     鼓励有创新性和趣味性的选题，不追求技术难度；○     最多2人组队；○     最终将甄选10支队伍入围训练营。为了帮助大家发散思维，这里我们提供几个例子供参考：○     书法生成器，可以根据你写的几个字 生成你特定字体的书写https://github.com/kaonashi-tyc/zi2zi○     用 Python 做一套自动防御水枪系统，抵御后院时常出现的松鼠http://bbs.crossincode.com/forum.php?mod=viewthread&tid=717>>>>项目二●     主题：前沿算法大赛●     形式：○     以 MxNet/Gluon 为平台，复现最新公开的学术界论文，探究前沿技术，并将代码加入开源项目，例如 GluonCV 和 GluonNLP；○     参与者从以下 CV 和 NLP 论文中选择一个方向。入选之后，导师将会与你沟通具体进行复现的论文。●     CV','2018-06-03 00:00','2018-08-11 23:59','上海、北京','https://tongqu.me/upload/acts/20180519/small_cc33e4c91ad79057c79c9c398d1c62ee.jpeg','同去网','2018-06-03 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1997171593,'OceanBase - 云时代的分布式关系数据库主题讲座','题目：OceanBase - 云时代的分布式关系数据库时间 : 2018年5月21日(周一)16:00-18:00地点 : 电信群楼3-100主题介绍：过去的几十年间，关系数据库系统从理论到实践都获得了迅速的发展，RDBMS成为金融、通信、政府等各种组织的数据处理核心，成为了当今社会的信息基础设施。然而，随着网络和移动互联网时代的发展，传统的数据库产品自身的高可用能力的缺失，高昂的成本以及缺乏弹性伸缩能力，越来越成为制约相关行业发展的瓶颈。本次报告将分享蚂蚁金服自己研发的OceanBase分布式数据库的创新尝试。在通用服务器的云环境中，OceanBase基于分布式的系统架构，服务了蚂蚁金服包括交易、支付、账务和会员等在内的全部核心系统，取代了原有的Oracle数据库，支撑了蚂蚁金服从2014年开始的多次双11大促，在2017年双11中创造了每秒25.6万笔支付以及每秒处理4200万条SQL的世界纪录。同时，OceanBase 也已经在商业银行中得到使用。报告嘉宾：韩富晟韩富晟，2008年毕业于上海交通大学计算机系ACM班，2010年加入蚂蚁金服从事OceanBase数据库系统研发，目前是OceanBase团队资深技术专家、事务引擎和内存存储引擎的技术负责人。韩富晟参与设计研发了OceanBase各版本的事务引擎，包括事务并发控制、二阶段提交、Paxos日志同步、高可用架构等，这些功能在多次双11大促中发挥了重要作用也支撑了金融业务的高可用能力。韩富晟还负责OceanBase数据库的性能调优工作，将OceanBase性能提升到业界领先水平。说明：电院研究生记讲座素拓一次，电院本科生记1161项95分素拓。','2018-05-21 16:00','2018-05-21 18:00','电信群楼3-100','https://tongqu.me/upload/acts/20180519/small_fe706af1d1ca47dcb549c78c47bd395a.jpg','同去网','2018-05-21 16:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1770552759,'公益未来社会实践校内挑战赛','2018年 “公益未来——希尔顿社会实践”校内挑战赛公益未来·希尔顿社会实践项目是由希尔顿集团和中国扶贫基金会联合发起的大学生社会实践项目，一直以来致力于倡导大学生通过参与公益的方式解决社会问题，在社会实践过程中锻炼自己的团队创造力（Creativity）、团队协作能力（Teamwork）以及社会责任感（Responsibility)。项目共计支持17城市48所高校60个社团开展实践活动，直接/间接影响20万社会公众。一．项目主题、命题1. 项目主题可持续的未来——如何营造一个人人享有平等工作机会、舒适居住环境、生产消费绿色化的未来。2018 Challenge: A sustainable future---How to create a world that everyone could enjoy equal opportunities, decent living environment and a greener consumption/production pattern.2. 项目命题针对体面工作和经济增长、可持续城市和社区、负责任的消费和生产这三个SDG中的其中一个目标，提出达到该目标的解决办法，对设计的方案进行调研，并通过实践探讨方案的可实施性、评估方案效果。二．项目流程（1）校内赛：进行校内选拔，评选3支团队（2）社会实践：公益项目落地实践+导师指导（3）全国赛初赛：线上接受评委考核，评选出20强进入全国总决赛（4）全国赛总决赛：全国总决赛暨落地成果汇报会三．项目支持与奖励1. 支持校内赛获胜团队（3支/校）获得3000元实践经费获得关于项目操作的培训以及沟通能力培训、演讲能力培训和创新思维设计培训2. 奖励全国冠军团队可以获得6000元奖励经费+香港公益交流机会+实习机会+荣誉证书+奖杯全国亚军可获得5000元/全国季军团队可获得4000元/第4-10强团队可获得2000元奖励经费+实习机会+荣誉证书+奖牌或奖杯四．报名时间及方式团队规模：2-3人截止时间：5月27日24：00报名方式：5月25日东转现场报名、加入咨询群后统一报名感兴趣的同学快到碗里来！！！扫描海报二维码加群也可以直接加群  QQ群：783603461','2018-05-28 21:42','2018-06-08 21:42','上海交大闵行校区','https://tongqu.me/upload/acts/20180518/small_d29ea7de9a0d5e9da1ccca788c06d405.jpg','同去网','2018-05-28 21:42:00.0');
