insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1846331489,'转基因科普讲坛','时值2018年全国科技周、上海科技节，由上海交通大学生物转基因科普教育基地承办的转基因科普讲坛将在5月20日在树华多功能厅开展。我们邀请到了中科院上海植物生理生态研究所、科学院院士许智宏与农业农村部转基因生物安全管理办公室，副处长张宪法来为我们讲解转基因的知识。活动时间：2018年5月20日9：30活动地点：上海交通大学生命科学技术学院树华多功能厅','2018-05-20 09:30','2018-05-20 11:30','上海交通大学生命科学技术学院树华多功能厅','https://tongqu.me/upload/acts/20180516/small_1a9d491579b33078ef06d24484a06be4.jpg','同去网','2018-05-20 09:30:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-846825888,'光，点亮世界','光纤国家重点实验室2018校园开放日活动——“探寻光纤线通信奥秘”光纤作为传输媒介具有无可比拟的优越性，是宽带通信网络的主要载体。截止2016年，我国铺设的光纤已经达到10亿芯公里，以超摩尔定律高速发展，光正在“点亮”信息社会的每一个角落。区域光纤通信网与新型光通信系统国家重点实验室围绕光纤通信，理工融合，强强联合，在光网络，光传输，光传感和信号处理，光电子器件四大方向形成了自己的研究特色，各方向的成果在通信行业的发展中也产生了重要的影响。本次公众开放日活动是实验室响应国家科技部的号召，在2018年科技活动周开展的系列活动之一。开放日当天，实验室将带大家一起探寻光通信的奥秘，了解光通信对日常生活的影响以及光通信在前沿领域的应用和发展。随着5G、物联网、AR/VR等新兴通信需求的涌现，未来光通信的创新和发展也面临着众多的挑战。无论是你是对通信兴趣浓厚还是未来有志于未来通信技术的研究，或者也许你只是充满好奇，光纤国重实验室都欢迎您的到来，跟我们一起来探究光通信的奥秘！注：本次活动面向各小学、初中、高中以及高等院校的老师和同学，若以团体形式参观，需提前预约，预约方式：发送"参观团体+参观人数+联系方式"至邮箱FiberHome16@163.com或者手机15800802370','2018-05-19 10:00','2018-05-19 15:00','上海市闵行区东川路800号电信学院5号楼','https://tongqu.me/upload/acts/20180516/small_ceedffb3278b139b79b1601ba554ca93.jpg','同去网','2018-05-19 10:00:00.0');
