insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(111644807,'【电院-讲座素拓】丝路多语言信息智能处理研究及展望','报告人：吾守尔·斯拉木，新疆大学信息科学与工程学院教授、博士生导师，中国工程院院士报告：丝路多语言信息智能处理研究及展望时间：2018年5月14日（周一）上午10:00-11:30地点：电信学院3号楼208会议室主持：谷大武，上海交通大学特聘教授，教育部长江学者摘要：一、新时代新需求、新机遇二、智能信息处理发展趋势三、丝路多语言信息智能处理拟解决的重大科技问题四、国家反恐维稳迫切需要丝路智能交流平台五、周边国家语言分类及智能交换六、近期开展的工作七、新疆多语种信息技术实验室简介吾守尔·斯拉木教授简介：吾守尔·斯拉木，新疆大学信息科学与工程学院教授、博士生导师，中国工程院院士。现任新疆多语种信息技术重点实验室、新疆民文信息技术研发中心、新疆多语种信息技术研究中心主任，中国中文信息学会原副理事长、新疆IT联盟名义主席，享受国务院特殊津贴专家、国家级突出贡献中青年专家、全国先进工作者、全国侨界十佳、新疆十佳科技人物等。长期从事多语种信息处理研究与开发应用。带领多民族研发团队，主持承担国家863计划项目7项、973项目1项、国家基金重点项目2项、国家级和省部级项目40多项；主持研发应用30多种支持多语种的新系统新软件；主持制定国际标准5项、国家标准22项、编译著作（教材）9本、发表论文170余篇。荣获国家科技进步奖3项、自治区科技进步奖7项、何梁何利科技奖、光华科技二等奖等省部级以上奖励13项。新疆大学计算机科学与工程国家一流学科、211工程新疆地区信息化及关键技术学科带头人，牵头组建了计算机应用博士点、博士后流动站、4个硕士点、新疆多语种信息技术重点实验室、新疆民文信息技术研发中心、新疆多语种信息技术研究中心。多次被江泽民、胡锦涛、李鹏、李岚清以及习近平、李克强、俞振声、刘延东等党和国家领导人接见和鼓励。2011年当选为中国工程院院士。本次活动针对电院同学开放报名，电院研究生记一次讲座素拓。请报名的同学提前15分钟到场，并刷学生卡签到。','2018-05-14 10:00','2018-05-14 11:30','电院3-208','https://tongqu.me/upload/acts/20180511/small_908660baa710796074b27a68e63fb163.jpg','同去网','2018-05-14 10:00:00.0');
