insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-619753219,'交大-苏州人工智能论坛报名','你想领略人工智能资深专家教授的独特分享吗？你想亲身体验AI行业巨头思必驰公司的研究氛围吗？你想免费参加苏州独墅湖的AI论坛吗？那就赶快报名吧——上海交大人工智能资深专家教授带你走进AI,对人工智能感兴趣的同学不可错过哦，具体安排参照下文：活动具体时间安排：5月16日上午7.15从闵行出发去苏州独墅湖，上海交大苏州人工智能研究院人工智能圆桌论坛报名，中午用餐结束回闵行。活动及报名邮箱：lejingwei@sjtu.edu.cn论坛时间：2018年5月16日上午09:30-11:30论坛地点：苏州独墅湖世尊酒店（ 苏州市吴中区启月街299号）论坛主持：王延峰 上海交大苏州人工智能研究院院长议  程：（一）  主持人王延峰院长开场、介绍嘉宾 9:30-9:40（二）  苏州工业园区领导致辞9:40-9:50（待定）（三）  主题演讲：9:50-10:10 上海交通大学张娅教授“资源受限下的机器学习”（四）  主题演讲：10:10-10:30 秒针系统创始人兼CEO吴明辉“将AI技术转化为生产力”（五）  主题演讲：10:30-10:50 思必驰首席科学家俞凯教授“流通生长的对话智能”（六）  秒针系统与上海交大苏州人工智能研究院战略合作签约 10:50-11:00（七）  现场观众与嘉宾问答互动 11:00-11:30（八） 参观人工智能独角兽思必驰公司和苏州上海交大人工智能研究院人工智能重量嘉宾：上海交通大学张娅教授张娅，女，上海交通大学未来媒体网络协同创新中心研究员、博士生导师、科技部863计划媒体大数据项目首席专家。清华大学本科、美国宾州州立大学博士。2005年至2007年，担任美国堪萨斯大学电子工程与计算机科学系助理教授(tenure-track assistant professor)。2006年至2010年，为美国雅虎实验室（Yahoo！Labs）资深研发经理。2010年加入上海交通大学，2014年晋升为研究员。主要研究方向为人工智能、机器学习、媒体智能处理。在国际高质量期刊和会议上发表学术论文80余篇，其中CCF-A/B类论文36篇。论文他引次数共计2000余次，论文单篇最高他引超过500次（Google Scholar）。研究成果已获得5项美国专利授权和6项中国专利授权。主持和参与多项国家973 计划课题、863 计划课题、国家自然科学基金项目，负责其中多项关键技术的研究及系统平台的开发。','2018-05-16 07:00','2018-05-16 20:41','苏州独墅湖','','同去网','2018-05-16 07:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(115277071,'仪表人材——材料科学与工程的基础实验室','材料科学一直都是科学研究领域的先驱者，材料学院也一直致力于培养优秀的科技创新型人才。想了解材料学院的大牛们每天都在研究些什么吗？想亲身体验参与材料学院各个实验室的功能，并实际操作吗？想近距离接触SEM、XRD、焊接机器人、EFM等高端实验仪器，学以致用吗？为培养本科生的科研兴趣，增强科研素养，鼓励本科生积极参与相关科研活动，材料科学与工程学院学生科技协会联合学院各课题组和实验室拟开展“仪表人材——材料科学与工程的基础实验室”活动，组织本科生进实验室参观走访活动。本次活动立足于创新的思维引领发展，将分为两个小组，采用轮换制分别走访材料学院六大科研平台的重点实验室，由院士、教授等领衔的讲解队伍引领大家深入了解各个领域及研究内容。本次活动实验平台简介：一、特种材料研究所由教育部“长江学者”王浩伟教授任中心主任，中科院院士周尧和教授、中国复合材料领域创拓者吴人洁教授担任学术顾问。研究所主要开展新型特种材料的制备和成形科学技术研究。近年在铝、镁基复合材料的界面与性能、成形过程、腐蚀与防护、疲劳与断裂，铝再生技术、重金属离子吸附技术等领域进行深入研究并取得突破性进展。多年来承担国家重点项目、国家自然科学基金、国际合作项目等十多项，获得教育部技术发明一等奖一项。在国内外学术刊物上发表学术研究论文100余篇，获授权中国国家发明专利43项。特种材料研究所目前拥有复合材料调压铸造设备、先进复合材料原位合成设备、复合材料挤压铸造设备、复合材料压力浸渗设备、复合材料挤压设备、复合材料连铸设备、静态力学试验机、动态力学试验机、超高压水射流切割机床、光电直读光谱分析仪、高频阻尼分析仪、图像分析仪、电化学工作站等大型设备和材料分析检测手段。二、轻合金研究所上海交通大学轻合金精密成型国家工程研究中心(Light Alloy Net Forming National Engineering Research Center-LAF-NERC)成立于2000年3月，是由国家发改委批准组建、上海交通大学承建的国家级工程研究中心，现任中心主任为丁文江教授。经 过十年多的发展，中心目前已经成为我国重要的镁合金材料研发基地，同时也是世界范围内最大最先进的镁合金研究团队之一。中心主要研究领域包括高性能镁合金 材料、镁合金液态成型技术、镁合金固态成型技术、镁合金净化技术、镁合金表面处理技术、镁基生物材料、镁基能源材料等七个主要研究领域','2018-05-11 12:00','2018-05-11 14:00','上海交通大学材料科学与工程学院','https://tongqu.me/upload/acts/20180510/small_ec29ebd5bdaca7a73ac85a5325f94134.jpg','同去网','2018-05-11 12:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1625802023,'生科讲坛——I型PKS硫酯酶的底物识别与催化机制初探','“生科讲坛”系列讲座：I型PKS硫酯酶的底物识别与催化机制初探【活动目的】为了推动本科生导师制的有效实施，帮助本科生全面正确的了解生命学科领域研究，培养专业志趣，训练科学思维，搭建导师与学生沟通交流的平台，学生办推出“生科讲坛”系列讲座。【活动内容】1. 嘉宾正在开展的研究课题，侧重于科学研究的思路、原理、方法等，讲述“发现问题-分析问题-解决问题”的过程；2. 嘉宾研究领域的、涉及与人类疾病、生活、生产等等相关的前沿介绍。在医学临床上多种常见的抗生素、抗真菌药物、免疫抑制剂和抗癌药物均属于聚酮类化合物，它是一类微生物次生代谢产物。由于聚酮类化合物极其重要的药用价值，聚酮合酶体系（PKS）自发现以来就备受关注，研究表明，该合成酶系中硫酯酶决定了聚酮母体的释放效率及化学结构。研究团队构建了苦霉素pikromycin的两种天然底物（十二元环10-deoxymethynolide（1）和十四元环narbonolide（2））所对应的研究体系（PIK-TE-1 和PIK-TE-2），运用分子动力学模拟（MD）、量子力学与分子力学联用的QM/MM方法，分析了两种底物形成“预反应态（PRS，prereaction state）”的难易程度，发现PIK-TE-1体系比PIK-TE-2体系更容易形成稳定的预反应态。同时，预反应态的结构特征分析表明，氢键和疏水相互作用在底物识别和产物释放过程发挥了重要作用。此外，通过反应势能面的计算发现，PIK-TE-1体系环化过程中底物末端羟基从re面方向亲核进攻的环化反应能垒为16.3 kcal/mol，比从si面进攻的能垒要低3.6 kcal/mol，而相比于PIK-TE-2体系的re面进攻的反应能垒低4.1 kcal/mol，这与实验结果相符。【活动时间】5月11日中午12：30-13：30【报告人】石婷  副研究员【报告地点】生物药学楼3-105【报告人简介】石婷，上海交通大学 生命科学技术学院 生物信息学与生物统计学系 副研究员教育经历：1997.09-2001.07西北大学化学专业，获理学学士学位。 2002.09-2007.07西北大学化学系，从事计算化学领域研究，获理学博士学位。工作经历：','2018-05-11 12:30','2018-05-11 13:30','生物药学楼3-105','https://tongqu.me/upload/acts/20180510/small_d18afbba0a51e2d89be077d525bff9c4.jpg','同去网','2018-05-11 12:30:00.0');
