insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(19388133,'线性规划的近期进展——斯坦福大学叶荫宇教授莅临报告','标题：线性规划的近期进展Title: Recent Progress on Linear ProgrammingWe describe recent algorithmic progress on linear programming (LP) as well as research explorations of alternative methods on big-data linear programs. The specific topics include polynomial-time simplex and policy iteration methods for the Markov Decision-Game Process, faster interior-point algorithms for network flows, convergence of the multi-block alternating direction method of multipliers for more generally constrained convex optimization, and first-order potential reduction algorithms for linear programming and their preliminary computational results. We also present modern LP and mixed-integer LP applications.','2018-05-11 10:00','2018-05-11 11:00','上海交通大学闵行校区电信群楼3-100','https://tongqu.me/upload/acts/20180508/small_f16998e65b339aa432dcfb4f6093a8cb.png','同去网','2018-05-11 10:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1175734553,'National Instruments 上海交通大学LabVIEW编程集训营|SI培训','美国国家仪器（National Instruments，简称“NI”）是一家在全球范围内测试测量领域提供专业解决方案的公司。NI提供了强大灵活的技术解决方案来帮助工程师和科学家提高生产力，加速创新。 从日常工作到重大挑战，NI均可帮助他们应对复杂的问题，获得超出预期的结果。 无论是医疗、汽车、消费电子还是粒子物理，几乎各行各业的客户均在使用NI的集成式软硬件平台来改善我们的世界。由上海交通大学学生创新中心与NI联合筹办的“LabVIEW编程集训营”开始接受报名！本次LabVIEW Camp旨在通过3天的集训帮助学员迅速掌握常用的编程技巧具备开发应用与科研项目的能力本次集训营培训内容不但全部免费集训结束后NI还提供帮助检验水平的CLAD助理工程师开发认证测试让学员巩固集训所学LabVIEW在测控届的江湖地位无人不知，无人不晓。它简单易用，可以实现各类平台（计算机，嵌入式，FPGA等）的开发，帮助工程师和科学家以更高的效率完成工作。LabVIEWCLAD是一项全球范围内的专业认证测试，合格者被证明非常了解LabVIEW工作环境、对最佳的编码和文档编制有基本认知。NI认证帮助工程师提高专业技能的自信度，获取更多新的机会和更好的发展。本次集训营培训赠送一次价值99美刀的CLAD助理工程师开发认证测试。面向对象：上海地区科研工作者、研究生、高年级本科生培训地点：上海交通大学学生创新中心培训日程：5月25日 9:00-12:00 LabVIEW基础编程集训13:30-17:00 LabVIEW基础编程集训5月26日9:00-12:00 LabVIEW进阶编程集训13:30-17:00 LabVIEW进阶编程集训与答疑5月27日9:00-12:00 CLAD考前指导13:30-17:00 CLAD测试报名请扫描下方二维码：','2018-05-25 09:00','2018-05-27 17:00','上海交通大学学生创新中心','https://tongqu.me/upload/acts/20180508/small_844100f9e25cd24c01fd2bf2b3d88782.png','同去网','2018-05-25 09:00:00.0');
