insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-71442047,'企业望远镜系列讲座第七期航天器智能自主技术宣讲会','中国运载火箭技术研究院"科技兴邦 航天报国"校园宣讲暨暑期实践团招募                 "立志宏远、探索未来"         ——航天器智能自主技术展望同学!!!听说过中国运载火箭技术研究院吗？想燃烧“科技兴邦 航天报国”的小宇宙吗？想拒绝在暑期做一条咸鱼吗？一场航天器自主技术主题的宣讲会等着你呢！先来膜拜一下重量级宣讲人吧~宣讲人介绍宋征宇总设计师研究员 / 博士生导师 / 国际宇航科学院（IAA）院士 / 现任中国运载火箭技术研究院型号总设计师长期从事航天控制基础理论和新型运载火箭控制系统的研究，先后担任中国载人运载火箭CZ-2F、中国新一代中型运载火箭CZ-7、中国重型运载火箭等型号的副总设计师，以及新型商业运载火箭CZ-8总设计师。获得国家科技进步奖特等奖和一等奖、国防科技进步奖一等奖等奖项，入选国家“万人计划”科技创新领军人才。并曾荣获中国青年五四杰出贡献奖章、中国十大杰出青年、全国五一劳动奖章等荣誉称号。厉害了吧！和设计师一起展望科技加入实践团队获得锻炼了解感兴趣的火箭技术宣讲会准备好迎接你们啦！宣讲会时间：2018年5月9日（周三） 14:00-16:00地点：上海交通大学闵行校区铁生馆200号报名方式：直接发送短信“姓名+参加宣讲会”至18217278379关于我们上海交通大学生涯发展协会（Student Career Development Union，简称 SCDU）是全国高校中首个成立的职业发展类学生社团，是面向上海交通大学在校生并专注于职业发展的协会。十五年来，SCDU 充分发挥学生团队自我管理，自我教育的特点，积极为同学们提供职业规划、求职指导、职业技能培训等多方面的服务；致力于融合校内企业资源，形成多层次、广覆盖的资源和职业发展培养体系。作为一个高层次高素质的学生团体，协会各种活动精彩不断，受到在校师生广泛好评，连续多年获评上海交通大学“十佳社团”称号，并以最高票数连年当选交通大学社团联合会理事社团。长按二维码获取关于职协的更多信息','2018-05-09 14:00','2018-05-09 16:00','闵行校区铁生馆200号','https://tongqu.me/upload/acts/20180506/small_ba728e6997c501c966d59fa97685f90e.jpg','同去网','2018-05-09 14:00:00.0');
