insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1639540224,'创新与创业大讲堂第87期：拒绝泡沫，探索AI时代的创业密码','创新与创业大讲堂第87期：拒绝泡沫，探索AI时代的创业密码图像识别、语音识别，机器学习…人工智能正在掀起一场新的技术革命。站在风口，本想成为AI时代的弄潮儿；前赴后继，谁曾想却被前浪后浪一起拍倒在沙滩上。并不是选择对了创业风口就会成功，你还需要有人替你拨开云雾，指点迷津！第87期创新与创业大讲堂，教你如何拒绝泡沫式灭亡，探索AI创业的摩斯密码。确认过眼神，你就是AI创业对的人。本期大讲堂有幸邀请到交大校友、普林斯顿电子信息工程博士、人工智能私人助理产品—“来也”创始人汪冠春与交大学弟学妹们进行零距离交流，他将为大家讲述AI领域最新发展动向，并倾情分享对AI创业的相关经验与思考。活动信息时间丨5月9日19:00 - 21:00地点丨报名成功后短信通知讲堂内容丨主题演讲+现场互动讲堂规模丨50-100人主讲人丨汪冠春，“助理来也”创始人，上海交通大学电子信息与电气工程学院校友，普林斯顿大学电子信息工程博士报名方式|同去网扫码报名主办单位丨上海交通大学创业学院上海交通大学电子信息与电气工程学院嘉宾简介：汪冠春，本科毕业于上海交通大学电子信息工程专业，普林斯顿大学电子信息工程博士。求学期间，以2008美国大选预测为题的论文发表于Decision Analysis，Politics&Policy等核心期刊。曾任普林中国学生会主席。毕业回国后首次创业，创办个性化影视推荐产品“今晚看啥”。 “今晚看啥”是国内个性化发现的先驱者，也成了百万用户喜爱的影视平台。公司于2012年底被百度以千万级美金收购。在百度任职期间，担任高级经理。负责智能交互应用团队专注研发人机交互和智能硬件，在自动化对话系统和大数据智能餐具等方面的创新积累了20多项专利，其中百度筷搜更是获得多项国内外大奖，包括中国首个戛纳创新金狮奖。现正在进行二次创业，主推人工智能私人助理产品—“来也”。目前公司在上海和北京都有团队，近60人。公司已完成两轮1,000万美金的融资，投资方包括光速，微软（首次投资中国创业公司），红杉，真格，澎湃等。产品上线后已服务过100多个城市近百万用户，交易流水已超千万元。','2018-05-09 19:00','2018-05-09 21:00','上海交通大学微电子学院多功能报告厅','https://tongqu.me/upload/acts/20180503/small_2f2eeed036a4c6c05de52309c470b7d5.jpg','同去网','2018-05-09 19:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(693746727,'周四沙龙','主讲人：彭星皓时间：5月3号  周四  18:30 地点：新图 C220 放映厅时长：124 分钟基本信息导演: 大友克洋编剧: 大友克洋 / 桥本以藏主演: 岩田光央 / 佐佐木望 / 小山茉美 / 玄田哲章 / 北村弘一 / 池水通洋类型: 动作 / 科幻 / 动画语言: 日语片长: 124 分钟剧情简介　　1988年7月，关东地区使用新型炸弹进行爆破，第三次世界大战即刻爆发。31年后，人类从一片废墟中重建都市，东京湾上建立了巨大城市——新东京。世界仿佛要恢复到以前的繁荣。 　　2019年的一个夜晚，新东京郊外的封闭高速公路上，暴走族团体正在公路上飙车。带头的少年，是一名叫做金田的职业高中学生。 　　突然，街上出现了一位奇怪的矮个子男子，暴走族少年铁雄为躲避车祸连人带车飞出公路。正当金田要拿那位男子问罪之时，军队驾驶直升飞机感到。将那位矮个男子与铁雄一起收押。 　　铁雄进入了一个陌生的地方，这里似乎在从事超能力的研究。而金田则在盘算如何营救铁雄。二人都不知道，他们的未来，因为神秘力量——阿基拉而已经开始了巨大的变化。 豆瓣短评大密度蓝文明的野蛮，野蛮的人性，人性的文明。可能，是一个像莫比乌斯一样走不出来的环。恶魔的步调结尾巨婴疑致敬2001太空漫友但感觉后来又被今敏的红辣椒给致敬了Minty滥用力量的弱者、失控的文明、“星孩”、政客、乌合之众。然而大部分的篇幅都在讲述与主题并无关系的主人公和小学生超能力大战。顶尖的作画在今天依然值得赞叹，可是叙事方式难以接受。','2018-05-03 18:00','2018-05-03 20:00','新图C220','https://tongqu.me/upload/acts/20180503/small_e60d6c3d9762872bcc7670b40706d380.jpg','同去网','2018-05-03 18:00:00.0');
