insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2069628951,'君行国际义工招募（清迈中文义教）','1， 泰国清迈简介泰北玫瑰，清新小城清迈是清迈府的首府，泰国的第二大城市，发达程度仅次于首都曼谷。这座曾沉睡在泰国北部的古城，历来以历史文化之悠久闻名。清迈是国际旅游城市，现颇受中国游人青睐。这里是邓丽君生前最爱的度假地，甚至连生命的最后一刻都留在了这里。与热闹的首都曼谷相比，清迈有着完全不同的气质：清爽的空气、幽静的城市、亲近自然的街景、沉淀几百年甚至更久的文化底蕴，素有“泰北玫瑰”之称。清迈的手工艺品同样以其雅致、精巧闻名世界。清迈城内遍布古色古香的寺庙殿堂：白色的墙壁、红色的屋顶、金色的飞檐在阳光下交相辉映，造型独特的泰北式佛塔雄伟而又别致。寺庙中的僧人和当地居民一样谦和友善，乐于和游客讨论一切关于佛教和清迈历史文化的问题。2， 项目内容中文教学，民俗体验，热门景点观光随着全球掀起“中文热”，加上“一带一路”政策的带动，沿线国家与中国在经贸、文化、旅游等领域交往更加密切，许多国家渴望了解中国文化，当地人民也渴望学习中文来增加就业选择，许多学校陆续把中文课列入选修/必修课程，但缺乏足够的中文课程教师。我们将前往清迈当地的小学，寺庙和社区，通过基础中文教学、游戏互动来增进中国文化的交流传播，对所服务机构教育资源匮乏的状况提供帮助。此项目的教学任务相对英语教学项目轻松，工作之余，我们为参与者安排了充分的文化体验活动使参与者们深度体验清迈，包含参观清迈最神圣的素贴山双龙寺，游览乐趣满满的3D艺术博物馆，学做可爱的微笑陶土玩偶，参观兰纳民俗博物馆，学做地道的泰国料理，游玩清迈动物园等，同时君行清迈项目是唯一住宿地点在清迈城区支教项目，在自由活动时间，义工们可便利进入清迈古城游览，或选择参与当地游览项目，体验绝佳。3， 项目日程明确日程安排，丰富体验活动开营日期前的周末当地项目负责人在清迈国际机场接机，送往入住义工统一住宿酒店，周末可以熟悉所在地区环境，进行项目准备，可自由活动，周日晚上建议游览著名的周日夜市。Day 1 星期一上午进行项目简介会，发放教学大纲，介绍项目内容，泰国文化，当地环境，学习简单当地语言，为本周的义工工作做准备；下午前往清迈当地最神圣的素贴山双龙寺，领略金顶佛寺风采，可以俯瞰清迈全景；晚上一起享用欢迎晚餐Welcome dinner。','2018-07-02 22:46','2018-09-10 22:46','泰国清迈','https://tongqu.me/upload/acts/20180502/small_8784ffc36a5aab8b101ef88d2c97d4c1.jpg','同去网','2018-07-02 22:46:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1572772008,'2018第六届全国赣协“C9+1百校夏令营”暑假支教志愿者招募令','我们是全国赣协C9+1高校夏令营活动是江西省外高校赣籍学子团工委/全国高校赣协总会的精品活动，由C9高校和北京师范大学及87所高校赣协社团联合参与的大型暑期三下乡社会实践活动我们希望将知识拓展到书本以，通过多科目、多形式的教学，拓宽学生视野，调动学生兴趣，引导学生自主思考：以各类素质拓展项目为引，寓教于乐，在激发学生想象力的同时培养学生的团队意识、集体荣誉意识。你需要做语数外、政史地、理化生信手拈来？音乐体育美术有所专长？街舞航模略有所通？不管怎样，有一颗乐于奉献的心、和善有爱心的态度就是你能来这里的原因。1、组织方指导单位：共青团江西省委江西教育厅主办单位：江西省外高校赣籍学子团工委全国高校赣协总会江西省光华扶贫基金会协办单位：全国各大高校赣协江西省11个社区市团委2、志愿者成员C9大学联盟高校和北京师范大学等江西省内外其他著名高校的感激学子（当然也热烈欢迎其他省热衷于江西教育事业的学子）我们会进行严格的面试筛选和充分的培训，确保人员的质量。3、活动受众分布在江西11个地级市的小学生、初中生、高中生以及支教地附近的农民4、教学设置每个班平均40个学生，每支队伍15个人左右5、支教时间时间分布在七月到八月，持续时间为两周，具体时间以去支教的学校为准。志愿者可以根据自己的实际情况选择合适的队伍进行支教。6、课程安排所授课程以兴趣开发类、思维拓展类为主，不推荐课本知识的教学。内容可涵盖文学、书画、外语、政治、音乐、舞蹈、影视、鉴赏等内容，以拓宽学生自主思考；另外，志愿者老师们还准备了拓展项目从，寓教于乐，在激发学生想象力的同时培养学生的团队意识和集体荣誉意识。报名方式报名时间:4月15日——5月8日报名方式：1、填写报名表：链接:https://pan.baidu.com/s/1ICt6IEFfUPNkhZyxs4vwKA 密码:8r7n（要是有打不开链接的宝宝直接加群就好了呢，里面会有哒）2、填写报名表发送至邮箱qgganxie@163.com3、邮箱请以“高校名称+姓名为主题”另欢迎加入百校支教咨询群进行咨询','2018-05-30 00:00','2018-05-30 23:59','江西','https://tongqu.me/upload/acts/20180502/small_2f1d1f8987766611239aa7662c35d646.jpg','同去网','2018-05-30 00:00:00.0');
