insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-5602500,'讲座|跟随交易员走进期货世界','时间：2018年5月2日 星期三 19:00地点：上海交通大学闵行校区 逸夫楼101主要内容：金融期货市场的历史和背景，期货基础知识、交易方法及案例分析，与讲师交流，问答环节说明：欢迎所有对期货及金融好奇、感兴趣的同学来听，讲座轻松愉快，即便是金融小白也不用有压力。有任何理财、交易、实习方面的问题都可以和讲师交流讨论！讲师介绍：1、翟贺攀翟贺攀，建信期货研究员，2009年毕业于上海财经大学，2017年5月加盟建信期货研投中心。具有煤焦钢矿期现货行业6年研究经验，服务于多家钢厂、贸易商、焦化和矿山企业以及投资机构的保值、套利和资产配置业务，善于在基本面逻辑梳理、技术面择时与事件驱动之间把握盘面与现货动态机会。2、郭阳郭阳，建信期货大宗商品部客户经理。2017年毕业前进入中国建设银行总行实习，毕业后进入建信期货任管理培训生。通过期货投资分析考试，对期货基础理论知识掌握扎实，多次给企业及公司内部做期货基础知识的培训，有着丰富的演讲能力和感染力。其他福利到场者可得小夜灯一个，先到先得','2018-05-02 19:00','2018-05-02 20:30','逸夫楼101','https://tongqu.me/upload/acts/20180427/small_842355e267f3f5ba04fa3ae2a7048f02.jpg','同去网','2018-05-02 19:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1627763219,'遨游天空赏美景！-魅力宏村 刺激滑翔伞 醉氧木坑竹海','活动须知05月25日晚-05月27日（宏村+滑翔伞）不一样的旅行！遨游天空赏美景！-魅力宏村 刺激滑翔伞 醉氧木坑竹海 （周末3天2晚）活动详情05月25日晚-05月27日（宏村+滑翔伞）不一样的旅行！遨游天空赏美景！-魅力宏村 刺激滑翔伞 醉氧木坑竹海 （周末3天2晚）1.宏村位于安徽省徽州六县之一的黟县东北部，整个村依山伴水而建，村后以青山为屏障，地势高爽，可挡北面来风，既无山洪暴发冲击之危机，又有仰视山色泉声之乐。八九百年前的建村者便有先建水系后依水系而建村的前瞻，所以使它有了水一样的灵性，这也正是它比其他徽派建筑的村落更具魅力的原因。2.黄山国际滑翔伞基地是安徽省唯第壹家滑翔伞体验中心，环境优美，起飞条件好，设备先进，安全系数高。滑翔伞运动是一项航空体育运动，是利用空气力学的原理而达到滑翔与滞空目的。这项运动具有参与性广，惊险刺激等特点，广大滑翔伞爱好者都可以在空中享受身心彻底的放松，以及挑战自我的乐趣和对生命的深层体验。3.木坑竹海，又称皖南大竹海，为黄山入黟第一处景点，主体景区面积6平方公里，四面环竹，郁郁葱葱，山势险要，形如金斗，环境优美，风水绝佳。早在1963年中央新闻纪录片《白云深处一山村》报道了这里的环境和乡村状况，每年吸引众多名人、摄影家、影视家来此采风创作第1天 05月25日 星期五晚上19：00人民广场博物馆南门（近武胜路）8个石狮子中间的平台上集合，19:30发车.车上领队介绍行程及解答相关问题。一路欢歌笑语，大家很块成为好朋友。00: 00左右下高速公路，旅馆休息，保证睡眠，养足精神第2天06:00早餐后继续出发到宏村，路上哪里风景好，我们安排下车拍照。09:00到宏村先安排去奇墅湖。分配好房间，开始我们的摄影飞翔之旅。有3条线路可选按照你的计划自选1.徒步组的同学可以一路我们沿着塔川一路徒步到协理。2.玩滑翔伞的同学会有车接你们上山到基地，徒步组可以在降落点看你们飞下来，摆拍哦 3.喜欢古村落的队员可以逛宏村或者徒步奇墅湖哦12:00左右 大家在滑翔伞降落点集合回农家。17:30然后回到农家，农家菜吃起来，腐败活动开始.....晚上也可以进宏村哦20:00夜里，点上手电头灯，真情告白也好，趣味故事也罢，游戏也行...运气好还能跟领队打猎去 恩 真事 22:00夜深人静，星光灿烂，大锅~ 你该歇息了吧...','2018-05-25 19:00','2018-05-27 20:00','安徽宏村','https://tongqu.me/upload/acts/20180426/small_5c1eea5058f217243a8109334571cfce.jpg','同去网','2018-05-25 19:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1457639817,'05-20爱在夏天,渔山岛看日出寻最美星空，约上好友带上TA一起来吧（周末3天2晚）','活动介绍05-20爱在夏天,渔山岛看日出寻最美星空，约上好友带上TA一起来吧渔山岛给人的第一印象便是清新的空气，令人心旷神怡。蔚蓝的天，湛蓝的海，把海岛掩映得更加美丽。渔山岛，位于猫头洋的东部，由13岛41礁组成，全岛呈东北、西南排列，南北7.5公里，东南4.5公里，全岛面积约5平方公里，其中以南渔山为最大，五虎礁雄踞海疆，系领海基线。岛礁，其实这从本岛看上去的五虎礁，是由从南而北的11座大小不一的岛礁组成。游人在岛上所见的五虎礁，实为伏虎礁（包括紧贴其身的仔虎礁）、尖虎礁、高虎礁（岛）、平虎礁、老虎屎礁。沉浮于汹涌波涛之中的“五虎”更显出它的雄健之势。潮高时其形状则如珠浮碧海，变得玲珑可爱，令游人一睹而终生难忘。运气好的话晚上还有看到“蓝色眼泪”【行程安排】D1 周五 05月18日晚上上海博物馆南门19:00集合（地铁人民广场站上海博物馆门口八个石狮子中间）20:00 准时发车。最后上车的表演节目哦！迟到的可以选择出租车过去！车上大家互相介绍一下。01:30 左右到达石浦码头，旅店分房休息。D2  周六早点起来收拾装备吃早饭（镇上饭店自行解决，这里的海鲜面很有特色）， 6:30-7:00班船上岛. (具体出发时间按航次可能调整)，上岛后, 海钓的海钓,打牌的打牌,晒太阳的晒太阳,发呆的发呆.渔山岛岛上住宿：住宿有渔家宾馆。可帮助代订，岛上扎营还是比较舒服的，有草地、很凉爽，抵得上海景房。大家可自由选择13:00环岛徒步走起！开始环岛漫步，面朝大海，春暖花开。我们欣赏无敌海景，看五虎礁惊涛拍岸；登远东第一大灯塔，看海天一色；过天生桥，看天地奇观；到渔村闲逛，看渔家生活。18:00农家腐败，海钓的童鞋可以把自己的战利品拿出来统统煮了，傍晚观晚霞. 晚上看月亮,星星,灯塔….；夜里，点上手电头灯，我们开始快乐的海滨激情晚会，我们燃放电火花，升起孔明灯（视天气和海岛规定而定）；真情告白也好，趣味故事也罢，杀人游戏也行，来海岛要的就是自由的时光~但夜里不要轻易抬头，否则那密密麻麻壮观的银河星系，会使你嘴巴张大合不拢，哈哈！ 夜里可能还能看到“蓝色眼泪”22:00夜深人静，星光灿烂，帐篷里的活动搞起（别想歪）。D3  周日','2018-05-18 19:00','2018-05-20 20:00','浙江渔山岛','https://tongqu.me/upload/acts/20180426/small_f9c934e1dc4febafbe3c6ba943d8a6ed.jpg','同去网','2018-05-18 19:00:00.0');
