insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1128503994,'赢创校园开放日及职业分享会','关于赢创工业集团赢创是一家来自德国的创新型工业集团，我们的核心业务—特种化工在全球处于领先地位。赢创的业务专注于全球发展大趋势，即健康、 营养、资源效率和全球化。2016 财年，赢创创造了约127亿欧元的销售额，营业利润（调整后税息折旧及摊销前利润）达21.65亿欧元。本次活动属于赢创集团针对2019届毕业生的校招项目的一部分。活动分为以下几个部分：    一、企业参观。同学们可以有机会在赢创莘庄工业区分部参访，机会难得。    二、职业分享。会有赢创内部交大学长或者优秀精英为大家分享职场的经验和公司的业务。    三、校园招聘。作为校招的前阶段，如果能够入选本阶段的招聘，就打开了进入赢创的第一扇大门，留任的机会非常之大喔。活动时间（拟定）：5月9日中午12：15在霍英东体育馆前停车场集合。具体集合地点以群通知为准。另外同学也可以在关注“赢创微招聘”公众号获取更加详细信息。活动参加方式：同去报名并扫码入群。群链接：本次活动因为名额有限：30人左右，所以请大家珍惜机会，如果报名之后因故不能参加，请务必发送邮件到sccejob@126.com. 邮件主题“活动退出+姓名+联系电话”。搬了六七年的砖，却越来越迷茫今后要不要以此为生？没日没夜地泡在实验室，简历上没有实习经历怎么破？GPA碾压众人，但依然不知道怎么作自我介绍？被师弟师妹称为“社交小王子”，每次都在面试中化身话痨无法自拔？……听说你正苦于不知道该如何赢得HR的中意？来看看参加过赢创求职分享会的前辈们有什么收获吧！关于简历比起简单罗列工作内容，简历书写更应该注重自己现阶段取得的成果原来简历中数字的使用也是有学问的啊~关于面试HR分享了他们面试时的经验和感受，让我更多地了解了他们的考察角度和心理问问题在面试中也是很重要的。以前都刻意地问一些听上去很高大上的问题，但其实帮助并不大关于职业生涯准确的自我认知是职业规划的第一步，那句“对自己的认识与别人对你的认识的程度越一致，你就越容易成功”让我有了很多思考……职业生涯其实没有完美的答案。如果有的话，答案就在自己手中！想成为最社会的搬砖工？急需史上最全求职通关秘籍？赢创上海职业分享会等你来！','2018-06-06 13:00','2018-06-06 17:00','赢创北亚区总部（春东路68号）','https://tongqu.me/upload/acts/20180425/small_5e3b69a0fb44f04a84b3da0e53be159f.jpg','同去网','2018-06-06 13:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2004945744,'君行国际义工招募（香格里拉）','1， 项目地简介多民族文化，独特边陲风貌云南位于中国西南的边陲，也是中国民族种类最多的省份，西部与南部分别与缅甸、老挝和越南交界，是中国连接东南亚、南亚的重要门户。因优良的气候环境与地理位置，这里动植物种类数为全国之冠，以独特的高原风光，热带、亚热带的边疆风物和多彩多姿的民族风情而闻名于海内外。项目所在地香格里拉，地处青藏高原东南边缘，独特的雪山、峡谷、草原、高山湖泊、原始森林景观，有着“高山大花园”、“动植物王国”的美称。这里雪峰连绵，峡谷纵横深切，最著名的有金沙江虎跳峡、澜沧江峡谷、辽阔的高山草原牧场、莽莽的原始森林以及星罗棋布的高山湖泊。在香格里拉生活着藏、纳西、彝、白、回等13个民族，他们在生活方式、服饰、建筑、民俗礼仪等仍保持着各自的特色，形成了别样的民族风情。2， 项目内容支教帮扶、民俗体验、景点游览云南部分山区的少数民族学校教学水平欠发达，教育结构较为单一，师资力量匮乏，加之各民族在语言和思维上差异，父母知识水平普遍较低，造成很多学生学习困难，学生获取课外教育资源的途径也非常局限。君行国际义工旅行携手香格里拉当地义工组织为当地学生开展支教工作和游戏互动，为当地孩子带去新的知识技能。改善基础教育是孩子们改变命运的重要途径。工作之余，项目还安排了藏语学习、藏文书写、藏族美食制作、藏族唐卡制作等文化体验，以及普达措森林公园、噶丹松赞林寺、龟山公园等景点参观活动，在进行支教活动外，充分了解多样民族风情。项目结束后也可自行安排旅行，云南历史文化悠久，自然风光绚丽，动植物种类丰富，有丽江古城、三江并流、石林、哈尼梯田、大理古城、崇圣寺三塔、玉龙雪山、洱海、滇池、抚仙湖、梅里雪山、西双版纳热带雨林等旅游景点。3， 项目日程明确日程安排，丰富当地体验Day 1 丽江机场接机后入住丽江古城民宿酒店，晚上自由活动，可游览丽江古城。Day 2 上午团队用车出发前往香格里拉（行驶时间约4小时），入住古城民宿酒店；下午进行项目介绍会，学习简单藏语、藏文、简单了解项目日程及工作安排。Day 3上午进行当地学校/社区支教工作，根据计划展开基础教学工作，如英语教学、课程辅导，利用游戏、歌曲等辅助方式与当地学生进行互动；下午游览独克宗古城，前往龟山公园，参观世界上最大、最高的转经筒。','2018-07-02 14:59','2018-09-10 14:59','香格里拉','https://tongqu.me/upload/acts/20180425/small_ba92981333657105817152f2b7ace13a.jpg','同去网','2018-07-02 14:59:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1198576830,'君行国际义工招募（马来西亚吉隆坡）','1， 马来西亚吉隆坡简介繁华都市，多民族风情交融马来西亚位于赤道附近，阳光充足、气候宜人，拥有很多高质量的海滩、奇特的海岛、原始热带丛林、珍贵的动植物、悠久的文化遗迹和繁华都市。作为一个多民族的国家，这里汇聚着多元的文化，在这块风光绮丽、物产丰富的大地上，能感受到来自各国的民俗风情、宗教文化和丰盛美食。吉隆坡是马来西亚的首都和最大城市，高耸入云的大厦和鳞次栉比的商铺彰显着繁华。世界最高双塔建筑双子塔是这座摩登都市的第一地标，无论是登顶88层饱览城市夜景，还是漫步空中桥梁感受惊心动魄，都是独一无二的体验。吉隆坡既有现代化大都会的豪华气派，也不乏古色古香的迷人风韵，风俗传统别具特色，多元文化活力无穷，更有融合了中国、印度、欧美的南洋风味美食让你一饱口福。2， 项目内容难民子女教学帮扶、特色观光游览本次项目所在地吉隆坡，吉隆坡外来人口众多，难民缺乏工作岗位，生活质量无法保证，难民儿童的教育问题更为严峻。为满足教育需求，许多社区和非政府组织建立了自己的教育中心。教育是改变难民生活、为下一代创造新未来的为数不多的契机之一。君行通过提供在吉隆坡进行义工项目的机会，与当地义工组织、UNHCR(联合国难民组织)组织参与者服务于当地的难民学校，志愿者们将在学校贡献自己的一份力量，通过简单英文授课，组织游戏和活动，帮助老师组织课堂，为孩子们带来新知识以及提供更好的教育环境。义工之余，我们同样致力于为参与者提供深度而优良的项目体验，提供吉隆坡安邦城区酒店，方便参与者们在当地的生活，项目内还安排了参观深度体验活动，如马来“天空之镜”、当地厨艺体验，城市文化探索等深入了解马来文化。项目结束后义工们可以自由安排旅行如前往吉隆坡云顶高原、水上粉红清真寺、黑风洞、热气球体验，或前往其他马来地点沙巴、仙本那、兰卡威等。3， 项目日程丰富日程安排，独家福利活动Day 1当地项目负责人在马来西亚吉隆坡国际机场接机，送往项目地，入住义工统一住宿酒店，周末可以熟悉所在地区环境，进行项目准备，可自由活动。','2018-07-02 14:46','2018-09-10 14:46','吉隆坡','https://tongqu.me/upload/acts/20180425/small_2813eb94d78b6c7950268adcfbb29172.jpg','同去网','2018-07-02 14:46:00.0');
