insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(180914705,'主动放映 春季巡展','“FIRST” 的创始初衷是持续发现与推选青年电影人首作及早期电影作品。“FIRST” 的另一层含义是希冀激发青年人的创作勇气和艺术自信，望他们争作执牛耳者。“FIRST” 是处女作，是竞技场，是实验室，是青年电影文化的集散地，以电影节竞赛形态淘洗推选冒险家，狂热派和顽固分子。这里有属于一个电影节的记忆力，价值观以及荣光。“FIRST” 关切所有阻滞理解的边界：政治的，社会的，学科的，性别的，地理的，因为我们相信，电影关于一切，电影为了一切。2006年以来，FIRST持续为业界推出极具价值的新锐导演，而这些新鲜面孔亦持续为电影界带来惊喜，不同的地区和城市，不同的文化语境，不同的影像探索，令我们感受到电影的锐度与力量、稚嫩与鲜活；这种多样化的创作意志，有力的补充着电影创作生态中关键部位的缺席。同时作为电影文化的交流平台，倡导青年电影文化介入当代生活，是世界电影流域内一处泊岸码头，在当代电影史记忆形成机制中，为世界提供中国青年电影的多义样本。亚洲一流电影人评审团，检验着FIRST评价体系的严谨与公正，2012年以来，历届电影节主席有：徐枫、许鞍华、谢飞、姜文、王家卫。历届大使有：徐峥、秦海璐、邓超、黄渤、陈坤。十年来，已有数百位电影新锐力量在FIRST影展收获了电影艺术上的尊重和肯定。他们包括范立欣、马莉、郝杰、忻钰坤、王一淳、德格娜、马凯、张涛、张大磊等一批批备受业界关注的青年电影人。今年第12届FIRST大使：赵薇主动放映为新鲜影像寻找观众，亦为观众带来青年影像的前端风向。春季，上百上千的新鲜影像由世界各地汇聚到组委会。而与此同时，电影节所呈现的优秀作品，随着主动放映的浪潮，奔涌至每一个对电影心向往之的角落。通过每一场主动放映，我们在这个充满选择而匮乏关注与耐心的数字世界，依然在以新鲜的作品延续对于电影艺术的理解与信心，期待以影像的力量激发思考。每一次放映、每一次观看，都是对广泛电影文化的一次小小改变，而当这些微小的改变在地域版图上由点连线、由线成面，终将成为对电影文化可见的塑造。以下为放映影片详情，文本较长请耐心阅读↓','2018-04-22 18:00','2018-04-22 21:30','C220','https://tongqu.me/upload/acts/20180418/small_71997ffb7845cebebb4eb91a46a83ce4.jpg','同去网','2018-04-22 18:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-255591544,'【关注】2018年度腾讯“犀牛鸟精英人才培养计划”申报启动！','尊敬的海内外高校及科研院所的老师们、同学们：随着人工智能等新技术的跨越式发展，以数字化为核心的全球科技与产业革命正在发生。在数字时代，创新是发展的第一动力，加快培养科技领军人才不仅有助于重塑创新体系、激发创新活力，更对引领科技革命、建设科技强国具有重要意义。2017年，腾讯首次发布犀牛鸟精英人才培养计划，引起国内外学生的广泛关注和参与，共收到近100余名学生申请。其中2018年，腾讯将继续加强与高校、科研机构的跨界联动，定制顶尖科技创新人才校企联合培养方案，覆盖机器学习、量子计算、计算机视觉、语音技术、自然语言处理等产学研热点方向，并为入选学生提供腾讯海内外实习及与海内外犀牛鸟学术专家交流的机会。具体申报流程及管理办法详见项目申报指南（点击页面底部的阅读原文）。更多项目信息，请关注腾讯高校合作官方网站、项目组邮件通知或高校合作官方微信账号：腾讯犀牛鸟精英人才培养计划项目组二零一八年三月二十日2018年度腾讯”申报指南第三条 评选流程：符合条件的学生在规定时间内填写申报表并上传简历、导师推荐表扫描件及其他佐证材料（申报表、导师推荐表内容见附件二、附件三），申请入口：http://ur.tencent.com/register/8。每位申请人限提交一份申请，最多申报两个课题（将优先评选第一课题选择），已入选计划的学生需隔一年再提交申请。时间节点如下。·        2018年03月20日 项目发布，申请开始；·        2018年05月10日 申请截止，启动评审；·        2018年07月04日 评审截止，发布结果。项目进行过程中的具体时间节点，请关注项目组通知。第四条 培养模式：本项目采用联合培养机制，入选计划的学生将由其导师及企业专家导师共同培养。入选后共同讨论确定联合培养期间的重点科研方向与目标（相关课题方向及腾讯导师介绍见附件一）。入选学生获得3个月以上的访问腾讯科研机会，接触产业真实问题，拓展科研思路，验证学术理论，联合发表论文、申请专利等。同时项目组将定制沟通技巧、创新创业等课程，举办线上线下交流活动，提升学生综合素质，助力其成长为顶尖研发人才。联合培养过程中，入选的精英学生还将获得如下资源：·        “十分精英圈”的在线访问权限，获取互联网行业真实研发内容、科研合作诉求等相关信息及资源（“十分精英圈”是面向精英学生的一个线上实名社群平台，具体介绍见附件四）；','2018-05-10 11:00','2018-05-10 12:00','线上报名参加','https://tongqu.me/upload/acts/20180418/small_06587ac0cfd9282e57a1b5dfde45c74a.PNG','同去网','2018-05-10 11:00:00.0');
