insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-542131925,'【"Voice-我思！"第26期】丨你所不知道的大飞机——六问“颤振”','你所不知道的大飞机——六问“颤振”嘉宾戴维中国商飞民用飞机试飞中心试飞工程师C919大型客机102架机首飞机组成员时间4月18日（周三）13：15-15：30地点上海交通大学（闵行校区）航空航天学院一楼报告厅国产大飞机C919在高空翱翔的壮丽身影背后，有许多工程师的心血与汗水，戴维就是其中之一。作为试飞工程师，他登机执行了C919飞机10101架机初始检查试飞、10102架机首飞及初始检查试飞，以及ARJ21研发及航线演示飞行等试飞任务。他曾获C919大型客机首飞二等功，先后两次C919大型客机项目年度先进个人等荣誉称号。1保试飞 亲执行戴维长期从事C919及CR929飞机试飞总体规划、试飞关键技术研究、外场专项试飞需求梳理等技术准备工作，具体承担了试飞任务的工程设计、风险评估、试验实施及总结报告，同时通过测试向改装、运行、场务等部门提出试飞实施保障需求，牵引试飞工作有序开展。C919飞机首飞准备过程中，他带领团队从技术交底、机组技术支持、试飞任务监控等多方面有力保障C919飞机首飞，并且登机执行了多次试飞任务。2展知识 窥原理你对大飞机有怎样的印象？是字母数字组成的名称？是精密复杂的部件组合？是国力科技进步的象征？大飞机总有你所不知道的一面。作为经验丰富的试飞工程师，戴维工程师将向我们分享飞机颤振的基本理论和试飞方法。什么是颤振？什么又是结构模态？什么情况下会发生颤振？颤振试飞危险吗？颤振试飞又需要什么特殊试验装置呢？颤振试飞该怎么飞呢？这些问题，都会在这次活动中得到解答。飞机  是人类对天空的梦想大飞机  是最迷人的梦想之一 试飞  是通往梦想的最后一程试飞工程师  是用智慧与勇气守护梦想的一群人 大飞机C919  大国重器试飞工程师   守卫雄鹰试飞原理和方法  精妙而美丽4月18日（周三）13：15航空航天学院一楼报告厅等你来了解到场有机会获得模拟机体验券声音汇聚力量，思考成就人生！                                                       供稿：谭怡宁                                                       供图：张若昕                                                       编辑：张若昕','2018-04-18 13:15','2018-04-18 15:30','航空航天学院一楼报告厅','https://tongqu.me/upload/acts/20180413/small_ab5c0c7f2e71da546ceb1325fd3a9f9d.png','同去网','2018-04-18 13:15:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2032595639,'专业社会实践活动—德勤','“专业实践类课程模块”是安泰经济与管理学院本科生专业实践类必修课程，修业时间为第2-4学期。该课程旨在结合各个专业（经济、金融、工商管理、会计）及其专业方向/模块的知识内容和实际操作过程，通过现场参观、现场观看企业录像，现场参观和组织企业嘉宾对话结合、请企业人员来学校进行有关专业职能方面讲解等实地学习方式，对所在专业及其专业方向相关的部分理论应用、实际操作过程、业务流程、解决专业问题的工具手段等进行感性认知，促使学生初步认识企业工作环境、直观了解所学专业职能、初步接触一线岗位的基本工作内容，为后期培养专业学习兴趣、加深对专业知识和工具的实际应用打下基础，也为学生明确自身未来的职业规划和职业发展提供一定的参考。为提高本课程学生的获得感，授课教师将仔细思考对象企业的参观内容、对参观过程与学生知识的匹配性进行分析、获得对象企业实践中专业知识的相关材料、企业参观后学生参观心得评价表的设计等等。专业社会实践活动—德勤参观时间：2018年4月21日（周六），下午12:00出发地点：闵行校区菁菁堂广场12:00集合，乘坐大巴集体前往上海市政立路500号企业中心7号楼6楼企业网址：https://www2.deloitte.com/cn/zh.html参观名额：45人','2018-04-21 12:00','2018-04-21 16:00','德勤','https://tongqu.me/upload/acts/20180413/small_5949a176075c005c0eb789a60a8a65e8.png','同去网','2018-04-21 12:00:00.0');
