insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-791590374,'【学术引导月·第二弹】学术之星面对面②','『博友下午茶』学术之星面对面②时间：4月25日 14: 00-15: 30地点：电信群楼3-306会议室嘉宾：李亮，赵家庆主题：《如何开始撰写你的第一篇高水平学术论文》；《如何在学术之路上坚持前行》师傅领进门，修行靠个人...问题是，门朝哪儿开？研路漫漫其修远兮，我越走越累......这些困难你遇到了吗？没关系，看这里！学术之星李亮博士与学术之星提名人物赵家庆博士将为大家带来科研路上的启航与续航经验，帮助大家迈过门槛、越走越顺！“博友”下午茶系列之学术之星面对面专场重磅开启，欢迎“博学之友”前来共话科研！嘉宾介绍：李亮，2013年本科毕业于哈尔滨工业大学自动化专业，2013年至今在上海交通大学自动化系攻读博士学位，导师为杨明教授。研究方向为无人驾驶汽车的高精度定位，鲁棒点集配准算法等。目前以第一作者已发表IEEE Trans.系列长文4篇，高水平国际会议论文5篇。赵家庆，2013级直博生，导师郭小军教授。本科毕业于上海交通大学电子科学与技术（联读班）专业，2012年加入印刷电子与柔性集成实验室。研究内容包括构建超低电压、高稳定性的有机晶体管器件以及相应的表征与建模，探索大面积印刷晶体管加工工艺和搭建电路设计平台，搭建便携式的通用传感系统。发表一作SCI期刊3篇（IEEE EDL, IEEE TED），A类国际会议1篇（IEEE IEDM），授权或公开国家发明专利 11项。','2018-04-25 14:00','2018-04-25 15:30','电信群楼3-306会议室','https://tongqu.me/upload/acts/20180413/small_237d06cc7d4881bb76955801834a515d.png','同去网','2018-04-25 14:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1128263114,'【学术引导月·第二弹】学术之星面对面①','『博友下午茶』学术之星面对面①时间：4月18日 14: 00-15: 30地点：电信群楼3-306会议室嘉宾：尹婷内容：《如何提高学术研究的效率》论文迟迟发不出来的原因是？A. 拖延症又双叒叕犯了B. 与导师沟通不得其法C. 最近比较忙，比较忙，比较忙...挤不出时间专心科研D. 其他...想知道上述问题如何解决吗？那么就来听一听学术之星提名人物尹婷博士的经验分享吧！与博学之友共度下午茶时光，与学术之星大牛0距离面对面，你还在犹豫什么？JOIN US！“博友”下午茶系列之学术之星面对面专场重磅开启，欢迎“博学之友”前来共话科研！嘉宾介绍：尹婷，2008年本科毕业于太原师范学院应用化学专业，2011年硕士毕业于重庆大学物理化学专业，2014年至今在上海交通大学仪器系攻读博士学位，导师为崔大祥教授。研究方向为多功能磁性纳米探针的制备及其胃癌精准诊疗中的研究;免疫磁珠的制备及细胞分选。目前以第一作者发表nature子刊3篇，其中两篇相继发表在NPG Asia Materials（IF=9.157）。共同一作的SCI论文3篇。一共申请国家发明专利7项，其中3项已授权。','2018-04-18 14:00','2018-04-18 15:30','电信群楼3-306','https://tongqu.me/upload/acts/20180413/small_082a6b035ab3336ab13531c3a4d42901.png','同去网','2018-04-18 14:00:00.0');
