insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-647805145,'2018首届中国碳博会志愿者第二波招募','在国家发展和改革委应对气候变化司住房和城乡建设部建筑节能与科技司以及环境保护部环境发展中心的支持下，由十届全国人大财经委副主任委员郭树言担任组委会主任，中国电子节能技术协会、低碳经济促进会、环境保护部环境认证中心和中国质量认证中心四个部门共同合作，中国工程院杜祥琬院士、中国科学院费维扬院士等专家参与指导，中国电子信息产业发展研究院和中国科学院上海高等研究院联合筹备的2018中国国际低碳科技博览会，以“低碳科技，点亮未来”为主题，旨在交流展示我国适用普及低碳技术，架设国际合作通道，带来更多合作空间。为做好博览会服务工作，特向社会招募志愿者若干名，涉及接待、引领、讲解、秩序维护等岗位。招募对象和标准1.男女不限，年龄在18至30周岁之间，身体健康，容貌端庄，有亲和力，形象气质佳；2.普通话标准（拥有普通话国家等级证书者优先考虑），具有较强的语言表达和沟通能力；英文讲解员要求英语口语标准、流利且具备日常英语交流能力；3.遵守国家法规，诚实守信，具有团队意识，具有优秀的学习能力，具有较强服务意识和应变能力；4.熟悉商务礼仪，有小语种专长、低碳相关专业背景以及相关工作经历者优先考虑。志愿服务内容1.服务时间：2018年4月24日至2018年4月25日；2.服务地点：上海世博展览馆4号馆；3.服务内容：如果您成为我们的志愿者，您将为2018年中国国际低碳科技博览会为公众作讲解服务，主要介绍各展区陈列的成果，国际以及我国近些年来在低碳领域做出的努力、成就及低碳新技术的应用。您将主要接待国际政要、国际低碳领域专家学者、国际气候谈判代表团队，以及国务院、各大部委、企事业单位领导和参观团队等。录用及待遇1.录用后对志愿讲解员进行统一培训。每人每天100元补贴；2.志愿者服务当天大巴车接送，主办方提供午餐，并提供志愿者服装；3.志愿服务结束后将由低碳经济专业委员会印制并颁发“低碳志愿者证书”；特别优秀者将颁发低碳勋章；4.提供与知名专家、企业大咖、知名社会认识等合影机会。','2018-04-24 08:30','2018-04-25 18:00','上海世博馆','https://tongqu.me/upload/acts/20180410/small_dfcc7ded600afb6fb9c92b64bcc7d05b.JPG','同去网','2018-04-24 08:30:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-636738614,'专业社会实践活动—普华永道创新中心','“专业实践类课程模块”是安泰经济与管理学院本科生专业实践类必修课程，修业时间为第2-4学期。该课程旨在结合各个专业（经济、金融、工商管理、会计）及其专业方向/模块的知识内容和实际操作过程，通过现场参观、现场观看企业录像，现场参观和组织企业嘉宾对话结合、请企业人员来学校进行有关专业职能方面讲解等实地学习方式，对所在专业及其专业方向相关的部分理论应用、实际操作过程、业务流程、解决专业问题的工具手段等进行感性认知，促使学生初步认识企业工作环境、直观了解所学专业职能、初步接触一线岗位的基本工作内容，为后期培养专业学习兴趣、加深对专业知识和工具的实际应用打下基础，也为学生明确自身未来的职业规划和职业发展提供一定的参考。为提高本课程学生的获得感，授课教师将仔细思考对象企业的参观内容、对参观过程与学生知识的匹配性进行分析、获得对象企业实践中专业知识的相关材料、企业参观后学生参观心得评价表的设计等等。专业社会实践活动—普华永道创新中心参观时间：2018年5月7日（周一），下午12:40-16:00出发地点：闵行校区菁菁堂广场12:40集合，乘坐大巴前往上海市湖滨路150号湖滨道购物中心3层（普华永道创新中心）企业网址：https://www.pwccn.com/参观名额：35人','2018-05-07 12:40','2018-05-07 16:00','普华永道创新中心','https://tongqu.me/upload/acts/20180410/small_a7b485ff0683ff7b1d6316712fba3672.png','同去网','2018-05-07 12:40:00.0');
