insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1567557925,'新加坡南洋理工大学金融硕士学位课程说明会','南洋理工大学商学院金融硕士学位课程（中英双语）---引领你进入国际金融市场之路---金融硕士学位课程旨在为学生提供扎实的金融财务知识和广阔的国际视野，为金融界培养优秀的双语专业人才。本学位要求学生在规定时间（最多24个月）内全职研修获得36个学分。符合毕业要求的学生将获得南洋理工大学颁发的金融硕士学位证书。课程特点§  独特的双语教学设计§  开阔的国际视野§  融入最新的实用金融解决方案§  强大的师资阵容申请资格§  申请人需拥有学士或以上学位，成绩优秀§  具备良好的中英文沟通能力§  在金融业、工商业以及管理等相关行业具有工作经验的申请人会得到优先考虑§  也欢迎具有理工科专业背景，并考虑往金融相关行业发展的申请人报名§  具备申请资格并通过初审的申请人，需要参加大学组织的面试申请程序§  课程在每年10月份开学。申请日期通常从前一年9月份开始，到6月底结束。§  2018学年的入学申请截止到今年6月30日（周六）。为介绍课程最新进展，金融硕士学位课程特于上海举行说明会。欢迎有兴趣的人士前来参加，与我们的教授面对面，深入了解课程讯息。长按键识别活动二维码报名完成线上注册。座位有限，请尽早报名，以免向隅。也欢迎具有理工科专业背景的同学参加。长按键识别活动二维码报名电话：(65) 6790 5879 / 5696电邮：mscfinance@ntu.edu.sg网址：www.mscf.ntu.edu.sg','2018-04-20 16:00','2018-04-20 17:30','闵行校区东中院4-103','https://tongqu.me/upload/acts/20180409/small_b0a102376f81c618ae1a32ba1369a8ce.jpg','同去网','2018-04-20 16:00:00.0');
