insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-633272919,'日语演讲比赛','上海交通大学日语演讲比赛是由日语系学生自发创办、面向全校、并盛邀中日友人共聚的一项友好学术交流活动。本次大赛的主题是「つながり」——羁绊，同学们将各自结合自身事例，探寻自己人生中的羁绊，深刻地剖析自己的成长历程，发表自己独特而深刻的观点。演讲比赛从征集演讲题目开始，将历经稿件征集、初赛、复赛，直到决赛。参赛选手们在比赛中不断地完善自我，取得进步，得以在最后的舞台上展现自己日臻成熟的思考。除了演讲比赛的部分，本次大赛也将加入歌舞和舞台剧这两项艺术活动，分别由日语系大一和大二的同学表演，到场同学可进行观看。本次比赛不但能丰富同学们的课余生活，增进彼此情谊，也能够为本次大赛注入活力与欢乐，生动而有张力的表演及温暖人心的情节将会让本次大赛光彩夺目。日语演讲比赛作为中日文化交流活动，不仅展现出同学们良好的日语能力水平及对生活、人生的感悟，更是成为了嘉宾观众以及参赛选手一年一度的难忘回忆。在这里我们诚挚地向全校范围内的每一位同学发出邀请，期待在这个花季，和大家的相遇～欢迎大家踊跃报名，还有精美小礼品等着大家哦～','2018-04-25 00:00','2018-05-31 00:00','外国语学院杨咏曼楼','https://tongqu.me/upload/acts/20180407/small_621e6123fdc6677fcca1963ae7d1321b.jpeg','同去网','2018-04-25 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1122323047,'“Voice-我思！”第25期 | 大国大城，生生不息','主题：大国大城，生生不息嘉宾：陆铭上海交通大学特聘教授、中国发展研究中心主任美国哈佛大学与国家经济研究局富布赖特学者前世界银行与亚洲开发银行咨询专家时间：2018.4.11   18：45-21：00地点：中英国际低碳学院 包图东楼 200室嘉宾介绍：陆铭，上海交通大学特聘教授、博士生导师，中国发展研究中心主任。曾作为富布莱特学者工作于美国哈佛大学和国家经济研究局（NBER），作为客座教授受聘于复旦大学、北京大学等多所高校，担任知名经济学刊物《经济学（季刊）》副主编。他站在时代的高度从经济学角度看待社会问题用长远的眼光探究大国未来比较全球经验解析本土现状立足当下困境聚焦发展道路他将从城市经济学和空间经济学的角度以通俗的方式 生动的例子 详实的数据为我们带来一场关于大国未来的思维盛宴三尺讲台之上，谈笑风生之间，经济学晦涩抽象的问题在陆铭教授深入浅出的讲解后变得清晰明了。陆教授将经济学道理讲的通俗易懂，但他的观点和建议却深刻而意义非凡，扎实的学术功底、博大的学术情怀和幽默风趣的语言风格使每一位听众都被经济学的魅力深深打动。面对学生提出的问题，陆铭教授亲切耐心，回答时同样妙语连珠，解答之际还会指出思维上的一些误区，令人受益颇深。陆铭教授主要研究领域为劳动经济学、城乡和区域经济发展、社会经济学，但他并不局限于理论堆砌的堡垒之中，实证在他看来同样重要。理论帮助我们更好的了解问题本身，但是离开了实证，理论就容易成为断了线的风筝。“对于理论经济学家，你可以片面而深刻，但对于实证经济学家来讲，必须全面，而且悉心洞察现实。”也正基于此，他的足迹遍布中国中西部广大地区，与当地居民交流共处，用自己的亲身体验和所见所闻掌握着中国经济发展中最真实、最鲜活的第一手资料；他用文字记录下当代中国经济发展的故事，并将这些故事分享给关心国家经济发展的每一个人。 “80后”朋友的就业选择、出租车司机的疑惑、台湾朋友的上海生意、地方官员对于空置工业园区的无奈……正是在这些故事中，孕育着中国未来的发展方向，以及解决大国发展难题的答案。','2018-04-11 18:45','2018-04-11 21:00','中英国际低碳学院（包图东楼）200室','https://tongqu.me/upload/acts/20180407/small_87741e373d0c804ee2df6ac023573cfa.jpg','同去网','2018-04-11 18:45:00.0');
