insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2103063004,'百万年薪精英培养计划！香港IEP金融研习项目招募','1， 项目地简介世界第三大金融中心香港是一座高度繁荣的国际大都市，全境由香港岛、九龙半岛、新界等3大区域组成，人口密度居全世界第三。作为中西文化汇流的聚点，高度的文化差异造就了香港成为国际性的大都会。这里可谓是包罗万象，这是个浓缩的精华的世界，是繁华热闹的不夜城，它也成为了人人欲看的万象之都。香港处于交通枢纽，零关税的商贸要地；随着时间和经济的发展，香港已逐渐成为及纽约、伦敦之后的世界第三大金融中心，与纽约、伦敦并称为“纽伦港”，是全球最自由经济体和最具竞争力城市之一，在世界享有极高声誉。香港金融研习由全球金融知名企业举办，全球华人地区规模最大的金融研习项目。旨在深入理解全球以及香港商业发展与金融市场，培养具有出色战略思考能力与市场洞察力的商界精英。在世界500强企业高管的负责下，学员将接受浸入式的职业体验，了解500强企业的文化、工作环境、日常工作任务等，同时在经验丰富的财富管理师指导下，通过财富管理课程培训、基金投资模拟、企业参访、商业策划大赛、商务礼仪学习、国际志愿者等环节完成从“菜鸟”到“精英”的神奇蜕变。与此同时，在完成实训之后将获得重磅世界500强企业研习证明、投行课程证书、国际志愿者活动证书、世界500强企业高管亲笔签名推荐信，为未来求学及求职道路助力。2， 项目内容深入世界500强名企，近距离接受企业实训学习商业知识，提升个人多维素养获得世界500强名企研习证书，提高学术背景获得世界500强高管亲笔签名推荐信收获国际志愿者证书，助力留学及求职感受香港文化，结识未来精英，拓展人脉参与该项目的未来商界精英们不仅可以学习到国际接轨的商业金融理论知识与素养课程，更有机会进入法国安盛、美国友邦、英国保诚、加拿大宏利等全球金融名企实地体验，融入最前沿的商业氛围，零距离鉴习到商界尖端高管们的非凡运营理念，聆听高管嘉宾授课，并通过多种模块提升多维商业素养。','2018-08-02 16:56','2018-08-20 16:56','香港','https://tongqu.me/upload/acts/20180403/small_649d8af1c84b9cebf0d906fdadc72fb2.jpg','同去网','2018-08-02 16:56:00.0');
