insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-564897842,'专业社会实践活动--上海临港漕河泾人才有限公司','“专业实践类课程模块”是安泰经济与管理学院本科生专业实践类必修课程，修业时间为第2-4学期。该课程旨在结合各个专业（经济、金融、工商管理、会计）及其专业方向/模块的知识内容和实际操作过程，通过现场参观、现场观看企业录像，现场参观和组织企业嘉宾对话结合、请企业人员来学校进行有关专业职能方面讲解等实地学习方式，对所在专业及其专业方向相关的部分理论应用、实际操作过程、业务流程、解决专业问题的工具手段等进行感性认知，促使学生初步认识企业工作环境、直观了解所学专业职能、初步接触一线岗位的基本工作内容，为后期培养专业学习兴趣、加深对专业知识和工具的实际应用打下基础，也为学生明确自身未来的职业规划和职业发展提供一定的参考。为提高本课程学生的获得感，授课教师将仔细思考对象企业的参观内容、对参观过程与学生知识的匹配性进行分析、获得对象企业实践中专业知识的相关材料、企业参观后学生参观心得评价表的设计等等。专业社会实践活动--上海临港漕河泾人才有限公司参观时间：2018年4月4日（周三），下午13:00-16:30出发地点：闵行校区菁菁堂广场集合，乘坐大巴集体前往企业网址：http://www.hrstage.com/index.action参观名额：45人（请报名活动的同学，务必带一份简历）参观内容：漕河泾展厅、漕河泾众创空间参观、宣传片观看以及漕河泾人才沟通互动','2018-04-04 13:00','2018-04-04 16:00','上海临港漕河泾人才有限公司','https://tongqu.me/upload/acts/20180402/small_c4f190cdbd09e202909c5fce47575f34.jpg','同去网','2018-04-04 13:00:00.0');
