insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1381001384,'君行国际义工招募（清迈 大象）','1， 泰国清迈简介泰北玫瑰，清新小城清迈是清迈府的首府，泰国的第二大城市，发达程度仅次于首都曼谷。这座曾沉睡在泰国北部的古城，历来以历史文化之悠久闻名。清迈是国际旅游城市，现颇受中国游人青睐。这里是邓丽君生前最爱的度假地，甚至连生命的最后一刻都留在了这里。清爽的空气、幽静的城市、亲近自然的街景、沉淀几百年甚至更久的文化底蕴，素有“泰北玫瑰”之称。东南亚地区曾是大象的天堂，茂密的热带森林非常适合她们生存，大象也被视为泰国的象征。但目前在泰国全境却仅存5000头左右的大象。象牙交易、大象表演、旅游业中过度使用大象等问题屡禁不止。亚洲象在世界范围内被列为保护动物，我们除了抵制象牙制品和大象表演外，最实际的方式就是去走近亚洲象种群，传播大象保护知识，让更多人了解大象的生存和保育状况，感受到这些巨大生灵的友善与灵性。2， 项目内容真正意义的动物保护目前很多泰国的大象营地都标榜他们在进行大象保育项目，但当我们抵达营地时却看到大象被锁在狭小的地方不能自由活动，园区进行大象表演，全天不间断地供多人骑行，大象明显存在进食不足、自由受限、超负荷劳动甚至被虐打驯化等问题。我们走遍了清迈的大象营，终于遇到了一个真正意义的大象保育营地。园区注重保护大象天性，所有大象可以自由活动，并为大象提供周到看护和充足的食物。象夫和志愿者们每天在喂养完大象后需要带大象在园区散步，带她们去最爱的泥地洗澡（大象其实皮肤非常敏感，感需要泥水洗澡防晒和保护），在水池和他们一起游泳。任何一个来到这里的人都能感受到大象轻松自在的生活（象宝宝性格也是非常活跃开朗）。我们认为这也是对志愿者最好的动物保育教育。除了大象保育活动外，我们还特别设计了泰国厨艺体验，制作与放飞孔明灯和漂流等活动，让义工们充分感受清迈的民俗与自然风情，同时项目还提供优良的住宿环境，空调和热水等设施一应俱全，给义工们带来良好的项目体验。3， 项目日程充实的项目安排，独家深度体验Day 1 星期六当地项目负责人在清迈国际机场接机，送往入住城区民宿酒店，进行项目准备，可在酒店泳池休憩，可自由活动，周六晚上建议体验清迈当地特色周六夜市。','2018-07-01 21:09','2018-09-10 21:09','清迈','https://tongqu.me/upload/acts/20180401/small_747ca621759a8bf3e7b6404ff45a9f8d.jpg','同去网','2018-07-01 21:09:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(252301119,'122周年校庆日志愿者招募','一百多年风雨兼程，励精图治，交大即将迎来她的第一百二十二个华诞。为与来自世界各地的心系交大的人们共同欢庆这一美好时光，我校将4月7日定为了“校友返校日和校园开放日”。届时，将会有来自世界各地的优秀校友和社会爱心人士齐聚在东海之滨，共赴交大闵行校区，重温昔日的青葱岁月，并一同为我们至亲至爱的母校送上诚挚的祝福。我校在当天也将开展一系列活动来欢迎他们的到来。为保证活动的顺利进行，服务好每一位远道而来的学长学姐们，特向材料科学与工程学院招募65名志愿者，具体内容如下：1.活动地点：交大闵行校区2.活动时间：4月7日全天/上午/下午  【全天优先录取】3.工作内容：路况引导和陪同/会务协助/新闻稿和拍摄注：本次活动提供午餐及志愿者证书。','2018-04-07 07:00','2018-04-07 17:00','交大闵行校区','https://tongqu.me/upload/acts/20180401/small_00ba1f026551384bed90407232cdcaad.jpg','同去网','2018-04-07 07:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(641417095,'君行国际义工招募（斯里兰卡 海龟）','1， 项目背景海龟繁多，保育状况严峻。斯里兰卡为印度洋岛国，有着绵长的海岸线，这里栖息的海龟种类繁多，堪称海龟的天堂。海龟们会在夜晚爬上海滩，挑选一片自己心仪的柔软沙滩产下海龟蛋然后离去，大概45天后小海龟会出世，并凭借自然本能朝着大海的方向前进，回归大自然。在斯里兰卡的海滩漫步，不难见到刚孵化出来的小海龟。斯里兰卡海龟保育状况严峻，当地居民会捡食海龟蛋，海鸟等海岸天敌也会啄食海龟蛋和小海龟，种种因素都影响这些珍贵海洋生命的生存。为了保护海龟，沿着斯里兰卡海岸线，政府建立了许多海龟保育中心，用于传播海龟保育知识，收养受伤的海龟和孵化并放养小海龟等。2， 项目内容海龟保育中心，学校帮扶。海龟保育项目在加勒进行，这里有大型的海龟养育池，现有12只大海龟和18只成年海龟，以及数量众多的小海龟。义工们在这里需要清洁海龟养育池，清扫海龟保育中心，加工海鱼并喂养海龟，照顾残疾的海龟，给海龟清洁等。当然更重要的工作是夜晚去到海滩等待海龟或是寻找海龟产卵的地方，并拾回海龟蛋，放在保育中心的养育沙滩上，并给海龟蛋标记上时间，以便于计算大约45天后小海龟出世的时间，将其放回大海。由于收集海龟蛋的工作在晚上，会牺牲部分休息时间，任务较为辛苦，我们会在白天穿插加勒的学校进行探访帮扶工作或是在社区中心教孩子们英语作为休整，项目内还附赠了加勒当地茶厂和特色寺庙参观，丰富义工们在斯里兰卡的体验。3， 项目地介绍加勒(Galle)位于斯里兰卡东南部海岸，距首都科伦坡约100公里，约3小时车程。它既是著名的海滨度假城市，又是东南亚保存最为完整的古代城堡，已经被列入了世界文化遗产，是所有前往斯里兰卡游客的主要目的地之一。4， 项目日程丰富义工活动，独家福利行程。开营日期前的周末当地项目负责人在科伦坡班达拉奈克国际机场接机，送往项目城市加勒，入住义工统一住宿酒店，周末可以熟悉所在地区环境，进行项目准备，可自由活动。Day 1 星期一上午进行项目简介会，介绍项目内容，斯里兰卡文化，当地环境，学习简单当地语言Sinhala；会议结束后一起前往海龟保育中心，给海龟准备食物，喂养海龟。下午参观加勒古堡，领略千年古堡的殖民建筑特色。晚上在项目负责人陪同下，前往海滩捡拾海龟蛋并送回海龟保育中心。Day 2 星期二上午前往当地幼儿园进行义工工作，教授简单英语，辅助老师教学；下午在社区儿童中心和孩子们交流，教简单英语。','2018-07-02 19:43','2018-09-10 19:43','斯里兰卡','https://tongqu.me/upload/acts/20180401/small_197e7a4077d27464dfa8948c26a4f264.jpg','同去网','2018-07-02 19:43:00.0');
