insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-139113553,'2018年海外研修宣讲暨经验分享会','叮铃铃，一年一度的暑期海外研修计划又要开始了！听，你的内心在纠结：想加入海外科研项目，却为没有经验发愁，为没有方向苦恼？想充实自己的暑假，却不知如何着手申请与准备？想带上梦想去看看远方，却担心前路无知己，没有伙伴很孤独？那么就快来参加荣荣举办的暑期海外科研计划宣讲会吧！通过这次宣讲会你将知道如何申请加入海外教授的科研团队，如何在海外研修的过程中学到更多的知识。而且，如果你足够优秀，我们也将资助你进行海外研修。看到这里，热爱科研的你，勤奋刻苦的你，踌踌满志的你，还没有心动吗？海外研修宣讲会，为你答疑解惑，助力你的留洋之梦。2018年海外研修宣讲暨经验分享会——亲历者经验分享+科研资助政策解读宣讲嘉宾：陈奕宁  韩锦程  孙嘉辰宣讲主题：签证以及研修经验宣讲时间：3月28日（周三）下午14：00-15：00宣讲地点：逸夫楼200号嘉宾简介：【陈奕宁】电院大四，专业排名第三，于康奈尔大学进行暑期研修，海外研修专项奖学金获得者。【韩锦程】就读于生命科学与技术学院生物工程系大四。科研方向转为免疫学，将在下半年进入位于休斯顿的MD Anderson Cancer Center继续phd学习。【孙嘉辰】信息工程专业，致远工科荣誉计划，在2017年夏天到UIUC暑研，参与了“在Wi-Fi协议OFDM信号中探测监听设备”研修项目，得到了研修成果准备投稿至Mobisys2018，毕业要去密西根大学安娜堡分校读CS的PhD。','2018-03-28 14:00','2018-03-28 15:00','逸夫楼200','https://tongqu.me/upload/acts/20180326/small_94c8f0fadbccf54c4504be3ba86bcb9f.jpg','同去网','2018-03-28 14:00:00.0');
