insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1175577407,'上海交通大学校庆电院志愿者招募','莺飞草长，雨润如酥，春的微笑明媚了整个世界。当美丽的校园再一次樱花锦簇，母校华诞也悄然将至。百廿又二的岁月，沉淀了太多厚重的历史，化作文化的沃土，孕育着每一个交大人的精神家园。饮水思源，爱国荣校。这是每一位校庆志愿者孜孜以求的目标。加入校庆志愿者的大家庭，你可以在这里为母校的122周年华诞献上一份独特的礼物。校庆日各项工作的顺利开展，需要恪尽职守的你保驾护航；向交大前辈们展示当代交大人的风采，需要朝气蓬勃的你一展英姿。此外，志愿者们还有近距离接触杰出校友们的宝贵机会，相信与他们的接触，对你未来的发展定会有所裨益。华诞献礼百芳开，青春有志是吾侪。又是一年春风笑，斟酒一杯候君来。2018年校庆电院共招收86名志愿者。后勤组工作内容：礼品装袋（10人)工作时间：4.2或4.3（待定）工作地点：霍英东体育馆后勤组除前期工作外，还要参加4.7当天上午的张钟俊纪念仪式（9：10-9：40）服务组工作内容：跟班组60人（其中20人负责往返徐汇），体育馆接待组10人，学院接待点4人，机动组2人工作时间：4月7日全天工作地点：徐汇校区、闵行校区1.本次活动仅招收电院学生；2.本次活动提供本科生107项满分素拓（2016年11月修订综合评测条例），研究生提供一次党团活动素拓；3.请务必关注电青公众号，后续面试及分组信息将在微信上发布；4.志愿者必须服从工作安排，有较强的组织纪律性，无故退出者将记录在案；5.报名者需要下载附件填写后上传，命名为校庆电院志愿者报名表_名字_学号。根据报名表的评分进行志愿者的录取。6.为成功上传附件至同去网，请各位志愿者使用电脑端报名；已经在手机上报名的志愿者请用电脑端补交附件。未交附件及命名格式不正确者视为无效报名。','2018-04-07 06:00','2018-04-07 15:00','上海交通大学徐汇/闵行校区','https://tongqu.me/upload/acts/20180324/small_9fd7f390518bdd91f46804b549038d2d.jpg','同去网','2018-04-07 06:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1289760731,'HKUST宣讲会','项目特色两年全日制，香港首个公共政策硕士学位课程集大学教学与研究卓著师资超越传统专业界限，打造适应跨学科人才适合社会科学、自然科学、工程科技、商业等不同教育背景学生申请课程着重培养可跨领域及不同地域应用的知识、分析能力及管理能力学生可选择专攻方向：科学技术与创新政策或环境政策和可持续性提供基于客户的政策分析项目及于政府、非营利组织、私营企业中为期十周的全职实习机会为在学术研究及专业实践中拥有突出成就的申请者提供奖学金嘉宾介绍Stuart Gietel-Basten博士香港科技大学公共政策学部副教授香港科技大学领导力与公共政策高级培训项目副主任到场嘉宾将介绍公共政策硕士项目学位课程的课程，设计、就业前景以及申请程序，宣讲会也有问答的环节帮助大家深入了解MPP课程。','2018-03-28 18:00','2018-03-28 21:00','铁生管200号','https://tongqu.me/upload/acts/20180324/small_5d89d894bef8bd8f07e171c62c3631a8.png','同去网','2018-03-28 18:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1513672117,'【素拓】防艾同伴教育活动','本次预约报名时间为2018/3/28（周三）12：30-15：30，提供素拓同伴教育发源于澳大利亚，流行于西方国家。经过近十几年的发展，已经成为一种在社会发展领域内广泛采用的培训方法。它不仅应用在生殖健康和艾滋病预防领域，还可以应用在反对毒品，戒烟，反对酗酒，性别平等，妇女能力建设，反对家庭暴力等等方面。它主要采用小组讨论，游戏，角色扮演等参与性强和互动性强的方式进行培训。参与的人主要是年龄相仿、知识背景、兴趣爱好相近的同伴和朋友。本次防艾同伴教育活动由上海交通大学红十字会开展，旨在传播有关预防检测疾病的知识，并使参与者保持理解与宽容之心，正确建立人生观、价值观。【时间】：2018年3月28日（周三）12：30-15：30【地点】：逸夫楼易班【报名方式】：同去网预约报名（3月24日晚6：00开放）【活动内容】：在这里，你将体验刺激有趣的游戏，学习生殖健康和艾滋病预防知识；在这里，你可以与志同道合的好友交流自己的观点，性别平等、安全性行为都是我们关注的话题；在这里，我们反对歧视，关心身边每一个人。注：本次同伴教育活动分两场进行，两场活动相互独立，内容不同，可同时报名或只报名一场。上海交通大学红十字会2018年3月24日','2018-03-28 12:30','2018-03-28 15:30','逸夫楼易班','https://tongqu.me/upload/acts/20180324/small_090aa9e162b5bf110a4d831536c145bf.jpg','同去网','2018-03-28 12:30:00.0');
